INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(384, 2, '2017-10-24 19:14:56', '2017-10-24 19:14:56', '<span lang=\"BN\">বাংলাদেশের অন্যতম জনপ্রিয় ও আধুনিক কবি নির্মলেন্দু গুণ</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৪৫ সালের ২১ জুন</span>, <span lang=\"BN\">নেত্রকোণায় জন্ম নেন এ খ্যাতিমান কবি</span><span lang=\"HI\">।</span> <span lang=\"BN\">নেত্রকোণার বারহাট্টা উপজেলায় কাটে তার দুরন্ত শৈশব</span><span lang=\"HI\">।</span> <span lang=\"BN\">চার বছর বয়সে মাকে</span> <span lang=\"BN\">হারান তিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">মায়ের মৃত্যুর পর বাবা সুখেন্দু প্রকাশ গুণ আবার বিয়ে করেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঘরে আসে নতুন মা চারুবালা</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">৬৬</span>’ <span lang=\"BN\">এর ছয় দফা আন্দোলনের সঙ্গে আত্মিকভাবে জড়িয়ে পড়েছিলেন এ কবি</span>; <span lang=\"BN\">এরপর বাদ পড়েনি স্বাধীনতার </span><span lang=\"BN\">পূর্বে</span><span lang=\"BN\"> এবং স্বাধীনতা পরবর্তী কোনো বিপর্যয়</span>, <span lang=\"BN\">যতবার এ রাষ্ট্র বিপথগামী হয়েছে</span>- <span lang=\"BN\">কলম ধরেছেন তিনি</span>, <span lang=\"BN\">লিখেছেন একের পর এক শ্রেণী সংগ্রাম এবং স্বৈরাচার</span>-<span lang=\"BN\">বিরোধী কবিতা</span><span lang=\"HI\">।</span> <span lang=\"BN\">বাংলাদেশে বঙ্গবন্ধুকে নিয়ে লেখা প্রথম কবিতার কবি তিনি</span>, <span lang=\"BN\">পঁচাত্তর পরবর্তী সময়ও বঙ্গবন্ধুকে নিয়ে কলম তিনিই ধরেছেন</span><span lang=\"HI\">। ১৯৭০ সালে প্রথম কাব্যগ্রন্থ ‘প্রেমাংশুর রক্ত চাই’ প্রকাশিত হবার পর জনপ্রিয়তা অর্জন করে। এ-গ্রন্থের ঐতিহাসিক প্রেক্ষাপটে লেখা হুলিয়া কবিতাটি ব্যাপক জনপ্রিয়তা অর্জন করে। ১৯৮২ সালে বাংলা একাডেমী </span>পুরস্কার, ২০০১ সালে একুশে পদক এবং ২০১৬ সালে স্বাধীনতা পুরস্কার অর্জন করেন তিনি।\r\n\r\n<span lang=\"BN\">তার কবিতার আরকটি প্রধান আলোচ্য বিষয় নারী ও প্রেম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিভিন্ন কবিতায় নারী</span>-<span lang=\"BN\">পুরুষের প্রেম গভীরভাবে ব্যক্ত করেছেন তিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে তার ভাষ্যমতে</span>- <span lang=\"BN\">আমরা যে ভূখণ্ডে বাস করছি</span>; <span lang=\"BN\">সেখানে বাস করা এত বেশি সংগ্রামের যে প্রেমিকাকে খুব বেশি সময় দিতে পারেননি তিনি</span>, <span lang=\"BN\">আর তাই হয়তো প্রেমিকাও তাকে বলার সুযোগ পাননি</span>- ‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’? <span lang=\"BN\">বোহেমিয়ান</span><span lang=\"BN\"> জীবন যাপনে অভ্যস্ত নির্মলেন্দু গুণ নিজের ইচ্ছাকে প্রাধান্য দিয়েই প্রকৃত কবির জীবন যাপন করে গিয়েছেন</span>; <span lang=\"BN\">এর জন্য আশ্রয় নেননি কোনো ছল</span>-<span lang=\"BN\">চাতুরীর</span>, <span lang=\"BN\">আর তাই স্বাধীনতা পুরস্কারটি এ রাষ্ট্রের কাছে তাকে চেয়ে নিতে হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্বাধীনতা পুরস্কার চেয়ে নেওয়ার মতো সাহসীকতা এ রাষ্ট্রে বোধহয় তাকেই মানায়</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">সম্প্রতি রাজধানীর কাঁটাবনের কনকর্ড এম্পোরিয়ামের নিচ তলায় কবির নিজস্ব বইয়ের দোকান </span>‘<span lang=\"BN\">কাঁশবন</span>’<span lang=\"BN\">এ বসে তিনি কথা বলেন প্রিয়</span>.<span lang=\"BN\">কম এর সঙ্গে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবির সাথে কথোপকথনের সেই মুহূর্তগুলো প্রিয়</span>.<span lang=\"BN\">কম এর পাঠকদের জন্য প্রকাশ করা হলো</span>-\r\n\r\n<strong> <span lang=\"BN\">দাদা</span>, <span lang=\"BN\">প্রথমে এই দোকান সম্পর্কে জানতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">সম্প্রতি এটি আপনি দিয়েছেন</span>, <span lang=\"BN\">দেখতে পাচ্ছি এখানে সবই আপনার বই</span>…</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">এ দোকানটি এ মাস থেকেই আমি নিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আসলে </span>‘<span lang=\"BN\">কাঁশবন</span>’ <span lang=\"BN\">নামের একটি ছোট পত্রিকার অফিস ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ওরা আমার উপর একটা বিশেষ সংখ্যা করেছিল প্রায় ৩০০</span>-<span lang=\"BN\">৪০০ পৃষ্ঠার</span>, <span lang=\"BN\">তারপর আমাদের সদ্য প্রয়াত কবি রফিক আজাদের উপরও খুব বড় একটা সংখ্যা করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই দুটি সংখ্যা করার কারণে কাঁশবন খুব গুরুত্বপূর্ণ একটি ভূমিকায় চলে গিয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু এ দোকানটা ওরা তেমন ব্যবহার করছিল না</span>, <span lang=\"BN\">ফলে আমি ওদের বললাম</span>- <span lang=\"BN\">যেহেতু তোমরা দোকানটা তেমন ব্যবহার করছো না</span>, <span lang=\"BN\">আমাকে দিয়ে দাও</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার তো একশর উপরে বই আছে</span>, <span lang=\"BN\">অনেক পাঠক সন্ধান করে বইগুলো খুঁজে পায় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিভিন্ন সময় অনেকে আমার কাছে ফোন করে জানতে চায় কোথায় পাওয়া যাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তখন আজিজ মার্কেট</span>, <span lang=\"BN\">নিউ মার্কেট এগুলোর কথা বলি</span>, <span lang=\"BN\">তবুও অনেক জায়গায় বই পাওয়া যায় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমার মাথায় চিন্তা এলো আমি যদি একটি বইয়ের দোকান দিই</span>, <span lang=\"BN\">যেখানে আমার সব বই এক সাথে পাওয়া যাবে একটা জায়গাতেই</span>, <span lang=\"BN\">তাহলে আমার একটা ব্যবসায়িক লাভও হলো</span>, <span lang=\"BN\">আবার সেখানে আমি দুইটা ছেলেকে প্রোভাইডও করতে পারব</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার অবর্তমানে আমার বইগুলোর তদারকি তারা করতে পারবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা স্ট্যাটাসের</span> (<span lang=\"BN\">সামাজিক যোগাযোগ মাধ্যম</span>) <span lang=\"BN\">মাধ্যমে এই দোকানের কথা প্রচার করে দিতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আজকেই একটা স্ট্যাটাস দিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সপ্তাহে তিন দিন আমি এখানে বসব</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে সন্ধান করার জন্য এখন আর কষ্ট করে কামরাঙ্গীর চর যেতে হবে না</span>, <span lang=\"BN\">কাঁটাবনের কনকর্ড এম্পোরিয়ামের কাঁশবনেই রাত আটটার পর আমাকে পাওয়া যাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">পাঠকরা ইচ্ছা করলে আমার অটোগ্রাফ যুক্ত বইও কিনতে পারবেন</span>, <span lang=\"BN\">আমার সাথে সেলফিও তুলতে পারবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমি সহজলভ্য হব এবং আমার বইও আগের চেয়ে সহজলভ্য হবে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার এই প্রতিষ্ঠান থেকে কি বই প্রকাশ করার কোনো ইচ্ছা আছে</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">না</span>, <span lang=\"BN\">বই প্রকাশের চিন্তা নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার তো একশজন প্রকাশক আছে</span>, <span lang=\"BN\">সুতরাং আমি আর নিজে প্রকাশনা শিল্পের মধ্যে নাক গলাতে চাই না</span><span lang=\"HI\">।</span> <span lang=\"BN\">একবার এক প্রকাশক আমার একটা বই প্রকাশ করা নিয়ে বিলম্ব করছিল</span>, <span lang=\"BN\">তখন আমি ঐ বইটা নিজেই প্রকাশ করে ফেলেছিলাম</span>, <span lang=\"BN\">সেটার নাম দিয়েছিলাম </span>‘<span lang=\"BN\">অগত্যা প্রকাশনী</span>’! <span lang=\"BN\">পরে অবশ্য সেটা বিক্রেতাদের দিয়ে দিয়েছি</span>, <span lang=\"BN\">তুমি বলাতে বই প্রকাশনার সেই ঘটনা মনে পড়ল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">তাহলে দাদা</span>; <span lang=\"BN\">এখন একটু আগে থেকে জানতে চাইব</span>, <span lang=\"BN\">মানে আপনার ছেলেবেলা</span>, <span lang=\"BN\">নেত্রকোণার জীবন আরকি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">নেত্রকোণার আগে তো আর যেতেও পারবা না</span>! <span lang=\"BN\">তুমি ইচ্ছা করলেও এর আগে যেতে পারতেছ না</span>!\r\n\r\n<strong> <span lang=\"BN\">সেটাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার ছেলেবেলা কেমন কেটেছে</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এই শোনো</span>, <span lang=\"BN\">তার আগে তোমারে একটা গল্প বলি</span>…\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আগে যখন বাংলা একাডেমীতে বইমেলা হতো</span>, <span lang=\"BN\">আমরা কয়েকজন স্টলের মধ্যে বসতাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছু ছেলেপেলে আসতো</span>; <span lang=\"BN\">হয়তো স্কুলে পড়ে</span>, <span lang=\"BN\">নাম শুনেছে বা টেলিভিশনে আমার চেহারাটা দেখেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাদের হয়তো আমার আর রবীন্দ্রনাথের বয়সের পার্থক্য সম্পর্কে স্পষ্ট ধারণা নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">নজরুল বড় না</span>, <span lang=\"BN\">মাইকেল মধুসূদন বড় সেটাও হয়তো তারা স্পষ্ট জানে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে একবার এদের মধ্যে কয়েকজন জিজ্ঞেস করছে</span>- <span lang=\"BN\">আচ্ছা</span>, <span lang=\"BN\">আপনি কি রবীন্দ্রনাথকে দেখেছেন</span>? <span lang=\"BN\">আমি বললাম</span>, <span lang=\"BN\">না</span>! <span lang=\"BN\">রবীন্দ্রনাথকে আমি দেখি নাই</span>, <span lang=\"BN\">আমার সে সৌভাগ্য হয় নাই কারণ রবীন্দ্রনাথ আমার জন্মের আগে মারা গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি আবার ওদের জিজ্ঞেস করলাম রবীন্দ্রনাথ কত সালে মারা গেছেন তোমরা সেই সম্পর্কে কিছু জানো</span>? <span lang=\"BN\">বললো যে</span>, <span lang=\"BN\">না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আমাদের ছেলেমেয়েদের জ্ঞানের স্বল্পতা</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছুক্ষণ পর ঘুরে এসে আবার জিজ্ঞেস করলো</span>- <span lang=\"BN\">আপনি কি মাইকেল মধুসূদনকে দেখেছেন</span>? <span lang=\"BN\">আমি বললাম মাইকেল মধুসূদন তো রবীন্দ্রনাথেরও আগের কবি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তোমরা শুধু নামগুলোই জানো</span>, <span lang=\"BN\">তাদের জন্ম তারিখ সম্পর্কে তোমাদের কোনো ধারণা নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে আবার বলে</span>, <span lang=\"BN\">আপনি কি নজরুলকে দেখেছেন</span>? <span lang=\"BN\">মানে বুঝছো তো</span>? <span lang=\"BN\">তারা জানতে চাইছে</span>, <span lang=\"BN\">আমি তো কবি</span>, <span lang=\"BN\">কিন্তু কোনো বড় কবির সঙ্গে আমার সাক্ষাৎ হয়েছে কি না</span>! <span lang=\"BN\">আমি তখন বললাম</span>- <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">নজরুলকে দেখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">নজরুল পিজি হাসপাতালে যখন চিকিৎসাধীন ছিলেন</span>, <span lang=\"BN\">তখন আমি উনার সঙ্গে দেখা করেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি ওদের পরামর্শ দিলাম</span>, <span lang=\"BN\">তোমরা একটা কাজ করবে</span>- <span lang=\"BN\">যেই লেখকের সম্পর্কে তোমাদের আগ্রহ হবে</span>, <span lang=\"BN\">তার জন্ম সাল</span>-<span lang=\"BN\">তারিখটা জেনে রাখবে</span>, <span lang=\"BN\">তাতে তোমরা ধারণা পাবে কে বয়সে কার চেয়ে বড়</span><span lang=\"HI\">।</span> <span lang=\"BN\">ওরা আমাকে জিজ্ঞেস করেছে রবীন্দ্রনাথের সঙ্গে আমার দেখা হয়েছে কি</span>-<span lang=\"BN\">না</span>; <span lang=\"BN\">তিনি মারা গেছেন ১৯৪১ সালে</span>, <span lang=\"BN\">আমার জন্ম ১৯৪৫ সালে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার মৃত্যু এবং আমার জন্মের মাঝখানে যে চার বছর</span>- <span lang=\"BN\">সে সময় কী হয়েছে সেটা আমার পক্ষে বলা মুশকিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঐ সময়টাতে রবীন্দ্রনাথের সঙ্গে আমার দেখা হয়েছিল কি</span>-<span lang=\"BN\">না সেটা আমার অজানা</span>!\r\n\r\n<strong> <span lang=\"BN\">যেটা বলছিলাম দাদা</span>, <span lang=\"BN\">আপনার ছেলেবেলার কথা</span>...<span lang=\"BN\">আমি জানি যে খুব ছোটবেলায় মাকে হারিয়েছেন আপনি</span><span lang=\"HI\">।</span></strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমার বয়স তখন চার হয়নি বোধহয়</span>, <span lang=\"BN\">চারের কাছাকাছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আম্মা অসুস্থ ছিলেন</span>, <span lang=\"BN\">বাবা তার মাথার পাশে বসে আম্মার মাথায় জল ঢালছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আম্মা করুণ চোখে আমার দিকে তাকিয়ে ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তখন একটা প্লাস্টিকের ফুটবল নিয়ে বাইরে খেলতে যাচ্ছিলাম</span>, <span lang=\"BN\">ওই সময় বাবা ডেকে মায়ের পাশে বসতে বললেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি এই প্রস্তাবে রাজি হইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি মাকে এক ঝলক দেখলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">মাও চেয়েছিলেন হয়তো আমাকে ছুঁয়ে দেখবেন</span>, <span lang=\"BN\">তার মৃত্যু ঘনিয়ে আসছে এই বোধটা হয়তো তিনি টের পাচ্ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন তো আর এগুলো বুঝতে পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন ভাবতে কষ্ট হয়</span>, <span lang=\"BN\">আমাকে শেষবার স্পর্শ করার যে ইচ্ছা তার মধ্যে জাগ্রত হয়েছিল সেটা আমি হতে দিইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">হাতের বলটি নিয়ে আমি দৌড়ে ঘর থেকে বের হয়ে গেলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মনে হচ্ছিল</span>- <span lang=\"BN\">ফুটবল খেলার সময় চলে যাচ্ছে</span>, <span lang=\"BN\">কিন্তু মা তো আর চলে যাবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই চলে যাওয়াটার সঙ্গে তো আমি পরিচিত ছিলাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">মৃত্যুর সঙ্গে ঐ প্রথম আমার পরিচয় হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ধারণার মধ্যেই ছিল না মা মরে যেতে পারেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই বোধই ছিল না আমার ভেতরে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারপরে আমার আর কিছু মনে নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি অনুমান করি যে</span>- <span lang=\"BN\">ঐ দিনই কিছুক্ষণ পরে মা মারা গিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার জ্যেঠি মা</span>, <span lang=\"BN\">কাকী মা</span>, <span lang=\"BN\">আমার বড় বোনেরা</span>, <span lang=\"BN\">আত্মীয়স্বজনরা আমাকে খেলার মধ্যে থেকে নিয়ে এসেছিলেন</span>, <span lang=\"BN\">আমি কিছুতেই আসতে চাচ্ছিলাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা জোর করে নিয়ে আসছিলেন</span>, <span lang=\"BN\">আমি কি কান্না করব</span>! <span lang=\"BN\">আমার নির্বিকার মূর্তি দেখে তারাই কান্নাকাটি করছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে তারা বুঝাতে চেষ্টা করছিলেন যে আমার মা মারা গেছেন</span>, <span lang=\"BN\">কিন্তু এই মারা যাওয়া জিনিসটাই তো আমার বোধের মধ্যে তখন ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু এখন যখন আমি আমার অতীতকে রোমন্থন করি তখন মায়ের ঐ শুয়ে থাকার দৃশ্যটিই আমার কাছে তাকে নিয়ে একমাত্র স্মৃতি বলে মনে হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এর আগে তাকে কখনো দেখেছি বলে আমার মনে পড়ে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাকে কখনো ঘরে বসে থাকতে বা ঘরে হাঁটাচলা বা পায়চারী করতে</span>, <span lang=\"BN\">কোথাও দাঁড়ানো অবস্থায় দেখেছি বলে আমার স্মৃতির মধ্যে নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">মনে থাকার মধ্যে মা শুয়ে আছেন</span>, <span lang=\"BN\">বাবা তার মাথায় জল ঢেলে দিচ্ছেন</span>- <span lang=\"BN\">এই দৃশ্যটুকুই শুধু মনে আছে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার আত্মজীবনীমূলক বই </span>‘<span lang=\"BN\">আমার ছেলেবেলা</span>’<span lang=\"BN\">তেও বোধহয় মায়ের মৃত্যুর সেই দৃশ্য দিয়ে শুরু করেছিলেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">আমার ছেলেবেলা</span>’ <span lang=\"BN\">বইটা শুরুই হয়েছে মায়ের মৃত্যুর দৃশ্য দিয়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ম্যাক্সিম গোর্কির ছেলেবেলা</span>, <span lang=\"BN\">রবীন্দ্রনাথের ছেলেবেলা</span>, <span lang=\"BN\">বুদ্ধদেব বসুর ছেলেবেলা</span>- <span lang=\"BN\">এই সবগুলো বই</span>-<span lang=\"BN\">ই আমি পড়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এর মধ্যে আমার সব থেকে ভালো লেগেছিল ম্যাক্সিম গোর্কির ছেলেবেলা</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার জীবনের মধ্যে এক বিরাট সংগ্রাম ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">জন্মের পরেই তার পিতা মারা যান</span>, <span lang=\"BN\">তাকে তার দিদি মা মানুষ করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">মামার বাড়িতে তিনি বড় হয়েছিলেন</span>, <span lang=\"BN\">কিন্তু তার লেখার ভেতরে দিদি মা</span>’<span lang=\"BN\">র চরিত্রটি খুব সুন্দরভাবে এসেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">লেখার মধ্যে তার জীবনের যে সংগ্রাম পেয়েছিলাম</span>, <span lang=\"BN\">ঐটা আমাকে খুব আকর্ষিত করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার আত্মজীবনী যখন আমি লেখা শুরু করি</span>, <span lang=\"BN\">আমার বোধহয় তখন ৪২ বছর বয়স</span><span lang=\"HI\">।</span> <span lang=\"BN\">৪২ বছর বয়সে আমি ছেলেবেলার বইটি লিখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথ কিন্তু তার ছেলেবেলার বইটি লিখেছিলেন সত্তর বছর বয়সে</span>, <span lang=\"BN\">বুদ্ধদেব বসু সম্ভবত ষাট বছর বয়সে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি চিন্তা করে দেখলাম</span>-<span lang=\"BN\">ছেলেবেলার গল্প লেখার জন্য এত দীর্ঘ দিন অপেক্ষা করার কিছু নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথের আগে বোধহয় কেউ ছেলেবেলা লিখেননি</span>, <span lang=\"BN\">এই ধারণাটাই হয়তো ছিল না তখন</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথ আমাদের অনেক নতুন ধারণার সাথে পরিচয় করিয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার দাদা মহর্ষি দেবেন্দ্রনাথ ঠাকুরও কিন্তু তার আত্মজীবনী রচনা করে গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং আত্মজীবনী রচনার ধারণা রবীন্দ্রনাথ পেয়েছিলেন দাদার কাছ থেকে</span>, <span lang=\"BN\">কিন্তু ছেলেবেলাকে পৃথক করে লেখার ধারণাটা বাংলা সাহিত্যে আগে ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে রবীন্দ্রনাথের আগে কারো ছেলেবেলার বই আমরা পাই না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এগুলো পড়ার পর তাই আমি চিন্তা করে দেখলাম</span>, <span lang=\"BN\">ষাট</span>-<span lang=\"BN\">সত্তর বছর পর্যন্ত অপেক্ষা করার কোনো প্রয়োজন নেই</span>, <span lang=\"BN\">স্মৃতি সতেজ থাকতে থাকতেই ছেলেবেলার ঘটনাগুলো আমার লেখা দরকার</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু আমি মনে করেছি যে লিখব</span>; <span lang=\"BN\">এবং আমার লেখা সম্পর্কে পাঠকদের কৌতূহল সৃষ্টি হবে</span>, <span lang=\"BN\">সুতরাং তা আগেই লিখে ফেলা ভালো</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই বই লেখার সময় শুরুটা কোন জায়গা থেকে করব এটা ভাবতে গিয়ে ম্যাক্সিম গোর্কির ছেলেবেলার লেখাটা মনে পড়ল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার বইটি একটি করুণ দৃশ্য দিয়ে শুরু হয়েছিল</span>- <span lang=\"BN\">যা তার জীবনকে নানাভাবে প্রভাবিত করেছে</span>, <span lang=\"BN\">এ ঘটনা তার জীবনের অনেক কষ্টের কারণও হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি সেই কষ্ট জয় করে কীভাবে বড় লেখকে পরিণত হলেন</span>, <span lang=\"BN\">সেই অভিজ্ঞতাটা যেহেতু তার বই থেকে আমি পেয়েছি</span>, <span lang=\"BN\">ফলে আমি </span>‘<span lang=\"BN\">আমার ছেলেবেলা</span>’ <span lang=\"BN\">বইটি মায়ের মৃত্যুর দৃশ্য দিয়েই শুরু করেছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">মায়ের মৃত্যুর পর আপনার অনুভূতি কী রকম ছিল</span>? <span lang=\"BN\">বুঝতে নিশ্চয় সময় লেগেছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমি তো বিশ্বাসই করতে পারিনি আমার মা মারা গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">মাকে দেখি না দেখি না</span>... <span lang=\"BN\">এত ছোটবেলার কথা মানুষের মনে থাকারও কথা না</span>, <span lang=\"BN\">আমার মনে হয় পাঁচ</span>-<span lang=\"BN\">ছয় বছর বয়সের আগের কথা মানুষের মনে থাকে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">সম্ভবত এটা একটি বিশেষ ঘটনা বলেই আমার চোখে দৃশ্যটা রয়ে গেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমানের একটা কবিতা আছে</span>- ‘<span lang=\"BN\">কখনো আমার মাকে</span>’, <span lang=\"BN\">যদিও শামসুর রাহমানের মা খুব দীর্ঘজীবী হয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমান যখন প্রায় পঞ্চাশ বছরের</span>, <span lang=\"BN\">তখন মায়ের সাথে তার একটা ছবি আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">মা তাকে আদর করছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবুও তিনি তার মাকে নিয়ে একটি কবিতা লিখেছিলেন</span>, <span lang=\"BN\">সে কবিতার শুরুতেই আছে</span>- ‘<span lang=\"BN\">কখনো আমার মাকে গান গাইতে শুনিনি</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">আমার কাছে মনে হয়</span>, <span lang=\"BN\">তার এই কবিতাটি আমার মায়ের বেলায় সত্য</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের কিছু পারিবারিক ছবি আছে</span>; <span lang=\"BN\">সেই ছবিগুলোতে দেখা যায় আমি মায়ের সামনে বসে আছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আবার আমার ছোট বোনকে নিয়ে মা আদর করছেন</span>, <span lang=\"BN\">আমি দাঁড়িয়ে দাঁড়িয়ে তা দেখছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">মা</span>’<span lang=\"BN\">র একার ছবিও অনেক দেখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমাদের বাড়িতে ছবি তোলার একটা রেওয়াজ ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আগের দিনের মানুষের তো একটা সমস্যা ছিল যে তারা জন্মবিরতিকরণ পদ্ধতির সাথে পরিচিত ছিল না</span>, <span lang=\"BN\">ফলে আমরা দেখি রবীন্দ্রনাথের মা ১৩</span>-<span lang=\"BN\">১৪টা সন্তানের জন্ম দিয়েছেন</span>, <span lang=\"BN\">আমার মাও খুব অল্প দিনে অল্প বয়সের ব্যবধানে অনেকগুলো সন্তানের জন্ম দিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার একটা বড় ভাই ছিলেন কালীদাস নামে</span>, <span lang=\"BN\">খুব অল্প বয়সেই তিনি মারা যান</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">কালীদাস</span>’ <span lang=\"BN\">নামটি আমার বাবার রাখা</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি মনে করি কালীদাসের নামে নাম রাখার কারণ</span>- <span lang=\"BN\">আমার বাবা হয়তো চেয়েছিলেন তার কোনো এক সন্তান কবি হোক</span>! <span lang=\"BN\">নাহলে কালীদাসে নামে নাম রাখবেন কেন</span>?\r\n\r\n<strong> <span lang=\"BN\">আপনার বাবা তো আর্ট</span>-<span lang=\"BN\">কালচারের সাথে জড়িত ছিলেন বলে শুনেছি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">তিনি কলকাতা আর্ট স্কুলের ছাত্র ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরিবারের ইচ্ছার বিরুদ্ধেই তিনি সেখানে ভর্তি হয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাকে নিয়মিত টাকা পাঠাতে হতো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার জ্যাঠা</span>, <span lang=\"BN\">কাকা বা আমার ঠাকুরমা এটার পক্ষপাতী ছিলেন না</span>, <span lang=\"BN\">ফলে ঠাকুরদাদা মারা যাওয়ার পর বাবাকে টাকা পাঠানো বন্ধ করে দেওয়া হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">বাবাকেও আর্ট স্কুলে পড়াশোনা শেষ না করেই ফেরত আসতে হয়েছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনি তো ছবিও আঁকেন</span>, <span lang=\"BN\">ছবি আঁকার ইচ্ছাটা কীভাবে হলো</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমাদের একটা স্বচ্ছল পরিবার ছিল</span>, <span lang=\"BN\">যেখানে ছবি আঁকা বা তোলার একটা রেওয়াজ চালু ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ঠাকুরদাদা যখন বেঁচে ছিলেন তখন টেকনোলজি এত উন্নত ছিল না</span>, <span lang=\"BN\">যার ফলে ১৯১৩ সালে তিনি তার একটা পোট্রেট আঁকিয়েছিলেন শারদা রায় নামের একজন শিল্পীকে দিয়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি আমার গ্রামে ঠাকুরদাদার</span> (<span lang=\"BN\">রামসুন্দর পাঠাগার</span>) <span lang=\"BN\">নামে যেই পাঠাগার করেছি</span>, <span lang=\"BN\">সেখানে ঐ তৈলচিত্রটি আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯১৩ সালে করা তার ওই পোট্রেট আমি সারা বাংলায় আর একটাও দেখিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এত সুন্দর পোট্রেট ছিল ঐটা</span><span lang=\"HI\">।</span> <span lang=\"BN\">গ্রামে আমি একটি আর্ট স্কুল করেছি শারদা রায়ের নামে</span> (<span lang=\"BN\">শারদা বসু দেব চিত্রশালা</span>)<span lang=\"HI\">।</span> <span lang=\"BN\">তিনি পরবর্তীতে তার কন্যাকে আমার জ্যাঠা মশাইয়ের সঙ্গে বিবাহ দেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা বাঙালিরা চিত্রকলার চর্চার সময়টাকে রবীন্দ্রনাথের সময় থেকেই ধরতে চাই</span>; <span lang=\"BN\">কিন্তু রবীন্দ্রনাথ ১৯২৬</span>-<span lang=\"BN\">২৭ সাল থেকে ছবি আঁকা শুরু করেছিলেন</span>, <span lang=\"BN\">তার আগে থেকেই কিন্তু আমাদের পটচিত্রে এগুলো পাওয়া যেত</span><span lang=\"HI\">।</span> <span lang=\"BN\">গ্রামের কুমার ছিলেন যারা</span>, <span lang=\"BN\">দেব</span>-<span lang=\"BN\">দেবীর মূর্তি তৈরি করতেন</span>, <span lang=\"BN\">মূর্তি তৈরির আগে তারা কাগজে নকশা তৈরি করে নিতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিক্রমপুরে শম্ভু আচার্য্য ছিলেন তিনি যে পটচিত্র তৈরি করেছেন সেগুলোও আগে থেকেই ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আধুনিক চিত্রকলার চর্চা যেমন তৈলচিত্র</span>- <span lang=\"BN\">১৯১৩ সালে শারদা রায়ের করা আমার ঠাকুরদাদার পোট্রেটটির আগে সারা বাংলাতেই কখনো কোনো শিল্পী করেছেন বলে আমার মনে হয় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি আমার একটা লেখায় উল্লেখ করেছিলাম যে যদি কোনো শিল্পীর আঁকা ১৯১৩ সালের কোনো তৈলচিত্র দেখাতে পারেন তাহলে আমি খুশি হবো</span>, <span lang=\"BN\">আমার এই ধারণাটার পরিবর্তন হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আজ পর্যন্ত কেউ আমাকে দেখাতে পারেননি</span>, <span lang=\"BN\">দাবিও করতেও পারেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঐ তৈলচিত্রটি আমাকে ভীষণভাবে প্রভাবিত করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আর আমার বাবা নিজেও তো ছবি আঁকতেন</span>, <span lang=\"BN\">ফলে তার উত্তরাধিকার সূত্রেও এটা পাওয়া বলতে পারো</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">মায়ের মৃত্যুর পর তো আপনার বাবা দ্বিতীয় বিয়ে করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কেমন ছিল নতুন মায়ের সাথে আপনার অভিজ্ঞতা</span>?</strong>\r\n\r\n<span lang=\"BN\">নির্মলেন্দু গুণ</span>: <span lang=\"BN\">মা যে মারা গেছেন আমি তো বুঝতে পারিনি</span>, <span lang=\"BN\">অন্যরা আমাকে বলত</span>, <span lang=\"BN\">আমাকে নিয়ে দুঃখ প্রকাশ করত</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি কিছুতেই বিশ্বাস করতাম না মা মারা গেছেন</span>, <span lang=\"BN\">আমার ধারণা ছিল মা কোথাও বেড়াতে গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমি কোনোদিন মায়ের জন্য কান্নাকাটি করেছি এ রকম স্মৃতি আমার মনে নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">অন্যরাও পরবর্তীতে কেউ বলেননি যে মায়ের মৃত্যুতে আমি কষ্ট পেয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">জিনিসটা আমি বুঝতেই তো পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার একটা কবিতা আছে </span>‘<span lang=\"BN\">নিজের ঘরে আগুন</span>’, <span lang=\"BN\">সেই কবিতার মধ্যে এর বর্ণনা আছে</span>, <span lang=\"BN\">আমি যে মাকে মুখাগ্নি করেছি</span>, <span lang=\"BN\">তার মৃতদেহে আগুন দিয়েছি</span>, <span lang=\"BN\">আমি যে এত বড় একটা সর্বনাশ করেছিলাম</span>- <span lang=\"BN\">সেটা আমার স্মৃতিতেই নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমার দ্বিতীয় মা যখন আমাদের সংসারে আসলেন</span>, <span lang=\"BN\">তাকে দেখে আমার মনে হলো</span>- <span lang=\"BN\">আগের মায়ের থেকে এ মা একটু মেদবহুল</span>, <span lang=\"BN\">স্বাস্থ্য ভালো</span>, <span lang=\"BN\">বয়স একটু কম মনে হয়েছে</span>, <span lang=\"BN\">রুগ্ন মনে হয় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মনে হয়েছে আমার মা</span>-<span lang=\"BN\">ই বোধহয় চিকিৎসার জন্য বাইরে কোথাও গিয়েছিলেন</span>, <span lang=\"BN\">সেখান থেকে ফিরে এসেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">উনাকে কখনোই আমার মনে হয়নি যে</span>- <span lang=\"BN\">তিনি আমার মা নন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমার একটা কবিতা আছে</span>, <span lang=\"BN\">এই মা মারা যাওয়ার পরে লিখেছিলাম</span>- <span lang=\"BN\">ভুলেই হয়তো কিন্তু আমি তো তোমার নিজের সন্তানরা জন্মের আগেই তোমাকে মা ডেকেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তো তোমার গর্ভজাত সন্তান না</span>, <span lang=\"BN\">কিন্তু তোমার গর্ভজাত সন্তান এবং আমার ভাই</span>-<span lang=\"BN\">বোনদেরও আগে আমি তোমাকে মা ডেকেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটাই আরকি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার সাথে আমার সম্পর্ক সব সময়ই খুব ভালো ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি কখনোই তাকে বিমাতা হিসেবে দেখিনি</span>, <span lang=\"BN\">তিনিও আমার সাথে কখনোই বিমাতাসুলভ আচরণ করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আমার কখনোই মনে হয়নি যে নিজের মা না থাকার কারণে তিনি আমার সাথে খারাপ ব্যবহার করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">বরং সব সময় বাজার</span>-<span lang=\"BN\">সদাই করা</span>, <span lang=\"BN\">দোকান থেকে এটা</span>-<span lang=\"BN\">সেটা আনতে তাকে আমি সাহায্য করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আমার যে বড় ভাই</span>; <span lang=\"BN\">তিনি তো তখন আমার থেকে অনেক বড়</span>, <span lang=\"BN\">নতুন মায়ের সাথে এডজাস্ট তিনি করতে পারতেন না</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে রাগ করে ভারতে চলে গিয়েছিলেন</span><span lang=\"HI\">।</span>\r\n\r\n<strong><span lang=\"BN\">ছাত্র হিসেবে তো আপনি দুর্দান্ত ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ইন্টারমিডিয়েটে নেত্রকোণা কলেজ থেকে আপনি একাই প্রথম বিভাগ পেয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু ঢাকা বিশ্ববিদ্যালয়ের ফার্মেসী বিভাগে সুযোগ পেয়েও ভর্তি হতে পারেননি</span>… </strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">সুযোগ পেয়েছিলাম</span>, <span lang=\"BN\">কিন্তু ৬৪</span>’ <span lang=\"BN\">সালে কিন্তু একটা বিরাট দাঙ্গা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেই দাঙ্গাটা ব্যাপক আকার ধারণ করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">নারায়ণগঞ্জ</span>, <span lang=\"BN\">সোনারগাঁ</span>, <span lang=\"BN\">মানিকগঞ্জসহ ঢাকার আশেপাশের এলাকায় এ দাঙ্গা ছড়িয়ে পড়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঢাকার ভেতরেও পুরনো ঢাকা</span>, <span lang=\"BN\">লক্ষ্মীবাজার</span>, <span lang=\"BN\">শাঁখারীপট্টিতে ছড়িয়ে পড়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন হিন্দু ছাত্ররা বসবাস করতো জগন্নাথ হলে</span>, <span lang=\"BN\">হল আক্রমণ হতে পারে এ রকম আশঙ্কায় হল কর্তৃপক্ষ একদিন রাতে হল বন্ধ করে দেওয়ার ঘোষণা দিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার এক পিসতুতো ভাই ঢাকা বিশ্ববিদ্যালয়ে ক্যামিস্ট্রিতে পড়ত</span>, <span lang=\"BN\">তিনিও জগন্নাথ হলে থাকতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি ঢাকায় এসে তার রুমেই উঠেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">হল বন্ধ করে দেওয়ার পর আমরা রাতে পালিয়ে তার বাড়ি গফরগাঁও</span>’<span lang=\"BN\">য়ে চলে যাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">রাতে সেখানেই থাকি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু দাঙ্গার ভয়াবহতা ক্রমশ বাড়তে থাকে</span>, <span lang=\"BN\">তখন আমরা স্থির করি যে ঢাকাতে আর ফিরে যাওয়া সম্ভব না</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে আমি নেত্রকোণাতেই ফিরে যাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে যাওয়ার পর আমার বাবা আর কিছুতেই ঢাকাতে ফেরার ব্যাপারে রাজি ছিলেন না</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভারতে চলে যাওয়ার জন্য পারিবারিকভাবে একটা চাপও ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার যে বড় ভাই ভারতে চলে গিয়েছিলেন</span>, <span lang=\"BN\">তিনিও সেখান থেকে চিঠি দিচ্ছিলেন যেন তার কাছে চলে যাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি একটা দোদুল্যমান পরিস্থিতির মধ্যে ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবি হব এটা তো তখনো ভাবিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ছাত্র হিসেবে মোটামুটি ভালোই ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">অন্যদের মধ্যে ক্যারিয়ার নিয়ে যে ভাবনা থাকে</span>, <span lang=\"BN\">আমার মধ্যেও তাই ছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার বাবার তো ইচ্ছা ছিল আপনি ডাক্তার হবেন</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">বাবার ইচ্ছা ছিল কিন্তু আমার সেদিকে আগ্রহ ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেবারই প্রথম ঢাকা বিশ্ববিদ্যালয়ে ফার্মেসী বিভাগ চালু করা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">মাত্র ৩৫ টা আসন সংখ্যা ছিল সেখানে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই পঁয়ত্রিশ জনের মধ্যে লিখিত এবং মৌখিক পরীক্ষা মিলে আমার স্থান ছিল একাদশ</span><span lang=\"HI\">।</span> <span lang=\"BN\">সো আই ডিড ওয়েল</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু ঐটা একটা নতুন সাবজেক্ট</span>, <span lang=\"BN\">ফলে ঐ সাবজেক্টে পড়ার জন্য ইন্টারমিডিয়েটে যে প্রথম স্থান অধিকার করেছিলেন তিনি থেকে শুরু করে সকলেই বেশ উদগ্রীব ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">দাঙ্গার ভয়াবহতা কমলে আমি যখন ঢাকায় ফিরলাম</span>, <span lang=\"BN\">তখন এসে দেখি বিশ্ববিদ্যালয় থেকে আমার নাম কেটে দেওয়া হয়েছে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">নাম কেটে দেওয়ার কারণ কী ছিল</span>? <span lang=\"BN\">সাম্প্রদায়িকতার কারণে</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">অবশ্যই</span><span lang=\"HI\">।</span> <span lang=\"BN\">এছাড়া আর কী কারণ থাকতে পারে</span>? <span lang=\"BN\">এরপর ১৯৬৫ সালে আমি আর কী করব</span>? <span lang=\"BN\">ঢাকা বিশ্ববিদ্যালয়ে তো ভর্তি হতে পারলাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু ঢাকা বিশ্ববিদ্যালয়ে চ্যান্স পেয়েছিলাম</span>, <span lang=\"BN\">ফলে ইঞ্জিনিয়ারিং এ চেষ্টাও করিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন মেডিকেলে পড়ার জন্য সেকেন্ড ডিভিশন ওয়াজ গুড</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের সময় সমগ্র ঢাকা বোর্ড থেকে ১১৯ জন প্রথম বিভাগ পেয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই ১১৯ জনের মধ্যে আমি একজন</span><span lang=\"HI\">।</span> <span lang=\"BN\">মেডিকেলে</span>, <span lang=\"BN\">ঢাকা বিশ্ববিদ্যালয়ের ফিজিক্স</span>-<span lang=\"BN\">কেমিস্ট্রিতে প্রথম বিভাগ পাওয়া কোনো শিক্ষার্থীরাই বাদ যায়নি</span>, <span lang=\"BN\">সবাই চ্যান্স পেয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমিই শুধু ঐ বছর বাদ পড়েছিলাম</span>, <span lang=\"BN\">ভর্তি হতে পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ভবিষ্যত স্বপ্নকে এ ঘটনা খুবই আহত করে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে পড়াশোনার প্রতি আমার আগ্রহ কমতে থাকে</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরের বছর ইঞ্জিনিয়ারিং ইউনিভার্সিটিতে আর্কিটেকচারে ভর্তি হওয়ার জন্য পরীক্ষা দিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এক বছর পড়াশোনা কেন করিনি এটা আমাকে সেখানে ব্যাখ্যা করতে হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">লিখিত পরীক্ষায় পাশ করেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফেল করার কথা আমার মাথায় কখনো আসেইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফেল যে করা যায়</span>, <span lang=\"BN\">এ ধারণা আমার ভেতরে ছিলই না কখনো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি কিন্তু পরে বি</span>.<span lang=\"BN\">এস</span>.<span lang=\"BN\">সিতে দুইবার ফেল করেছিলাম</span>! <span lang=\"BN\">একবার হলো কি</span>, <span lang=\"BN\">মাননীয় রাষ্ট্রপতি আব্দুল হামিদের সঙ্গে দেখা করতে গিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি কলেজ জীবন থেকে আমার ভীষণ ঘনিষ্ঠ ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তো মাননীয় রাষ্ট্রপতি আমাকে জিজ্ঞেস করছিলেন আমি কীভাবে প্রধানমন্ত্রী শেখ হাসিনার সহপাঠী হলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তাকে উত্তর দিলাম</span>- <span lang=\"BN\">ইট ওয়াজ নট বাই মাই চয়েজ</span>, <span lang=\"BN\">ইট ওয়াজ অ্যা নেচারাল ফ্রেন্ডশিপ</span>! <span lang=\"BN\">ঢাকা বিশ্ববিদ্যালয়ের বাংলা বিভাগে কে কে ভর্তি হচ্ছে তা তো আমি জানতাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি ক্লাসে গিয়ে একদিন আবিষ্কার করলাম শেখ মুজিবের কন্যা শেখ হাসিনা আমাদের সহপাঠী</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তখন রাষ্ট্রপতি মহোদয়কে বললাম যে</span>- <span lang=\"BN\">স্যার</span>, <span lang=\"BN\">আমি তো দুইবার বি</span>.<span lang=\"BN\">এস</span>.<span lang=\"BN\">সি ফেল করার পর ১৯৬৭ সালে ঢাকা বিশ্ববিদ্যালয়ে ভর্তি হয়েছিলাম</span>, <span lang=\"BN\">তখন উনি আমাকে বললেন</span>- ‘<span lang=\"BN\">ও</span>! <span lang=\"BN\">আপনি দুইবার ফেল করছিলে</span>...<span lang=\"BN\">ন</span>? <span lang=\"BN\">আমি তিনবার</span>! <span lang=\"BN\">আমরা ফেল করছিলাম বইলাই তো বাংলাদেশ পাশ করল</span>’! <span lang=\"BN\">জীবনের উপর আসলেই সে ঝুঁকি নিতে হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা সে ঝুঁকি নিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধু যখন ছয় দফা দাবি পেশ করলেন</span>, <span lang=\"BN\">তারপরে আর আমাদের মনের ভেতর পড়াশোনা করে বড় হওয়ার আগ্রহটা আমরা হারিয়ে ফেলেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মনে হয়েছিল</span>, <span lang=\"BN\">বঙ্গবন্ধুর ছয় দফা দাবি বাস্তবায়িত হলে আমাকে যে সাম্প্রদায়িকতার কারণে বিশ্ববিদ্যালয়ে ভর্তি হতে দেওয়া হয়নি</span>, <span lang=\"BN\">এ পরিস্থিতি থেকে অন্তত পরবর্তী প্রজন্ম মুক্তি পাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">দেশ স্বাধীন হওয়ার পর তো অন্তত এটুকু নিশ্চিত হয়েছে</span>, <span lang=\"BN\">তাই না</span>? <span lang=\"BN\">এখন বহু হিন্দু ছেলে মেডিকেলে পড়ছে</span>, <span lang=\"BN\">ইঞ্জিনিয়ারিং পড়ছে</span>, <span lang=\"BN\">ভারত থেকে পড়াশোনা করে আবার দেশে ফিরছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আগে একটা কথা ছিল যে হিন্দুরা ডাক্তার</span>-<span lang=\"BN\">ইঞ্জিনিয়ার হয়ে ভারতে চলে যায়</span>, <span lang=\"BN\">সুতরাং স্টপ দেম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাদেরকে ডাক্তার</span>-<span lang=\"BN\">ইঞ্জিনিয়ারিং পড়িয়ে আমাদের রাষ্ট্রের অর্থের অপচয় হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে কিন্তু এ কারণেই বুয়েটে পড়তে দেওয়া হলো না</span><span lang=\"HI\">।</span> <span lang=\"BN\">লিখিত পরীক্ষায় সুযোগ পাওয়ার পর মৌখিকে জিজ্ঞেস করা হলো</span>- <span lang=\"BN\">ভারতে আমার কে আছে</span>? <span lang=\"BN\">আমি বললাম যে আমার বড় ভাই আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে তারা আমাকে আর কোনো প্রশ্নই করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু আমার বড় ভাই সেখানে আছেন</span>, <span lang=\"BN\">তারা ধরেই নিয়েছিলেন যে আমিও চলে যাব</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রশ্ন যেহেতু করেননি</span>, <span lang=\"BN\">আমি ধরে নিয়েছিলাম লিখিত পরীক্ষায় টিকেছি</span>, <span lang=\"BN\">ফলে ভর্তি হতে পারব</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু ফলাফল বের হওয়ার পর দেখলাম যে আমার নাম নেই</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">ছয় দফা আন্দোলন তো খুব কাছ থেকে দেখেছেন</span>, <span lang=\"BN\">সেই আন্দোলনে উজ্জীবিতও হয়েছেন</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এই যে সাম্প্রদায়িক একটা পরিস্থিতি দেশে বিরাজমান ছিল</span>, <span lang=\"BN\">এর থেকে উত্তরণের জন্য বঙ্গবন্ধুর নেতৃত্বে ছয় দফা আন্দোলনের মাধ্যমে স্বায়ত্তশাসিত বাংলাদেশের স্বপ্ন আমরা দেখেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি যেহেতু কবি ছিলাম</span>, <span lang=\"BN\">তাই হয়তো আমার ভাবনা দূরদর্শিতা সম্পন্ন ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি স্বপ্ন দেখতাম</span>- <span lang=\"BN\">স্বায়ত্তশাসিত বাংলাদেশ হলে</span>, <span lang=\"BN\">এর নিয়ন্ত্রণ বাঙালিদের হাতেই চলে আসবে শেষ পর্যন্ত</span>, <span lang=\"BN\">পাকিস্তানিরা বাংলাদেশের উপর নিয়ন্ত্রণ হারাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে ত্রিশ লক্ষ প্রাণের বিনিময়ে ১৯৭১ সালে আমরা যে স্বাধীনতা পেয়েছি</span>; <span lang=\"BN\">সেই স্বাধীনতা হয়তো এত কষ্টের বিনিময়ে অর্জন করতে হতো না</span>- <span lang=\"BN\">যদি ছয় দফার ভিত্তিতে বঙ্গবন্ধু শাসনতন্ত্র প্রণয়নের অধিকার পেতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি সত্তরের নির্বাচনে নিরঙ্কুশ সংখ্যাগরিষ্ঠতা লাভ করার পরও পাকিস্তানিরা তাকে ক্ষমতা হস্তান্তর করতে রাজি হয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধু পাশ করার পর রেসকোর্স ময়দানে এক বিরাট নৌকা বানিয়ে সেখানে ১৬৬ জন সংসদ সদস্য নিয়ে তিনি একটা মিটিং করেছিলেন</span>, <span lang=\"BN\">সেই মিটিং এ তিনি জনগণের উদ্দেশ্যে বলেছিলেন</span>- ‘<span lang=\"BN\">ছয় দফার সঙ্গে যদি কেউ বেঈমানি করে</span>, <span lang=\"BN\">তবে তাকে এই উদ্যানে জ্যান্ত কবর দেবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শেখ মুজিবুর রহমানও যদি করে</span>, <span lang=\"BN\">তাকেও ছাড়বেন না</span>, <span lang=\"BN\">তাকেও এখানেই কবর দেবেন</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">ছয় দফাই হচ্ছে আমাদের মুক্তির একমাত্র পথ</span>, <span lang=\"BN\">এই ছয় দফার পক্ষেই বাংলার মানুষ রায় দিয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং এর সাথে কোনো আপোষ নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরবর্তীতে কিন্তু ইয়াহিয়া এবং ভুট্টোর সাথে যে আলোচনা হলো</span>, <span lang=\"BN\">সেখানে ছয় দফার ভিত্তিতে শাসনতন্ত্র প্রণয়ন না করার জন্য বঙ্গবন্ধুর উপর তারা চাপ সৃষ্টি করেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">বলা হয়েছিল ছয় দফা থেকে সরে এসে তুমি যদি পাকিস্তানের সাথে সংবিধান প্রণয়ন করো</span>, <span lang=\"BN\">তাহলে প্রধানমন্ত্রী করে তোমার কাছে আমরা ক্ষমতা হস্তান্তর করব</span><span lang=\"HI\">।</span> <span lang=\"BN\">ইনফ্যাক্ট প্রেসিডেন্ট ইয়াহিয়া বঙ্গবন্ধুকে প্রাইম মিনিস্টার হিসেবে সম্বোধনও করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু বঙ্গবন্ধু বলেছিলেন</span>, ‘<span lang=\"BN\">আমি প্রধানমন্ত্রীত্ব চাই না</span>, <span lang=\"BN\">আমি বাংলার মানুষের অধিকার চাই</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">এ আন্দোলনের সঙ্গে আত্মিকভাবে আমি যুক্ত হয়ে পড়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধুর এ স্বপ্নের সঙ্গে আমার স্বপ্ন মিশে গিয়েছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">বঙ্গবন্ধুকে নিয়ে প্রথম কবিতাও তো আপনার লেখা</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">আমিই ১৯৬৭ সালে তাকে নিয়ে প্রথম কবিতাটা লিখেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার আগে কেউ বঙ্গবন্ধুকে কবিতার বিষয় হিসেবে বিবেচনা করেন নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার পরবর্তীকালে উনি যখন নির্বাচনে জয় লাভ করলেন</span>, <span lang=\"BN\">তখন জসীম উদ্দীন সাহেব তাকে নিয়ে কবিতা লিখেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি তো বঙ্গবন্ধুর খুব ঘনিষ্ঠ বন্ধুও ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সিকান্দার আবু জাফর</span>, <span lang=\"BN\">সুফিয়া কামালসহ আরো অনেকেই পরে তাকে নিয়ে কবিতা লিখেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি লিখেছিলাম সবচেয়ে আগে</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৬৭ সালে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যখন ছয় দফা আন্দোলনের সময় তাকে কারাবন্দি করা হয়েছিল</span>, <span lang=\"BN\">তিনি জেলখানায় ছিলেন এবং আওয়ামীলীগের প্রায় হাজার হাজার কর্মীকে গ্রেফতার করা হয়েছিল</span>, <span lang=\"BN\">তাদের মুক্তির জন্য ১৯৬৬ সালের ৭ই জুন সারাদেশে হরতাল ডাকা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে হরতালে এগারজন প্রাণ দিয়েছিলেন পুলিশের গুলিতে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারাই ছিলেন স্বাধীনতা আন্দোলনের প্রথম শহীদ</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি একটা কবিতা লিখেছিলাম</span> (‘<span lang=\"BN\">প্রচ্ছদের জন্য</span>’), <span lang=\"BN\">সে কবিতা </span>‘<span lang=\"BN\">সংবাদ</span>’ <span lang=\"BN\">পত্রিকায় ছাপা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সব লাইন তো মনে নেই</span>, <span lang=\"BN\">কয়েকটা লাইন এ রকম ছিল</span>- ‘<span lang=\"BN\">শীতের রোগীর মতো জবুথবু নয়</span>/ <span lang=\"BN\">গঞ্জের জনতার মতো নির্ভীক হতে হবে</span>/<span lang=\"BN\">রক্তের রঙ দেখে ভয় নেই</span>/<span lang=\"BN\">স্বাধীন দেশের মুক্ত জনতা উল্লাস করো সবে</span>’<span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">ইন্টারমেডিয়েটে পড়ার সময় থেকে নাকি আপনার জুয়া খেলার অভ্যাস হয়েছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার আত্মজীবনীমূলক বইয়ে এ বিষয়টা আমি অকপটে স্বীকার করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি যে হোস্টেল রুমে জুয়া খেলতাম</span>, <span lang=\"BN\">এটা আমার হোস্টেল সুপার গোপন সূত্রে খবর পেয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">একদিন খেলছিলাম কয়জন বন্ধু মিলে</span>, <span lang=\"BN\">তিনি এসে হঠাৎ করে দরজায় কড়া নাড়লেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা ভেবেছি আমাদের সাথে খেলার জন্য এক বন্ধুর মুসলিম হোস্টেল থেকে আসার কথা</span>, <span lang=\"BN\">সে বোধহয় এসেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমরা খুব সানন্দেই দরজা খুলে দিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের বিছানার উপর তাস ছড়ানো</span>, <span lang=\"BN\">টাকা পয়সাও ছড়ানো ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্যারকে দেখে তো সবাই দৌড়</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি খুব কড়া মেজাজের মানুষ ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে তিনি কলকাতায় চলে গিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শুনেছি সম্প্রতি তিনি প্রয়াত হয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">খুব ভালো শিক্ষক ছিলেন</span>, <span lang=\"BN\">কিন্তু আমাকে খুবই অপছন্দ করতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে বলতেন</span>- ‘<span lang=\"BN\">মোস্ট ডিস্টার্বিং বয় ইন দ্য হোস্টেল</span>’! <span lang=\"BN\">অবশ্য যুক্তিসংগত কারণেই আমাকে অপছন্দ করতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফাস্ট ডিভিশন পাওয়া ছাত্র আমি</span>, <span lang=\"BN\">অথচ ছাত্রদের মধ্যে জুয়া খেলার প্রচলন আমিই শুরু করি</span>- <span lang=\"BN\">সেটা তো তার পক্ষে মেনে নেওয়া কঠিন ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি আমাকে হোস্টেল থেকে বহিষ্কার করে দিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শুধু বহিষ্কার করেই ক্লান্ত হননি</span>, <span lang=\"BN\">আমাকে শাসিয়েও ছিলেন</span>- <span lang=\"BN\">কীভাবে প্র্যাকটিক্যাল পরীক্ষায় পাশ করি</span>, <span lang=\"BN\">তিনি দেখে নেবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে দেখলাম এই কলেজে থাকলে আমার পাশ করা কঠিন হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি ওই কলেজ থেকে টি</span>.<span lang=\"BN\">সি নিয়ে নেত্রকোণায় চলে গেলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরীক্ষার মাস ছয়েক আগে নেত্রকোণা কলেজে গিয়ে ভর্তি হলাম</span>, <span lang=\"BN\">সেখান থেকেই আইএসসি পরীক্ষা দিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই কথা আমার আত্মজীবনীতে লেখার পরে তিনি কীভাবে জানি জানতে পেরেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রায় ত্রিশ বছর পর একবার আমার বাসায় আসলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই ত্রিশ বছরে কিন্তু তার সঙ্গে আমার দেখা</span>-<span lang=\"BN\">ই হয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি এসে আমাকে বললেন</span>- ‘<span lang=\"BN\">তুমি এসব কথা লিখছো কেন</span>’? <span lang=\"BN\">আমি বললাম যে স্যার সত্য কথা লিখছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">উনি পরে বললেন</span>- ‘<span lang=\"BN\">তুমি মনের থেইকা আমার উপর কোনো রাগ পোষণ করো না</span>, <span lang=\"BN\">আমি কষ্ট পেয়েছি যে তুমি আমার অত্যাচারে কবি হইছো এই কথা বলছো</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">আমি বলেছি</span>, <span lang=\"BN\">এটা ভালো তো</span><span lang=\"HI\">।</span> <span lang=\"BN\">কারো না কারো অত্যাচারে যদি অন্য কারো পুনর্জন্ম হয়</span>, <span lang=\"BN\">সেটাতে মন্দ কি</span>? <span lang=\"BN\">আমি তো খারাপ কিছু লিখি নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্যার</span>, <span lang=\"BN\">এ ব্যাপারটা নিয়ে কিছুটা বিব্রত ছিলেন</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">জুয়ার এই অভ্যাসটা কি দীর্ঘ দিন ছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা অনেক বছর ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি যখন পরে ঢাকাতে এলাম</span>, <span lang=\"BN\">এখানে এসে জুয়া খেলা আমাদের নিত্য দিনের ঘটনাই ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিদেশে গেলেও আমি খেলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে শহরে ক্যাসিনো আছে</span>, <span lang=\"BN\">সেখানেই চলে যেতাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছুদিন আগে লাস ভেগাসে যে ম্যাসাকার হলো</span>; <span lang=\"BN\">ঐ যে একজন মানসিক ভারসম্যহীন একটি লোক সাতান্ন না আটান্নজন লোক মেরে ফেলল</span>, <span lang=\"BN\">ওইখানেও কিন্তু আমি জুয়া খেলার জন্য গিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">লাস ভেগাস হচ্ছে জুয়ার স্বর্গ</span><span lang=\"HI\">।</span> <span lang=\"BN\">সারা পৃথিবীর জুয়াড়িরা সেখানে গিয়ে জুয়া খেলে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আটলান্টিক সিটি আছে</span>, <span lang=\"BN\">সেখানেও খেলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমেরিকার যে এখন প্রেসিডেন্ট ডোনাল্ড ট্রাম্প</span>, <span lang=\"BN\">তার প্লাজাতে আমি বেশি খেলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ট্রাম্প যখন প্রেসিডেন্ট হলেন</span>, <span lang=\"BN\">তাকে আমি একটা চিঠি লিখেছি</span>- <span lang=\"BN\">অ্যা লেটার টু দ্য নিউলি ইলেকটেড প্রেসিডেন্ট</span>! <span lang=\"BN\">তার ওয়েবসাইটে আমি সেটা পোস্টও করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি সেখানে লিখেছি</span>- ‘<span lang=\"BN\">আপনার ট্রাম্প প্লাজাতে আমি অনেকদিন জুয়া খেলেছি এবং অনেক টাকা হেরেছি</span>; (<span lang=\"BN\">হি ডিক্লিয়ার হিমসেলফ ব্যাংক</span>-<span lang=\"BN\">রাপ্ট থ্রাইস অর ফোর্থ টাইম</span><span lang=\"HI\">।</span> <span lang=\"BN\">হি নিউ হাউ টু চিট দ্য আমেরিকান সিস্টেম</span>) <span lang=\"BN\">আপনি নিজেকে ব্যাংক</span>-<span lang=\"BN\">রাপ্ট ঘোষণা করেছিলেন</span>; <span lang=\"BN\">আপনার কাছে তখন অর্থ দাবি করা সমুচিত হবে না ভেবেছি</span>, <span lang=\"BN\">এখন তো আপনি আমেরিকার প্রেসিডেন্ট হয়েছেন</span>, <span lang=\"BN\">এখন যদি আপনি টাকাটা ফেরত না দেন</span>, <span lang=\"BN\">অন্তত আপনার কনভেকশনে আমাকে ডাকবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার ক্যাসিনোতে আমি আবার খেলতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আর আপনার কাছে আমার একটাই আর্জি</span>- <span lang=\"BN\">আপনি আমার কথা বিবেচনা করে</span>, <span lang=\"BN\">বাংলাদেশের অভিবাসীদের সাথে কোনো দুর্ব্যবহার করবেন না</span>’! <span lang=\"BN\">আমি হ্যাপি ছিলাম ট্রাম্প জয়ী হওয়ার কারণে</span>, <span lang=\"BN\">কারণ হিলারী সম্পর্কে আমার ডিজার্ভেশন ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার বন্ধু শেখ হাসিনাকে নানাভাবে নাজেহাল করার প্রস্তুতি তার মধ্যে ছিল এবং তিনি ইতোপূর্বেও সেটা করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমি চাইনি হিলারী ক্লিনটন জিতুক</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">লেখালেখির শুরুটা কখন থেকে</span>? <span lang=\"BN\">কীভাবে শুরু করলেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">লেখালেখির শুরুটা তো আসলে</span>…<span lang=\"BN\">আমি মুখে মুখে ছড়া</span>-<span lang=\"BN\">টড়া বানাতে পারতাম আগে থেকেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের এলাকার পুরনো কবিদের বেশ সম্মান করতাম</span>; <span lang=\"BN\">কেন করতাম জানি না</span>, <span lang=\"BN\">অন্যদের তো করতাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">হয়তো আমার মধ্যে একটা ব্যাপার ছিল</span>, <span lang=\"BN\">মানুষ হয়তো তার অজ্ঞাতসারেই নিয়তি দ্বারা পরিচালিত হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বিজ্ঞানে বিশ্বাসী ছিলাম</span>, <span lang=\"BN\">ভাগ্যে ছিলাম অনাস্থাশীল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন যেমন দেখতে পাচ্ছি</span>, <span lang=\"BN\">ভবিতব্যে জীবনের মিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভবিতব্য বলে একটা জিনিস থাকতে পারে</span>, <span lang=\"BN\">নাহলে রবীন্দ্রনাথরা ভাই</span>-<span lang=\"BN\">বোন মিলে তের</span>-<span lang=\"BN\">চৌদ্দজন ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার অন্য ভাই</span>-<span lang=\"BN\">বোনরা তো কেউ কবি হননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">একই পিতা</span>-<span lang=\"BN\">মাতার ঘরে জন্ম নিয়েছেন</span>, <span lang=\"BN\">একই পরিবেশে ঠাকুর বাড়ির আঙিনার মধ্যে বড় হয়েছেন</span>- <span lang=\"BN\">সেখান থেকেও তো রবীন্দ্রনাথ একজনই হয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমান বলেছেন যে তিনি কবিতা লিখেছেন তার বোন মারা যাওয়ার পর</span><span lang=\"HI\">।</span> <span lang=\"BN\">বোনের অকাল মৃত্যু তাকে খুব কষ্ট দিয়েছিল বলে তিনি একটা কবিতা লিখেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপর থেকে তার মধ্যে ওই উপলব্ধি আসে যে তিনি কবিতা লিখতে পারেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন আমি কবিতা কেন লিখেছি</span>, <span lang=\"BN\">এটা স্পষ্ট করে বলতে পারব না</span><span lang=\"HI\">।</span> <span lang=\"BN\">কেউ যুক্তিসঙ্গত</span>-<span lang=\"BN\">ভাবে এটা ব্যাখ্যা করতে পারবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার পক্ষেও বলা কঠিন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে আমার নিজ এলাকার আঞ্চলিক কবি এবং লোকসাহিত্যের ব্যাপারে ভীষণ আগ্রহ অনেক আগে থেকেই ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঐ এলাকায় কোনো কবি আছে কি</span>-<span lang=\"BN\">না</span>, <span lang=\"BN\">সেটা জানার</span>, <span lang=\"BN\">সে বিষয়ে খোঁজ নেওয়ার এবং তথ্য সংগ্রহ করার প্রতি আমি আগ্রহী ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাদের জানার পরে অনেকটা প্রভাবিত হয়েই এ জগতের দিকে কিছুটা ধাবিত হয়েছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার প্রথম বই </span>‘<span lang=\"BN\">প্রেমাংশুর রক্ত চাই</span>’ <span lang=\"BN\">ছাপানোর অভিজ্ঞতা কেমন ছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমার প্রথম কবিতা ১৯৬১ সালে </span>‘<span lang=\"BN\">উত্তর আকাশ</span>’ <span lang=\"BN\">নামের একটি পত্রিকায় ছাপা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এ পত্রিকার সম্পাদনা করতেন খালেক দাদ চৌধুরী</span><span lang=\"HI\">।</span> <span lang=\"BN\">এ পত্রিকায় একটি সাহিত্য বিভাগ ছিল</span>, <span lang=\"BN\">নেত্রকোণার প্রবীণ এবং তরুণ কবি</span>-<span lang=\"BN\">সাহিত্যকরাই সেখানে বেশি লিখতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপর মাঝখানে অনেকদিন গ্যাপ দেওয়ার পর ১৯৬৫ সালের একুশে ফেব্রুয়ারির উপর লেখা একটা কবিতা ঢাকার </span>‘<span lang=\"BN\">সাপ্তাহিক জনতা</span>’ <span lang=\"BN\">বলে একটা পত্রিকায় ছাপা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে আবার ঢাকা বিশ্ববিদ্যালয়ের ফার্মেসী বিভাগ</span>, <span lang=\"BN\">ইঞ্জিনিয়ারিং</span>, <span lang=\"BN\">বি</span>.<span lang=\"BN\">এস</span>.<span lang=\"BN\">সিতে ভর্তি হওয়ার চক্করে লেখালেখিতে ভাটা পড়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এক দিক থেকে অবশ্য ভালোই হলো</span>; <span lang=\"BN\">প্রাতিষ্ঠানিক শিক্ষার ক্ষেত্রে আমি যে হোঁচট খেলাম</span>, <span lang=\"BN\">সেটা আমাকে বাউন্স ব্যাক করতে সুবিধা করল</span>- <span lang=\"BN\">আই গো অ্যান্ড ব্যাক টু মাই ওউন ওয়ার্ল্ড</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার নিজস্ব জগৎ যে কাব্য</span>, <span lang=\"BN\">সেটার দিকে আমার নিয়তি আমাকে ঠেলে দিলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি আবার কবিতা লেখা শুরু করলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথমে বঙ্গবন্ধুর পক্ষে রাজনৈতিক কবিতা</span>; <span lang=\"BN\">তারপর আস্তে আস্তে প্রেম বোধ জাগ্রত হলো</span>, <span lang=\"BN\">তখন প্রেমের কবিতা</span>…<span lang=\"BN\">এভাবে আস্তে আস্তে কবিতার প্রতি ঝুঁকে পড়লাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবিতার বইটা প্রকাশিত হয়েছে অনেক পরে</span>, <span lang=\"BN\">১৯৭০ সালে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে তার আগেই ৬৭</span>’ <span lang=\"BN\">সালে ঢাকা বিশ্ববিদ্যালয়ে ভর্তি হওয়ার পর আমি </span>‘<span lang=\"BN\">সংবাদ</span>’, ‘<span lang=\"BN\">আজাদ</span>’, ‘<span lang=\"BN\">পয়গাম</span>’, ‘<span lang=\"BN\">পূর্বদেশ</span>’- <span lang=\"BN\">সব কাগজেই লিখতে শুরু করেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তরুণ কবি হিসেবে মোটামুটি খ্যাতি বই প্রকাশের আগেই পেয়ে গিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু বই প্রকাশের ব্যাপারে প্রথমে কোনো প্রকাশকই আগ্রহ দেখাননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিভিন্ন প্রকাশনীর কাছে গিয়েছি</span>, <span lang=\"BN\">তারা একজন নতুন কবির ব্যাপারে তেমন আগ্রহ প্রকাশ করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমানের বই তার এক বন্ধু নিজের টাকায় প্রকাশ করে দিয়েছিলেন</span>; <span lang=\"BN\">আল মাহমুদের বই তার বন্ধুরা মিলে একটা প্রকাশনা সংস্থা দিয়েছিলেন</span>, <span lang=\"BN\">সেখান থেকে বের করেছিলেন</span>, <span lang=\"BN\">আব্দুল মান্নান সৈয়দও নিজের টাকাতেই বই বের করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি</span>-<span lang=\"BN\">ই কিন্তু প্রথম যে প্রফেশনাল প্রকাশক দিয়ে বই প্রকাশ করিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">খান ব্রাদার্স থেকে ১৯৭০ সালের নভেম্বর মাসে আমার প্রথম বই </span>‘<span lang=\"BN\">প্রেমাংশুর রক্ত চাই</span>’ <span lang=\"BN\">প্রকাশিত হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৭১ সালের ২৫ মার্চ থেকে তো স্বাধীনতার জন্য যুদ্ধই শুরু হয়</span>, <span lang=\"BN\">ফলে এ বইটি খুব বেশি দিন বাজারে ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">মুক্তিযুদ্ধের সময় ১৯৭১ সালে কলকাতা থেকে চিত্তরঞ্জন সাহা বইটি আবার প্রকাশ করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি মুক্তধারা সাহিত্য সংগঠন করতেন</span>, <span lang=\"BN\">সেখান থেকেই বইটি বের হয়েছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আমি শুনেছি </span>‘<span lang=\"BN\">প্রেমাংশুর রক্ত চাই</span>’ <span lang=\"BN\">বইটির প্রথম কপিটি আপনি একজন গণিকাকে দিয়েছিলেন</span>... </strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা তুমি কোথায় শুনলে</span>? <span lang=\"BN\">বই এর মধ্যে লিখেছি নাকি</span>?\r\n\r\n<strong> <span lang=\"BN\">আমি কোথায় যেন এক জায়গায় পড়েছিলাম ঘটনাটা</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">বইটার যে প্রচ্ছদটা ছাপা হয়েছিল বাবু বাজারের খুশি প্রিন্টার্স বলে একটা জায়গা থেকে</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথম বই বেরোচ্ছে খুব আবেগে এবং ফূর্তিতে ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বইটা যেখানে ছাপা হয়েছে তার পাশেই একটা গণিকা পল্লী ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে আমাদের কয়েকজন বন্ধুর নিয়মিত যাতায়াত ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথম বই ছাপানোর উত্তেজনাটা কারো সাথে শেয়ার করার ইচ্ছা হয়েছিল আমার</span>, <span lang=\"BN\">এই আবেগটাকে শেয়ার করার জন্য কাউকে পাইনি তখন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেই সূত্রেই আমার সঙ্গে যার ভালো সম্পর্ক ছিল</span>, <span lang=\"BN\">ওই মহিলাকে বইয়ের কাভারটা দিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে জিজ্ঞেস করলো </span>‘<span lang=\"BN\">এটা কী</span>’? <span lang=\"BN\">সে তো এ বিষয়ে কিছু জানে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বললাম এটা তোমার ঘরের মধ্যে টাঙানো থাক</span>, <span lang=\"BN\">তুমি যে আমার খুব প্রিয়</span>- <span lang=\"BN\">এটা তারই প্রমাণ বহন করবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি নিজেই ওইটা তার ঘরে টাঙিয়ে দিয়েছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">পঁচাত্তর পরবর্তী সময় আপনাকে তো গ্রেফতার করা হয়েছিল বলে শুনেছি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">সেনাবাহিনী ধরে নিয়ে গিয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">৭ই নভেম্বরের পাল্টা অভ্যুত্থানে যখন খালেদ মোশাররফসহ অনেকেই নিহত হয়েছিলেন</span>, <span lang=\"BN\">তখন সেনাবাহিনীর মধ্যে একটা বিশৃঙ্খলার সৃষ্টি হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা সেটাকে সিপাহী বিপ্লব বলে দাবি করেছিলেন</span>, <span lang=\"BN\">কিন্তু তাদের চরিত্র</span>- <span lang=\"BN\">কোনো বিপ্লবী চরিত্র ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">খালেদ মোশাররফকে হত্যা করার পর সেনাবাহিনীর সদস্যরা তার বাড়ি লুট করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">শুধুমাত্র একটি ভাঙা চেয়ার রেখে সবই তারা লুট করে নিয়ে যায়</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং যারা ৭ই নভেম্বরের বিপ্লবের কথা বলে</span>, <span lang=\"BN\">তাদের চরিত্রে যদি বিন্দুমাত্র বিপ্লবের উপাদান থাকতো তাহলে খালেদ মোশাররফের বাড়ি এভাবে লুণ্ঠিত হতো না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার সঙ্গে আরো যারা নিহত হয়েছিলেন বজলুল হুদা এবং এটিএম হায়দার</span>- <span lang=\"BN\">তাদের তিনজনকে একসঙ্গে হত্যা করা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে সময় যখন সেনা কর্মকর্তাদের হত্যা করা হচ্ছিল</span>, <span lang=\"BN\">বিটিভি তখন রামপুরাতে স্থানান্তরিত হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে গিয়ে আকমল</span>, <span lang=\"BN\">মুনীরুল আলম</span>- <span lang=\"BN\">এ রকম কয়েকজনকে ওরা ভারতীয় চর অভিযোগে হত্যা করে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেনাবাহিনীর উপর কোনো নিয়ন্ত্রণ নেই</span>, <span lang=\"BN\">কোনো চেইন অব কমান্ড নেই</span>-  <span lang=\"BN\">এ রকম একটা অবস্থায় আমার বন্ধু আবুল হাসান তখন পিজি হাসপাতালে চিকিৎসাধীন ছিলেন</span>, <span lang=\"BN\">তাকে দেখতে গিয়ে আমি অপেক্ষমান সেনাবাহিনীর হাতে ধরা পড়ি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা সেখানে কার জন্য</span>, <span lang=\"BN\">কেন অপেক্ষা করছিল</span>- <span lang=\"BN\">এ প্রশ্নের জবাব মেলেনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আমার মনে হয়েছে</span>- <span lang=\"BN\">আমাকে ধরেই যখন তাদের অপেক্ষার শেষ হলো</span>, <span lang=\"BN\">তখন আমার জন্যই বোধহয় অপেক্ষা করছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">একজন কর্নেল আমাকে অ্যারেস্ট করে একটা ট্রাকে উঠতে বললেন</span>, <span lang=\"BN\">যেহেতু সেনাবাহিনীর মধ্যে তখন কোনো কমান্ড নেই</span>, <span lang=\"BN\">ফলে আমি ঐ ট্রাকে উঠতে ভয় পাচ্ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">ট্রাকের মধ্যে অধিকাংশই অশিক্ষিত জোয়ানরা আছে</span>, <span lang=\"BN\">আমি কবি হিসেবে কতটা মূল্য বহন করি</span>- <span lang=\"BN\">তারা তো সেটা বুঝবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার সঙ্গে হয়তো মিসবিহেভ করবে</span>, <span lang=\"BN\">কিংবা মেরেও ফেলতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে কর্নেল আমাকে গ্রেফতার করল</span>, <span lang=\"BN\">তাকে আমি গিয়ে বললাম</span>- <span lang=\"BN\">স্যার</span>, <span lang=\"BN\">আমি আপনার সঙ্গে যেতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু তিনি তাতে রাজি হলেন না</span>, <span lang=\"BN\">ফলে জোয়ানদের রোষের মধ্যে আমাকে পড়তে হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা ভাবলো আমি তাদের প্রতি আস্থা রাখিনি</span>, <span lang=\"BN\">তাদের সঙ্গে যেতে রাজি হইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমার দিকে অস্ত্র তাক করে তারা ভয় দেখানো শুরু করল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপর আমাকে সোহরাওয়ার্দীর ভেতরে পুলিশ ক্যাম্পে নিয়ে যাওয়া হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন যেটা থানা হয়েছে</span>, <span lang=\"BN\">আগে সেটা পুলিশের অস্থায়ী ক্যাম্প ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">যখন আমি ঐ ক্যাম্পের ভেতরে ঢুকছি</span>, <span lang=\"BN\">কর্নেল সাহেব তখন বের হয়ে যাচ্ছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি সেল্যুট দিয়ে তার দৃষ্টি আকর্ষণ করার চেষ্টা করলাম</span>, <span lang=\"BN\">তিনি না তাকিয়েই চলে গেলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভেতর থেকে একজন পুলিশ অফিসার এসে আমাকে রিসিভ করে নিয়ে গেলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভেতরে নিয়ে যাওয়ার পর পুলিশ অফিসারটি আমার কানে কানে বলল</span>- <span lang=\"BN\">আপনি ভয় পাবেন না</span>, <span lang=\"BN\">যে অফিসার আপনাকে অ্যারেস্ট করেছেন</span>, <span lang=\"BN\">তিনি আমাদের লিখিত দিয়ে গেছেন</span>- ‘<span lang=\"BN\">ডোন্ট মিসবিহেভ উইথ হিম</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">ঐ কর্নেল ভদ্রলোকের নাম ছিল কর্নেল নওয়াজেশ আহমেদ</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে ঐ ক্যাম্প থেকে রমনা থানার পুলিশকে ডাকা হলো</span>, <span lang=\"BN\">যাতে তাদের কাস্টডিতে আমাকে নিয়ে যাওয়া হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে রমনা থানায় আমাকে নিয়ে যাওয়া হলো</span>, <span lang=\"BN\">ওইখানে আমি সাত দিন ছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">রমনা থানায় আপনি যখন ছিলেন</span>, <span lang=\"BN\">আপনাকে মুক্তি দেওয়ার জন্য বোধহয় বিবৃতি প্রকাশ করার চেষ্টা করেছিলেন কবি মহাদেব সাহা</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন শামসুর রাহমান</span>, <span lang=\"BN\">হাসান হাফিজুর রহমান এবং আহসান হাবীব নাকি আপনার পক্ষে কাজ করার জন্য তাকে ডিসকারেজ করেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span><span lang=\"HI\">।</span> <span lang=\"BN\">মহাদেব অ্যাটেম্প নিয়েছিল আমাকে মুক্তির জন্য একটা বিবৃতি প্রকাশ করতে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু তুমি যাদের নাম বললে</span>, <span lang=\"BN\">তারা নিজেরা তো বিবৃতি দিতে রাজি ছিলেনই না</span>, <span lang=\"BN\">মহাদেবকেও ডিসকারেজ করেছিলেন এটা বলে যে</span>- ‘<span lang=\"BN\">তোমার এটা নিয়ে মুভ করা উচিত না</span>, <span lang=\"BN\">কারণ বঙ্গবন্ধু নিহত হওয়ার পর নির্মল কোথায় ছিল সে সম্পর্কে আমাদের স্পষ্ট ধারণা নেই</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধুকে হত্যার পর আমি গ্রামে ফিরে গিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ধারণা ছিলো</span>- <span lang=\"BN\">যে কোনো সময় আমাকে মেরে ফেলা হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছুটা মানসিক ভারসাম্যও হারিয়ে ফেলেছিলাম আমি</span><span lang=\"HI\">।</span> <span lang=\"BN\">দীর্ঘ সময় আমাকে অনুপস্থিত দেখে</span>, <span lang=\"BN\">তারা অনুমান করতে পারেননি হোয়াট আই ডিড দ্যাট টাইম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তো অ্যারেস্ট হয়েছি ১১ নভেম্বর</span>, <span lang=\"BN\">বঙ্গবন্ধুকে হত্যা করা হয়েছে ১৫ আগস্ট</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৮ আগস্ট আমি ঢাকা ত্যাগ করেছি অ্যান্ড কেম ব্যাক টু ঢাকা অন ফাস্ট নভেম্বর</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে এই আড়াই মাস সময় যে আমি বাড়িতে ছিলাম</span>, <span lang=\"BN\">এ সময় কিন্তু কাদের সিদ্দিকীর নেতৃত্বে ভারতে একটা বাহিনী গড়ে উঠেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা বঙ্গবন্ধু হত্যার প্রতিশোধ নেওয়ার চেষ্টা চালাচ্ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে অনেকেই যোগদান করেছিল এবং মজার বিষয় হচ্ছে ওই বাহিনীতে ময়মনসিংহের পূর্বাঞ্চলের মানুষই বেশি ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তো তারা মাঝে মাঝে আক্রমণ করতো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার যেহেতু বাড়ি ঐদিকেই</span>, <span lang=\"BN\">ফলে অনেকের ধারণা হয়েছিল সম্ভবত আমি কাদেরিয়া বাহিনীর সঙ্গে যুক্ত হয়েছি</span>, <span lang=\"BN\">আর ভারতের সঙ্গেও বোধহয় আমার একটা যোগাযোগ আছে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">ব্যক্তি লেখক এবং তার লেখক সত্ত্বা কী অভিন্ন নাকি আলাদা</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আলাদা কি করে হয়</span>- <span lang=\"BN\">এটা আমি বুঝি না</span><span lang=\"HI\">।</span> <span lang=\"BN\">যিনি লেখক তিনি তো তার দেহের বাইরের সত্ত্বা নন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি দেহের ভেতরেই বাস করেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং এই দেহের সঙ্গে তার তো কোনো বিরোধ নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে আমি যেরকম অকপটে জীবনের সকল সত্য প্রকাশ করি</span>, <span lang=\"BN\">তাতে আমাকে অন্যের কাছ থেকে ভিন্ন মনে হতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা হয়তো বলতে পারো</span>- <span lang=\"BN\">আপনার জীবনের সঙ্গে অন্যের জীবনের মিল নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেমন একজন বলেছিল</span>- <span lang=\"BN\">আপনি যেভাবে আপনার বোহেমিয়ান জীবনের কথা বর্ণনা করেন</span>, <span lang=\"BN\">তাতে আমাদের পক্ষে আপনার রচনা পাঠ করার কথা ছেলে</span>-<span lang=\"BN\">মেয়েদের বলা সম্ভব হয় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বললাম</span>- <span lang=\"BN\">আমি আমার জীবনের অভিজ্ঞতাকে বর্ণনা করছি</span>; <span lang=\"BN\">সে অভিজ্ঞতা মানুষকে তার ভুল থেকে শিক্ষা গ্রহণ করতে আগ্রহী করবে</span>, <span lang=\"BN\">এ অভিজ্ঞতা মানুষকে আপোষহীন সংগ্রামের মধ্যে দিয়ে জীবন যাপন করা শেখাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঢাকা শহরে থাকার মতো কোনো ব্যবস্থা আমার ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৬৬ সালে একটা পত্রিকায় আড়াইশ টাকা বেতনের চাকরি পাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথম বেতন পেয়ে আমি একটা বালিশ কিনেছিলাম যে নিজের বালিশে ঘুমাতে পারব</span>, <span lang=\"BN\">একটা থালা কিনেছিলাম এটা ভেবে যে নিজের থালায় খাব</span><span lang=\"HI\">।</span> <span lang=\"BN\">নিজের থালা</span>-<span lang=\"BN\">বালিশ কেনা আমার সঙ্গতির মধ্যে ছিল না</span>; <span lang=\"BN\">চিন্তার মধ্যেও ছিল না</span>, <span lang=\"BN\">এগুলো যে আমার নেই</span>- <span lang=\"BN\">সেই উপলব্ধিও আমার ছিল না</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">আমার কণ্ঠস্বর</span>’ <span lang=\"BN\">বইটি পড়লে আমার জীবন সংগ্রামের সঙ্গে পাঠকের যে পরিচয় হবে</span>- <span lang=\"BN\">সেটা তাদেরকে জীবনে সংগ্রাম করতে সহায়তা করবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিশ্ববিদ্যালয়ের ফার্মেসীতে ভর্তি হতে পারলাম না</span>; <span lang=\"BN\">মেডিকেলে পড়তে পারলাম না</span>, <span lang=\"BN\">কিন্তু আমার জীবনে যে বড় হওয়ার স্বপ্ন</span>- <span lang=\"BN\">সেটা মরে যায়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রতিকূলতার সঙ্গে সংগ্রাম করে</span>; <span lang=\"BN\">বর্ষা</span>-<span lang=\"BN\">প্লাবনের মধ্যে পাল্লা দিয়ে যে ধানগুলো বাড়ে</span>- <span lang=\"BN\">আমি সেভাবে প্রতিকূলতার সঙ্গে লড়ে মাথা উঁচু করে বাঁচার জন্য সংগ্রাম করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে কারণে আমার অকপট স্বীকারোক্তিগুলো মনে হয় সবার জানা জরুরি</span>, <span lang=\"BN\">জরুরি না হলেও আমি কী রকম সেটা তো আমাকে বলতেই হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি মিথ্যে কথা বলে মানুষের চিত্ত জয় করতে চাই না</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার অসংখ্য প্রেমের কবিতা আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার লেখালেখি জীবনে নারী এবং প্রেমকে কীভাবে মূল্যায়ন করেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা তো একটা প্রাকৃতিক সত্য</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে আমাদের সৃজন করেছেন অর্থাৎ স্রষ্টা যিনি</span>, <span lang=\"BN\">তিনি নারী এবং পুরুষদের পরস্পরের প্রতি আকৃষ্ট হওয়ার জন্য কিছু মন্ত্র তাদের ভেতরে দিয়ে দিয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার </span>‘<span lang=\"BN\">নিশিকাব্য</span>’<span lang=\"BN\">র ভূমিকায় আছে</span>, <span lang=\"BN\">এটা হাদিস থেকে উদ্ধৃত করছেন একজন তিউনিশিয়ান কবি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি বলেছেন</span>- ‘<span lang=\"BN\">সকল প্রশংসা আল্লাহর</span>, <span lang=\"BN\">যিনি এই পুরুষের প্রিয় অঙ্গসমূহ নারীর দেহে এবং নারীর প্রিয় অঙ্গসমূহ পুরুষের দেহে সংস্থাপন করেছেন</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">এখন তিনি একটা নারী মূর্তি তৈরি করলেন আর একটা পুরুষ মূর্তি তৈরি করলেন</span>- <span lang=\"BN\">তারা যদি পরস্পর আকৃষ্ট বোধ না করে</span>, <span lang=\"BN\">ইফ দে ডু নট এপ্রিশিয়েট ইচ আদার</span>- <span lang=\"BN\">তাহলে তো তারা ইউনাইটেড হওয়ার প্রয়োজন বোধ করবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আর যদি ইউনাইটেড হওয়ার প্রয়োজন বোধ না করে</span>, <span lang=\"BN\">তাহলে তো সৃষ্টির প্রক্রিয়ার মধ্যে সে অংশগ্রহণ করবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথের একটা গান আছে না</span>-  <span lang=\"BN\">তুমি তাই এসেছ নীচে</span>/<span lang=\"BN\">আমায় নইলে ত্রিভুবনেশ্বর</span>/<span lang=\"BN\">তোমার প্রেম হত যে মিছে</span>/ <span lang=\"BN\">আমায় নিয়ে মেলেছ এই মেলা</span>/<span lang=\"BN\">আমার হিয়ায় চলছে রসের খেলা</span>/<span lang=\"BN\">মোর জীবনে বিচিত্ররূপ ধরে</span>/<span lang=\"BN\">তোমার ইচ্ছা তরঙ্গিছে</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">অর্থাৎ এখানে স্রষ্টার ইচ্ছাটা তরঙ্গিত হচ্ছে নারী এবং পুরুষের মাধ্যমে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যার ফলে নারী</span>-<span lang=\"BN\">পুরুষের পরস্পরের প্রতি একটা বিশেষ আকর্ষণ থাকে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা শুধু মানুষের ক্ষেত্রে না</span>, <span lang=\"BN\">রিপ্রোডাকশন বা নিজেকে পুনরায় সৃষ্টি করার একটা আগ্রহের দিক থেকে পৃথিবীর সকল জীব</span>-<span lang=\"BN\">জন্তুর মধ্যেই ঐক্য আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং নারীর প্রতি যে পুরুষের আকর্ষণ বা পুরুষের প্রতি যে নারীর আগ্রহ</span>- <span lang=\"BN\">এটা প্রাকৃতিকভাবেই আমাদের মধ্যে স্রষ্টা দিয়ে দিয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা শুধু তার ডাকে সাড়া দিচ্ছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের ভেতরের যে প্রবণতাগুলো শৈশবে সুপ্ত আকারে থাকে</span>, <span lang=\"BN\">সে সময় তো আমরা নারী</span>-<span lang=\"BN\">পুরুষের ব্যবধানটা বুঝতে পারি না</span>, <span lang=\"BN\">মানে পার্থক্যগুলো তো ছোটবেলায় ধারণাতে আসে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আচার</span>-<span lang=\"BN\">আচরণের মধ্যেও কোনো পার্থক্য বোঝা যায় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু বয়োঃবৃদ্ধির সঙ্গে সঙ্গে বোঝা যাচ্ছে যে নারীর প্রতি পুরুষের বা পুরুষের প্রতি নারীর এক ধরণের কাম তৃষ্ণা জাগ্রত হচ্ছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে এটারই একটা সভ্য</span>-<span lang=\"BN\">সংস্কৃত প্রকাশ হচ্ছে </span>‘<span lang=\"BN\">প্রেম</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">এই প্রেমটা কোনো মৌলিক রিপু নয়</span>, <span lang=\"BN\">মৌলিক রিপু হচ্ছে </span>‘<span lang=\"BN\">কাম</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">মানুষ আর অন্য প্রাণীর মধ্যে পার্থক্য হলো</span>, <span lang=\"BN\">নিজেদের পরিবর্তন করার শক্তি আমাদের আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের ভেতরে যে প্রকৃতি প্রদত্ত হয়েছে</span>, <span lang=\"BN\">সে প্রকৃতি পরিবর্তন করার ক্ষমতাও আমাদের রয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমরা ধর্ষণকে উৎসাহিত করি না</span>, <span lang=\"BN\">প্রেমকে উৎসাহিত করি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ধর্ষণ সামাজিক বিশৃঙ্খলার জন্য করা হয়</span>, <span lang=\"BN\">এটা অন্যায় এবং পাপ</span><span lang=\"HI\">।</span> <span lang=\"BN\">মানব জাতির গর্ভধারিণী হচ্ছে নারী</span>, <span lang=\"BN\">সেই নারীকে পূজনীয় জ্ঞান করা</span>, <span lang=\"BN\">তার প্রতি কৃতজ্ঞতা বোধে নত হওয়া যেখানে পুরুষের কর্তব্য</span>, <span lang=\"BN\">সেখানে শিকড় অস্তিত্বের এই নিগুঢ় সত্যটিকে উপলব্ধি করতে না পেরে ধর্ষক তার কামকে চরিতার্থ করার জন্য নারীর উপর অত্যাচার করছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সঙ্গম আর ধর্ষণের মধ্যে যে পার্থক্য আছে</span>, <span lang=\"BN\">এটা ঐ ধর্ষকরা ভুলে যাচ্ছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখানে সে অন্য বন্য প্রাণীর থেকেও ভয়ঙ্কর আচরণ করছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে তাকে পাশবিক যখন বলা হয়</span>, <span lang=\"BN\">তখন মূলত পশুকে অপমান করা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">পশুর মধ্যে এই পাশবিকতা নেই</span>, <span lang=\"BN\">যে অন্যায়</span>, <span lang=\"BN\">নিষ্ঠুর আচরণ আমরা মানুষের মধ্যে প্রত্যক্ষ করি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কদিন আগে আমি লিখেছি</span>- <span lang=\"BN\">মায়ানমারের বিরুদ্ধে যুদ্ধ করতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেটা আমি লিখেছি</span>- <span lang=\"BN\">রোহিঙ্গা নারীদের উপর অত্যাচারের ভিডিওগুলো দেখে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে নারীকে ধর্ষণ করা হচ্ছে</span>, <span lang=\"BN\">তাকেই আবার মেরে ফেলা হচ্ছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">মানুষের চরিত্রের এই অধঃপতন</span>, <span lang=\"BN\">অমানবিকতাকে পরাভূত না করা পর্যন্ত মানুষ যে সভ্য</span>- <span lang=\"BN\">এটা দাবি করা যাবে না</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">সব সৃষ্টিই তো স্রষ্টার কাছে খুব প্রিয় বা সন্তানতুল্য</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা আপনার কণ্ঠে আপনার সৃষ্ট একটি প্রিয় কবিতা শুনতে চাই</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এই কবিতাটা</span> (‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’) <span lang=\"BN\">আমারও খুব প্রিয়</span>, <span lang=\"BN\">পাঠকদেরও প্রিয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আবার গ্রামীণফোনেরও খুব প্রিয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">গ্রামীণফোন এটাকে ওয়েলকাম টিউন হিসেবে ব্যবহার করে ব্যবহারকারীদের কাছ থেকে মাসে ত্রিশ টাকা করে কেটে নিচ্ছে</span>, <span lang=\"BN\">কিন্তু আমাকে তারা আজ পর্যন্ত একটি পয়সাও রয়্যালিটি দেয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে জন্য আমি এই কবিতাটা পাঠ করছি এবং গ্রামীণফোনের প্রতি এই অভিযোগ উত্থাপন করছি</span>- <span lang=\"BN\">তারা আমার এই কবিতাটি বহু বছর ধরে ব্যবহার করে আসছে</span>, <span lang=\"BN\">কিন্তু আমি তাদের কাছে আজ পর্যন্ত কোনো টাকা পাইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">অনেকবার আমি ফেসবুকের স্ট্যাটাসেও এটা লিখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের বর্তমান যে টেলিযোগাযোগ প্রতিমন্ত্রী আছেন</span>; <span lang=\"BN\">তাকেও বলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারাও আমার এই ন্যায্য</span>-<span lang=\"BN\">ন্যায়সঙ্গত দাবি আদায় করে দিতে পারেননি</span>, <span lang=\"BN\">কিংবা কোনো উদ্যোগই হয়তো গ্রহণ করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি এর প্রতিবাদ জানিয়ে কবিতাটি পাঠ করছি</span>…\r\n\r\n“<span lang=\"BN\">আমি বলছি না ভালোবাসতেই হবে</span>, <span lang=\"BN\">আমি চাই</span>\r\n\r\n<span lang=\"BN\">কেউ একজন আমার জন্য অপেক্ষা করুক</span>,\r\n\r\n<span lang=\"BN\">শুধু ঘরের ভিতর থেকে দরোজা খুলে দেবার জন্য</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">বাইরে থেকে দরোজা খুলতে খুলতে আমি এখন ক্লান্ত</span><span lang=\"HI\">।</span>\r\n\r\n&nbsp;\r\n\r\n<span lang=\"BN\">আমি বলছি না ভালোবাসতেই হবে</span>, <span lang=\"BN\">আমি চাই</span>\r\n\r\n<span lang=\"BN\">কেউ আমাকে খেতে দিক</span><span lang=\"HI\">।</span><span lang=\"BN\">আমি হাতপাখা নিয়ে</span>\r\n\r\n<span lang=\"BN\">কাউকে আমার পাশে বসে থাকতে বলছি না</span>,\r\n\r\n<span lang=\"BN\">আমি জানি</span>, <span lang=\"BN\">এই ইলেক্ট্রিকের যুগ</span>\r\n\r\n<span lang=\"BN\">নারীকে মুক্তি দিয়েছে স্বামী</span>-<span lang=\"BN\">সেবার দায় থেকে</span><span lang=\"HI\">।</span>\r\n\r\n&nbsp;\r\n\r\n<span lang=\"BN\">আমি চাই কেউ একজন আমাকে জিজ্ঞেস করুক</span>:\r\n\r\n<span lang=\"BN\">আমার জল লাগবে কিনা</span>, <span lang=\"BN\">নুন লাগবে কিনা</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">পাটশাক ভাজার সঙ্গে আরও একটা</span>\r\n\r\n<span lang=\"BN\">তেলে ভাজা শুকনো মরিচ লাগবে কি না</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">এঁটো বাসন গেঞ্জি</span>-<span lang=\"BN\">রুমাল আমি নিজেই ধুতে পারি</span><span lang=\"HI\">।</span>\r\n\r\n&nbsp;\r\n\r\n<span lang=\"BN\">আমি বলছি না ভালোবাসতেই হবে</span>, <span lang=\"BN\">আমি চাই</span>\r\n\r\n<span lang=\"BN\">কেউ একজন ভিতর থেকে আমার ঘরের দরোজা খুলে দিক</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">কেউ আমাকে কিছু খেতে বলুক</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">কাম</span>-<span lang=\"BN\">বাসনার সঙ্গী না হোক</span>, <span lang=\"BN\">কেউ অন্তত আমাকে</span>\r\n\r\n<span lang=\"BN\">জিজ্ঞেস করুক</span>, ‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’\"?\r\n\r\n<strong> <span lang=\"BN\">একজন কবি বা লেখককে কতটা রাজনৈতিক সচেতন হতে হয়</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা ম্যান টু ম্যান ভ্যারি করে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা যে ভূখণ্ডে বাস করছি</span>, <span lang=\"BN\">সেটি সাম্প্রদায়িক দাঙ্গায় ক্ষত</span>-<span lang=\"BN\">বিক্ষত হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৪৭ সালে দেশ বিভক্ত হয়ে ভারত</span>-<span lang=\"BN\">পাকিস্তান হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধু খুব অল্প কথায় ইতিহাসটা বর্ণনা করেছিলেন</span>- ‘<span lang=\"BN\">৫২</span>’ <span lang=\"BN\">সালে ভাষার জন্য রক্ত দিয়েছি</span>, <span lang=\"BN\">৫৪</span>’ <span lang=\"BN\">সালে নির্বাচনে জয় লাভ করেও ক্ষমতার গদিতে বসতে পারি নাই</span>, <span lang=\"BN\">৫৮</span>’ <span lang=\"BN\">সালে আইয়ূব খান সামরিক শাসন জারি করে আমাদের দশ বছরের জন্য গোলাম করে রেখেছেন</span>, <span lang=\"BN\">কী অন্যায় করেছিলাম</span>?’- <span lang=\"BN\">এই যে তিন</span>-<span lang=\"BN\">চারটা ঘটনা উল্লেখ করে তিনি দীর্ঘ দিনের বঞ্চনার ইতিহাস তুলে ধরেছিলেন</span>...<span lang=\"BN\">অল্প কথায় বলতে গেলে আমরা যদি বৃটিশ আমল থেকে ধরি</span>, <span lang=\"BN\">আমরা দুশ বছর বৃটিশদের গোলামী করেছি</span>, <span lang=\"BN\">তারপরে পাকিস্তান হলো</span>, <span lang=\"BN\">ভাষা আন্দোলন শুরু হয়ে গেল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভাষার জন্য গান লেখা</span>, <span lang=\"BN\">রক্ত দেওয়া</span><span lang=\"HI\">।</span> <span lang=\"BN\">৫৪</span>’ <span lang=\"BN\">সালে যুক্তফ্রন্ট জয় লাভ করল</span>, <span lang=\"BN\">তাদেরকে ক্ষমতায় যেতে দেওয়া হলো না</span><span lang=\"HI\">।</span> <span lang=\"BN\">৫৮</span>’ <span lang=\"BN\">সালে সামরিক শাসন চলে আসলো</span>, <span lang=\"BN\">যেটা আমরা ১৯৬৯</span>-<span lang=\"BN\">৭০ পর্যন্ত পেলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রতিকূল শক্তির বিরুদ্ধে বাংলাদেশের মানুষকে যত সংগ্রাম করতে হয়েছে</span>, <span lang=\"BN\">এ রকম সংগ্রাম খুব কম জাতি করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">বাংলাদেশের সঙ্গে তুলনা করার মতো একটি জাতিই আছে</span>, <span lang=\"BN\">তারা হলো ভিয়েতনামের জনগণ</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারাও অনেক অপশক্তির বিরুদ্ধে লড়াই করে গেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদেরও অনেক সংগ্রাম করতে হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">দেশ স্বাধীন হলো</span>, <span lang=\"BN\">কয়েক বছরের মধ্যেই বঙ্গবন্ধুকে হত্যা করা হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">অপশক্তি ক্ষমতায় এসে বাংলাদেশের মুক্তিযুদ্ধের চেতনার অঙ্গিকার থেকে দেশকে সরিয়ে আবার পাকিস্তানি ভাব ধারায় নিয়ে যাওয়ার চেষ্টা করল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই সর্ব সময়টা আমাকে তো বিভিন্ন অপশক্তির বিরুদ্ধেই সংগ্রাম করতে হয়েছে</span>, <span lang=\"BN\">আমি তো সব সময় প্রেমের কবিতা লিখতে পারিনি</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’?- <span lang=\"BN\">এ প্রশ্ন যেই প্রেমিকার কাছে আমি প্রত্যাশা করছি</span>, <span lang=\"BN\">তাকে তো আমি সময় দিতে পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে তো বারবার যুদ্ধ ক্ষেত্রে যেতে হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং বাংলাদেশের কবিতার এই বৈশিষ্ট্য অন্যসব কবিতা থেকে বাংলাদেশের কবিতাকে আলাদা করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এ দেশের কবিতা</span>- <span lang=\"BN\">সংগ্রামী কবিতা</span>, <span lang=\"BN\">সংগ্রামের কবিতা</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার একটা কবিতার কিছু লাইন আছে</span>, ‘<span lang=\"BN\">আর কিছু তো আরাধ্য নয়</span>, <span lang=\"BN\">কাব্য এবং নারী ছাড়া</span>/ <span lang=\"BN\">ওটা পেলে জাহান্নামেও যেতে আমি এক পা খাড়া</span>’<span lang=\"HI\">।</span></strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হুম</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">গুণ তো আমার নামেই আছে জন্মসূত্রে ছায়ার মতন</span>/<span lang=\"BN\">দোষটা আমার কর্মসূত্রে খেটে</span>-<span lang=\"BN\">পাওয়া</span>, <span lang=\"BN\">অর্জিত ধন</span>...’\r\n\r\n<strong> <span lang=\"BN\">আপনার এটা খুব জনপ্রিয় কবিতা</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার অসংখ্য ভক্ত আছে এ কবিতার</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু এ কবিতার যারা ভক্ত</span>- <span lang=\"BN\">স্বাধীনতা পদক পাওয়ার জন্য যে স্ট্যাটাসটি আপনি দিয়েছিলেন</span>, <span lang=\"BN\">সেটা বোধহয় তাদের জন্য বেশ দুঃখজনক ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার কাছে তো নিশ্চয়ই এর ব্যাখ্যা আছে</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">অনেক মানুষ একসঙ্গে ভুল করতে পারে</span>, <span lang=\"BN\">কিংবা অনেক মানুষ একসঙ্গে একটা ভুল সিদ্ধান্ত নিতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্কটল্যান্ড একবার ইংল্যান্ড থেকে বের হয়ে যাওয়ার সিদ্ধান্ত নিয়েছিল</span>, <span lang=\"BN\">পরবর্তীকালে দেশের মানুষ আবার সিদ্ধান্ত পরিবর্তনও করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বলছি যে অনেকগুলো মানুষও কিন্তু ভুল সিদ্ধান্ত নিতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেমন আমার ক্ষেত্রে যেটা হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি দেখেছি যে ঐ ঘটনার বিপুল প্রতিক্রিয়া হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">অধিকাংশ মানুষ আমার এভাবে পুরস্কার চাওয়াটা গ্রহণ করতে পারেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবির সম্পর্কে যে ধারণা তাদের মনের মধ্যে আছে</span>, <span lang=\"BN\">সেটা তো আমি না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা তো আমার মতো কবি দেখেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মতো কবি কীভাবে রিয়্যাক্ট করে</span>, <span lang=\"BN\">কীভাবে চায়</span>- <span lang=\"BN\">আমার চাওয়ার পদ্ধতির সঙ্গে তারা পরিচিত না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার অনেক কিছুই তারা নিতে পারবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই যে তুমি একটু আগে বললে যে</span>- <span lang=\"BN\">আপনার প্রথম বইটা আপনি একজন গণিকাকে দিয়েছিলেন</span>, <span lang=\"BN\">এটা পৃথিবীর খুব কম কবি</span>-<span lang=\"BN\">ই স্বীকার করবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার কন্যা একবার আমাকে প্রশ্ন করেছে</span>- <span lang=\"BN\">বাবা তুমি কী গণিকালয়ে যেতে</span>? <span lang=\"BN\">আমি বলেছি হ্যাঁ</span>, <span lang=\"BN\">যেতাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মেয়ে আমাকে বলেছে</span>- <span lang=\"BN\">থ্যাংক ইউ</span>, <span lang=\"BN\">তুমি সত্য কথা বলেছো তাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মতো কবি তারা দেখেনি বলে আমার আচরণে কষ্ট পেয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মতো আর কেউ চাইতে পারবে</span>? <span lang=\"BN\">স্বাধীনতা পুরস্কার চাওয়ার মতো শক্তি কি তাদের আছে</span>, <span lang=\"BN\">সাহস আছে তাদের</span>? <span lang=\"BN\">যাদের নেই</span>, <span lang=\"BN\">তারা ভাবছে</span>- <span lang=\"BN\">এই কি</span>! <span lang=\"BN\">এই কবি পুরস্কার চায় কেন</span>! <span lang=\"BN\">আবার প্রধানমন্ত্রী সে দাবি মেনেও নেন</span>! <span lang=\"BN\">প্রধানমন্ত্রীর ক্যাবিনেটের মিনিস্টাররা পর্যন্ত বিব্রত হয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা আমাকে মনোনীত করেননি বলে শেখ হাসিনা তাদেরকে ভৎসনা করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কেন এত দিন আমার নাম তারা মনোনয়ন দেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি বলেছেন</span>- <span lang=\"BN\">নির্মলেন্দু গুণ যদি স্বাধীনতা পুরস্কার না পায়</span>, <span lang=\"BN\">কারা পাচ্ছে তবে এ পুরস্কার</span>? <span lang=\"BN\">আই ডিজার্ভ ইট সো প্রাউডলি</span>, <span lang=\"BN\">সো স্ট্রংলি</span>- <span lang=\"BN\">ফলে আমার কাছে এটা না পাওয়াটাই অসম্মানজনক ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">পাওয়াটা যে সম্মানজনক হয়েছে তা নয়</span>, <span lang=\"BN\">কিন্তু না পাওয়াটা খুব অসম্মানজনক ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে জন্য আমি এই বিরক্তিকর জিনিসটির অবসান হোক চেয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি ভেবেছি</span>, <span lang=\"BN\">আমি যদি চাই</span>- <span lang=\"BN\">তবে তাদের পুরস্কারের মূল্যটা বৃদ্ধি পাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি চাইলে এ পুরস্কার যে খুব কাঙ্ক্ষিত এবং প্রত্যাশিত</span>- <span lang=\"BN\">এটা লোকে জানবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তো পুরস্কারের মূল্য বৃদ্ধি হলে আমার তো কোনো ক্ষতি নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরবর্তীতে যারা পাবে</span>, <span lang=\"BN\">তারা একটা উর্ধ্ব মূল্যের পুরস্কার পাবে</span>, <span lang=\"BN\">যে পুরস্কার নির্মলেন্দু গুণ প্রকাশ্যে চেয়ে নিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রকাশ্যে না চেয়ে আমি যদি গোপনে শেখ হাসিনার কাছে পুরস্কার চাইতাম</span>, <span lang=\"BN\">সেটা কি ভালো হতো</span>? <span lang=\"BN\">অনেকে তো গোপনে চাইছে</span>! <span lang=\"BN\">আমি যে গোপনীয়তায় বিশ্বাস করি না</span>- <span lang=\"BN\">তারা সেটা বিবেচনা করেনি</span>, <span lang=\"BN\">তারা ভেবেছে</span>- <span lang=\"BN\">আমার এভাবে চাওয়াটা ঠিক হয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা না চাইলে এখনো আমার ভেতরে পুঞ্জিভূত ক্ষোভ কাজ করতো হয়তো</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে আমি স্বাধীনতা শব্দটি কীভাবে আমাদের হলো সেই কবিতা লিখিছি</span>, <span lang=\"BN\">যে আমি বঙ্গবন্ধুকে নিয়ে প্রথম কবিতা লিখেছি</span>, <span lang=\"BN\">যে আমি বঙ্গবন্ধুর মৃত্যুর পর কবিতা লিখলাম</span>- <span lang=\"BN\">আমি ছাড়া এ পুরস্কার দাবি করার দ্বিতীয় ব্যক্তি আমি দেখি না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে যারা ছোট দেখতে চায়</span>, <span lang=\"BN\">বঞ্চিত দেখতে চায়</span>, <span lang=\"BN\">তারাই আমাকে পুরস্কারবিহীনও দেখতে চায়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি চেয়েছি তাই আমাকে দিয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন আরেকজন চেয়ে নিক</span>, <span lang=\"BN\">আমি তো পথ সহজ করে দিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা এখন প্রমাণ করুক</span>- <span lang=\"BN\">চাইলেই দেয় কি</span>-<span lang=\"BN\">না</span>!\r\n\r\n<strong><span lang=\"BN\">আপনার অনেক ঘনিষ্ঠ একজন বন্ধু ছিলেন আমাদের আরেকজন জনপ্রিয় কবি আবুল হাসান</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার সঙ্গে আপনার স্মৃতিগুলো যদি শোনাতেন</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> ‘<span lang=\"BN\">আমার কণ্ঠস্বর</span>’ <span lang=\"BN\">বইটিতে আমাদের যৌথ জীবন নিয়ে আমি লিখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা দীর্ঘ দিন একসঙ্গে থেকেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেও কিন্তু কবিতার জন্য আমার সঙ্গে বিশ্ববিদ্যালয় ত্যাগ করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা একসঙ্গে ঢাকা বিশ্ববিদ্যালয় ছেড়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">লেখাপড়া করে ডিগ্রী অর্জন করে</span>, <span lang=\"BN\">চাকরি করে</span>, <span lang=\"BN\">সরকারি আমলা হয়ে নিজেদের প্রতিষ্ঠিত করে আমরা আমাদের প্রতিভার অপচয় করতে চাইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আবুল হাসান সম্পর্কে আমার আপত্তির একটা জায়গা আছে</span>- <span lang=\"BN\">যখন ২৫ মার্চের জেনোসাইড ঢাকাতে হলো</span>, <span lang=\"BN\">তারপরে এ শহরকে যারা বাসযোগ্য বলে মনে করেছিল</span>- <span lang=\"BN\">হি ওয়াজ এমং দেম</span><span lang=\"HI\">।</span> <span lang=\"BN\">অথচ তার বয়স ছিল মুক্তিযুদ্ধে অংশগ্রহণ করার মতো</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি মুক্তিযুদ্ধে অংশগ্রহণ করেননি</span><span lang=\"HI\">।</span>\r\n\r\n<strong><span lang=\"BN\">আমাদের অনেক কবি</span>-<span lang=\"BN\">ই কিন্তু মুক্তিযুদ্ধে অংশগ্রহণ করেননি বা করতে পারেননি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">যারা করেননি</span>, <span lang=\"BN\">যাদের বয়স ছিল</span>, <span lang=\"BN\">যারা বলে</span>- ‘<span lang=\"BN\">এখন যৌবন যার</span>/<span lang=\"BN\">যুদ্ধে যাবার তার শ্রেষ্ঠ সময়</span>’ <span lang=\"BN\">তারা মুক্তিযুদ্ধের সময় কোথায় ছিল</span>? <span lang=\"BN\">শ্রেষ্ঠ সময়টা তারা কোথায় কাটিয়েছিল</span>? <span lang=\"BN\">জাতি তো জানতে চায় সেটা</span>, <span lang=\"BN\">তাই না</span>? <span lang=\"BN\">সুতরাং তাদের সাথে বন্ধুত্ব ছিল</span>, <span lang=\"BN\">বন্ধুত্ব এখনো আছে কিন্তু এই ক্ষোভটাও আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেমন শামসুর রাহমান যুদ্ধে যাননি কিন্তু তিনি </span>‘<span lang=\"BN\">তোমাকে আসতেই হবে স্বাধীনতা</span>’ <span lang=\"BN\">বা </span>‘<span lang=\"BN\">তোমাকে পাওয়ার জন্য হে স্বাধীনতা</span>’ <span lang=\"BN\">বা </span>‘<span lang=\"BN\">স্বাধীনতা তুমি</span>’- <span lang=\"BN\">এ কবিতাগুলো তো লিখেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি ঢাকাতে বন্দী শিবিরে থেকেও ছদ্মনামে কবিতা লিখে সেগুলো পাঠিয়েছেন ভারতে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেগুলো কলকাতা থেকে প্রকাশিত হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">জাতির দুঃসময়ে যখন দেশকে বাঁচাতে মানুষ যুদ্ধ করছে</span>, <span lang=\"BN\">আমাদের তরুণ কবিদের মধ্যে অনেকেই এখানে থেকে গিয়েছিলেন</span>, <span lang=\"BN\">যুদ্ধের পক্ষ তো তারা নেয়</span>-<span lang=\"BN\">ইনি</span>, <span lang=\"BN\">শামসুর রাহমানের মতো কবিতা লিখে পাঠানোর এই প্রয়াসটাও তো তাদের মধ্যে দেখিনি তখন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কালের ইতিহাসে এ প্রশ্নের মুখোমুখি হবেন তারা</span><span lang=\"HI\">।</span> <span lang=\"BN\">তুমি কোথায় ছিলে</span>, <span lang=\"BN\">কী করছিলে</span>? <span lang=\"BN\">কাদের সমর্থন করেছিলে</span>- <span lang=\"BN\">এ প্রশ্নগুলো উত্থাপন করলেই অনেকের সঙ্গে বন্ধুত্ব আর থাকে না</span><span lang=\"HI\">।</span>', '‘প্রকাশ্যে না চেয়ে যদি গোপনে শেখ হাসিনার কাছে পুরস্কার চাইতাম, সেটা কী ভালো হতো?', '', 'publish', 'open', 'open', '', '%e0%a6%aa%e0%a7%8d%e0%a6%b0%e0%a6%95%e0%a6%be%e0%a6%b6%e0%a7%8d%e0%a6%af%e0%a7%87-%e0%a6%a8%e0%a6%be-%e0%a6%9a%e0%a7%87%e0%a7%9f%e0%a7%87-%e0%a6%af%e0%a6%a6%e0%a6%bf-%e0%a6%97%e0%a7%8b', '', '', '2017-10-24 19:14:56', '2017-10-24 19:14:56', '', 0, 'https://shakti24.com/?p=384', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(387, 2, '2017-10-24 19:14:43', '2017-10-24 19:14:43', '', 'Gun da', '', 'inherit', 'open', 'closed', '', 'gun-da', '', '', '2017-10-24 19:14:43', '2017-10-24 19:14:43', '', 384, 'https://shakti24.com/wp-content/uploads/2017/10/Gun-da.jpg', 0, 'attachment', 'image/jpeg', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(388, 2, '2017-10-24 19:14:56', '2017-10-24 19:14:56', '<span lang=\"BN\">বাংলাদেশের অন্যতম জনপ্রিয় ও আধুনিক কবি নির্মলেন্দু গুণ</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৪৫ সালের ২১ জুন</span>, <span lang=\"BN\">নেত্রকোণায় জন্ম নেন এ খ্যাতিমান কবি</span><span lang=\"HI\">।</span> <span lang=\"BN\">নেত্রকোণার বারহাট্টা উপজেলায় কাটে তার দুরন্ত শৈশব</span><span lang=\"HI\">।</span> <span lang=\"BN\">চার বছর বয়সে মাকে</span> <span lang=\"BN\">হারান তিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">মায়ের মৃত্যুর পর বাবা সুখেন্দু প্রকাশ গুণ আবার বিয়ে করেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঘরে আসে নতুন মা চারুবালা</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">৬৬</span>’ <span lang=\"BN\">এর ছয় দফা আন্দোলনের সঙ্গে আত্মিকভাবে জড়িয়ে পড়েছিলেন এ কবি</span>; <span lang=\"BN\">এরপর বাদ পড়েনি স্বাধীনতার </span><span lang=\"BN\">পূর্বে</span><span lang=\"BN\"> এবং স্বাধীনতা পরবর্তী কোনো বিপর্যয়</span>, <span lang=\"BN\">যতবার এ রাষ্ট্র বিপথগামী হয়েছে</span>- <span lang=\"BN\">কলম ধরেছেন তিনি</span>, <span lang=\"BN\">লিখেছেন একের পর এক শ্রেণী সংগ্রাম এবং স্বৈরাচার</span>-<span lang=\"BN\">বিরোধী কবিতা</span><span lang=\"HI\">।</span> <span lang=\"BN\">বাংলাদেশে বঙ্গবন্ধুকে নিয়ে লেখা প্রথম কবিতার কবি তিনি</span>, <span lang=\"BN\">পঁচাত্তর পরবর্তী সময়ও বঙ্গবন্ধুকে নিয়ে কলম তিনিই ধরেছেন</span><span lang=\"HI\">। ১৯৭০ সালে প্রথম কাব্যগ্রন্থ ‘প্রেমাংশুর রক্ত চাই’ প্রকাশিত হবার পর জনপ্রিয়তা অর্জন করে। এ-গ্রন্থের ঐতিহাসিক প্রেক্ষাপটে লেখা হুলিয়া কবিতাটি ব্যাপক জনপ্রিয়তা অর্জন করে। ১৯৮২ সালে বাংলা একাডেমী </span>পুরস্কার, ২০০১ সালে একুশে পদক এবং ২০১৬ সালে স্বাধীনতা পুরস্কার অর্জন করেন তিনি।\r\n\r\n<span lang=\"BN\">তার কবিতার আরকটি প্রধান আলোচ্য বিষয় নারী ও প্রেম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিভিন্ন কবিতায় নারী</span>-<span lang=\"BN\">পুরুষের প্রেম গভীরভাবে ব্যক্ত করেছেন তিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে তার ভাষ্যমতে</span>- <span lang=\"BN\">আমরা যে ভূখণ্ডে বাস করছি</span>; <span lang=\"BN\">সেখানে বাস করা এত বেশি সংগ্রামের যে প্রেমিকাকে খুব বেশি সময় দিতে পারেননি তিনি</span>, <span lang=\"BN\">আর তাই হয়তো প্রেমিকাও তাকে বলার সুযোগ পাননি</span>- ‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’? <span lang=\"BN\">বোহেমিয়ান</span><span lang=\"BN\"> জীবন যাপনে অভ্যস্ত নির্মলেন্দু গুণ নিজের ইচ্ছাকে প্রাধান্য দিয়েই প্রকৃত কবির জীবন যাপন করে গিয়েছেন</span>; <span lang=\"BN\">এর জন্য আশ্রয় নেননি কোনো ছল</span>-<span lang=\"BN\">চাতুরীর</span>, <span lang=\"BN\">আর তাই স্বাধীনতা পুরস্কারটি এ রাষ্ট্রের কাছে তাকে চেয়ে নিতে হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্বাধীনতা পুরস্কার চেয়ে নেওয়ার মতো সাহসীকতা এ রাষ্ট্রে বোধহয় তাকেই মানায়</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">সম্প্রতি রাজধানীর কাঁটাবনের কনকর্ড এম্পোরিয়ামের নিচ তলায় কবির নিজস্ব বইয়ের দোকান </span>‘<span lang=\"BN\">কাঁশবন</span>’<span lang=\"BN\">এ বসে তিনি কথা বলেন প্রিয়</span>.<span lang=\"BN\">কম এর সঙ্গে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবির সাথে কথোপকথনের সেই মুহূর্তগুলো প্রিয়</span>.<span lang=\"BN\">কম এর পাঠকদের জন্য প্রকাশ করা হলো</span>-\r\n\r\n<strong> <span lang=\"BN\">দাদা</span>, <span lang=\"BN\">প্রথমে এই দোকান সম্পর্কে জানতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">সম্প্রতি এটি আপনি দিয়েছেন</span>, <span lang=\"BN\">দেখতে পাচ্ছি এখানে সবই আপনার বই</span>…</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">এ দোকানটি এ মাস থেকেই আমি নিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আসলে </span>‘<span lang=\"BN\">কাঁশবন</span>’ <span lang=\"BN\">নামের একটি ছোট পত্রিকার অফিস ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ওরা আমার উপর একটা বিশেষ সংখ্যা করেছিল প্রায় ৩০০</span>-<span lang=\"BN\">৪০০ পৃষ্ঠার</span>, <span lang=\"BN\">তারপর আমাদের সদ্য প্রয়াত কবি রফিক আজাদের উপরও খুব বড় একটা সংখ্যা করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই দুটি সংখ্যা করার কারণে কাঁশবন খুব গুরুত্বপূর্ণ একটি ভূমিকায় চলে গিয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু এ দোকানটা ওরা তেমন ব্যবহার করছিল না</span>, <span lang=\"BN\">ফলে আমি ওদের বললাম</span>- <span lang=\"BN\">যেহেতু তোমরা দোকানটা তেমন ব্যবহার করছো না</span>, <span lang=\"BN\">আমাকে দিয়ে দাও</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার তো একশর উপরে বই আছে</span>, <span lang=\"BN\">অনেক পাঠক সন্ধান করে বইগুলো খুঁজে পায় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিভিন্ন সময় অনেকে আমার কাছে ফোন করে জানতে চায় কোথায় পাওয়া যাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তখন আজিজ মার্কেট</span>, <span lang=\"BN\">নিউ মার্কেট এগুলোর কথা বলি</span>, <span lang=\"BN\">তবুও অনেক জায়গায় বই পাওয়া যায় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমার মাথায় চিন্তা এলো আমি যদি একটি বইয়ের দোকান দিই</span>, <span lang=\"BN\">যেখানে আমার সব বই এক সাথে পাওয়া যাবে একটা জায়গাতেই</span>, <span lang=\"BN\">তাহলে আমার একটা ব্যবসায়িক লাভও হলো</span>, <span lang=\"BN\">আবার সেখানে আমি দুইটা ছেলেকে প্রোভাইডও করতে পারব</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার অবর্তমানে আমার বইগুলোর তদারকি তারা করতে পারবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা স্ট্যাটাসের</span> (<span lang=\"BN\">সামাজিক যোগাযোগ মাধ্যম</span>) <span lang=\"BN\">মাধ্যমে এই দোকানের কথা প্রচার করে দিতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আজকেই একটা স্ট্যাটাস দিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সপ্তাহে তিন দিন আমি এখানে বসব</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে সন্ধান করার জন্য এখন আর কষ্ট করে কামরাঙ্গীর চর যেতে হবে না</span>, <span lang=\"BN\">কাঁটাবনের কনকর্ড এম্পোরিয়ামের কাঁশবনেই রাত আটটার পর আমাকে পাওয়া যাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">পাঠকরা ইচ্ছা করলে আমার অটোগ্রাফ যুক্ত বইও কিনতে পারবেন</span>, <span lang=\"BN\">আমার সাথে সেলফিও তুলতে পারবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমি সহজলভ্য হব এবং আমার বইও আগের চেয়ে সহজলভ্য হবে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার এই প্রতিষ্ঠান থেকে কি বই প্রকাশ করার কোনো ইচ্ছা আছে</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">না</span>, <span lang=\"BN\">বই প্রকাশের চিন্তা নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার তো একশজন প্রকাশক আছে</span>, <span lang=\"BN\">সুতরাং আমি আর নিজে প্রকাশনা শিল্পের মধ্যে নাক গলাতে চাই না</span><span lang=\"HI\">।</span> <span lang=\"BN\">একবার এক প্রকাশক আমার একটা বই প্রকাশ করা নিয়ে বিলম্ব করছিল</span>, <span lang=\"BN\">তখন আমি ঐ বইটা নিজেই প্রকাশ করে ফেলেছিলাম</span>, <span lang=\"BN\">সেটার নাম দিয়েছিলাম </span>‘<span lang=\"BN\">অগত্যা প্রকাশনী</span>’! <span lang=\"BN\">পরে অবশ্য সেটা বিক্রেতাদের দিয়ে দিয়েছি</span>, <span lang=\"BN\">তুমি বলাতে বই প্রকাশনার সেই ঘটনা মনে পড়ল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">তাহলে দাদা</span>; <span lang=\"BN\">এখন একটু আগে থেকে জানতে চাইব</span>, <span lang=\"BN\">মানে আপনার ছেলেবেলা</span>, <span lang=\"BN\">নেত্রকোণার জীবন আরকি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">নেত্রকোণার আগে তো আর যেতেও পারবা না</span>! <span lang=\"BN\">তুমি ইচ্ছা করলেও এর আগে যেতে পারতেছ না</span>!\r\n\r\n<strong> <span lang=\"BN\">সেটাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার ছেলেবেলা কেমন কেটেছে</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এই শোনো</span>, <span lang=\"BN\">তার আগে তোমারে একটা গল্প বলি</span>…\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আগে যখন বাংলা একাডেমীতে বইমেলা হতো</span>, <span lang=\"BN\">আমরা কয়েকজন স্টলের মধ্যে বসতাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছু ছেলেপেলে আসতো</span>; <span lang=\"BN\">হয়তো স্কুলে পড়ে</span>, <span lang=\"BN\">নাম শুনেছে বা টেলিভিশনে আমার চেহারাটা দেখেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাদের হয়তো আমার আর রবীন্দ্রনাথের বয়সের পার্থক্য সম্পর্কে স্পষ্ট ধারণা নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">নজরুল বড় না</span>, <span lang=\"BN\">মাইকেল মধুসূদন বড় সেটাও হয়তো তারা স্পষ্ট জানে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে একবার এদের মধ্যে কয়েকজন জিজ্ঞেস করছে</span>- <span lang=\"BN\">আচ্ছা</span>, <span lang=\"BN\">আপনি কি রবীন্দ্রনাথকে দেখেছেন</span>? <span lang=\"BN\">আমি বললাম</span>, <span lang=\"BN\">না</span>! <span lang=\"BN\">রবীন্দ্রনাথকে আমি দেখি নাই</span>, <span lang=\"BN\">আমার সে সৌভাগ্য হয় নাই কারণ রবীন্দ্রনাথ আমার জন্মের আগে মারা গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি আবার ওদের জিজ্ঞেস করলাম রবীন্দ্রনাথ কত সালে মারা গেছেন তোমরা সেই সম্পর্কে কিছু জানো</span>? <span lang=\"BN\">বললো যে</span>, <span lang=\"BN\">না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আমাদের ছেলেমেয়েদের জ্ঞানের স্বল্পতা</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছুক্ষণ পর ঘুরে এসে আবার জিজ্ঞেস করলো</span>- <span lang=\"BN\">আপনি কি মাইকেল মধুসূদনকে দেখেছেন</span>? <span lang=\"BN\">আমি বললাম মাইকেল মধুসূদন তো রবীন্দ্রনাথেরও আগের কবি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তোমরা শুধু নামগুলোই জানো</span>, <span lang=\"BN\">তাদের জন্ম তারিখ সম্পর্কে তোমাদের কোনো ধারণা নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে আবার বলে</span>, <span lang=\"BN\">আপনি কি নজরুলকে দেখেছেন</span>? <span lang=\"BN\">মানে বুঝছো তো</span>? <span lang=\"BN\">তারা জানতে চাইছে</span>, <span lang=\"BN\">আমি তো কবি</span>, <span lang=\"BN\">কিন্তু কোনো বড় কবির সঙ্গে আমার সাক্ষাৎ হয়েছে কি না</span>! <span lang=\"BN\">আমি তখন বললাম</span>- <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">নজরুলকে দেখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">নজরুল পিজি হাসপাতালে যখন চিকিৎসাধীন ছিলেন</span>, <span lang=\"BN\">তখন আমি উনার সঙ্গে দেখা করেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি ওদের পরামর্শ দিলাম</span>, <span lang=\"BN\">তোমরা একটা কাজ করবে</span>- <span lang=\"BN\">যেই লেখকের সম্পর্কে তোমাদের আগ্রহ হবে</span>, <span lang=\"BN\">তার জন্ম সাল</span>-<span lang=\"BN\">তারিখটা জেনে রাখবে</span>, <span lang=\"BN\">তাতে তোমরা ধারণা পাবে কে বয়সে কার চেয়ে বড়</span><span lang=\"HI\">।</span> <span lang=\"BN\">ওরা আমাকে জিজ্ঞেস করেছে রবীন্দ্রনাথের সঙ্গে আমার দেখা হয়েছে কি</span>-<span lang=\"BN\">না</span>; <span lang=\"BN\">তিনি মারা গেছেন ১৯৪১ সালে</span>, <span lang=\"BN\">আমার জন্ম ১৯৪৫ সালে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার মৃত্যু এবং আমার জন্মের মাঝখানে যে চার বছর</span>- <span lang=\"BN\">সে সময় কী হয়েছে সেটা আমার পক্ষে বলা মুশকিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঐ সময়টাতে রবীন্দ্রনাথের সঙ্গে আমার দেখা হয়েছিল কি</span>-<span lang=\"BN\">না সেটা আমার অজানা</span>!\r\n\r\n<strong> <span lang=\"BN\">যেটা বলছিলাম দাদা</span>, <span lang=\"BN\">আপনার ছেলেবেলার কথা</span>...<span lang=\"BN\">আমি জানি যে খুব ছোটবেলায় মাকে হারিয়েছেন আপনি</span><span lang=\"HI\">।</span></strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমার বয়স তখন চার হয়নি বোধহয়</span>, <span lang=\"BN\">চারের কাছাকাছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আম্মা অসুস্থ ছিলেন</span>, <span lang=\"BN\">বাবা তার মাথার পাশে বসে আম্মার মাথায় জল ঢালছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আম্মা করুণ চোখে আমার দিকে তাকিয়ে ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তখন একটা প্লাস্টিকের ফুটবল নিয়ে বাইরে খেলতে যাচ্ছিলাম</span>, <span lang=\"BN\">ওই সময় বাবা ডেকে মায়ের পাশে বসতে বললেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি এই প্রস্তাবে রাজি হইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি মাকে এক ঝলক দেখলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">মাও চেয়েছিলেন হয়তো আমাকে ছুঁয়ে দেখবেন</span>, <span lang=\"BN\">তার মৃত্যু ঘনিয়ে আসছে এই বোধটা হয়তো তিনি টের পাচ্ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন তো আর এগুলো বুঝতে পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন ভাবতে কষ্ট হয়</span>, <span lang=\"BN\">আমাকে শেষবার স্পর্শ করার যে ইচ্ছা তার মধ্যে জাগ্রত হয়েছিল সেটা আমি হতে দিইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">হাতের বলটি নিয়ে আমি দৌড়ে ঘর থেকে বের হয়ে গেলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মনে হচ্ছিল</span>- <span lang=\"BN\">ফুটবল খেলার সময় চলে যাচ্ছে</span>, <span lang=\"BN\">কিন্তু মা তো আর চলে যাবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই চলে যাওয়াটার সঙ্গে তো আমি পরিচিত ছিলাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">মৃত্যুর সঙ্গে ঐ প্রথম আমার পরিচয় হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ধারণার মধ্যেই ছিল না মা মরে যেতে পারেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই বোধই ছিল না আমার ভেতরে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারপরে আমার আর কিছু মনে নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি অনুমান করি যে</span>- <span lang=\"BN\">ঐ দিনই কিছুক্ষণ পরে মা মারা গিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার জ্যেঠি মা</span>, <span lang=\"BN\">কাকী মা</span>, <span lang=\"BN\">আমার বড় বোনেরা</span>, <span lang=\"BN\">আত্মীয়স্বজনরা আমাকে খেলার মধ্যে থেকে নিয়ে এসেছিলেন</span>, <span lang=\"BN\">আমি কিছুতেই আসতে চাচ্ছিলাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা জোর করে নিয়ে আসছিলেন</span>, <span lang=\"BN\">আমি কি কান্না করব</span>! <span lang=\"BN\">আমার নির্বিকার মূর্তি দেখে তারাই কান্নাকাটি করছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে তারা বুঝাতে চেষ্টা করছিলেন যে আমার মা মারা গেছেন</span>, <span lang=\"BN\">কিন্তু এই মারা যাওয়া জিনিসটাই তো আমার বোধের মধ্যে তখন ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু এখন যখন আমি আমার অতীতকে রোমন্থন করি তখন মায়ের ঐ শুয়ে থাকার দৃশ্যটিই আমার কাছে তাকে নিয়ে একমাত্র স্মৃতি বলে মনে হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এর আগে তাকে কখনো দেখেছি বলে আমার মনে পড়ে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাকে কখনো ঘরে বসে থাকতে বা ঘরে হাঁটাচলা বা পায়চারী করতে</span>, <span lang=\"BN\">কোথাও দাঁড়ানো অবস্থায় দেখেছি বলে আমার স্মৃতির মধ্যে নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">মনে থাকার মধ্যে মা শুয়ে আছেন</span>, <span lang=\"BN\">বাবা তার মাথায় জল ঢেলে দিচ্ছেন</span>- <span lang=\"BN\">এই দৃশ্যটুকুই শুধু মনে আছে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার আত্মজীবনীমূলক বই </span>‘<span lang=\"BN\">আমার ছেলেবেলা</span>’<span lang=\"BN\">তেও বোধহয় মায়ের মৃত্যুর সেই দৃশ্য দিয়ে শুরু করেছিলেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">আমার ছেলেবেলা</span>’ <span lang=\"BN\">বইটা শুরুই হয়েছে মায়ের মৃত্যুর দৃশ্য দিয়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ম্যাক্সিম গোর্কির ছেলেবেলা</span>, <span lang=\"BN\">রবীন্দ্রনাথের ছেলেবেলা</span>, <span lang=\"BN\">বুদ্ধদেব বসুর ছেলেবেলা</span>- <span lang=\"BN\">এই সবগুলো বই</span>-<span lang=\"BN\">ই আমি পড়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এর মধ্যে আমার সব থেকে ভালো লেগেছিল ম্যাক্সিম গোর্কির ছেলেবেলা</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার জীবনের মধ্যে এক বিরাট সংগ্রাম ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">জন্মের পরেই তার পিতা মারা যান</span>, <span lang=\"BN\">তাকে তার দিদি মা মানুষ করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">মামার বাড়িতে তিনি বড় হয়েছিলেন</span>, <span lang=\"BN\">কিন্তু তার লেখার ভেতরে দিদি মা</span>’<span lang=\"BN\">র চরিত্রটি খুব সুন্দরভাবে এসেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">লেখার মধ্যে তার জীবনের যে সংগ্রাম পেয়েছিলাম</span>, <span lang=\"BN\">ঐটা আমাকে খুব আকর্ষিত করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার আত্মজীবনী যখন আমি লেখা শুরু করি</span>, <span lang=\"BN\">আমার বোধহয় তখন ৪২ বছর বয়স</span><span lang=\"HI\">।</span> <span lang=\"BN\">৪২ বছর বয়সে আমি ছেলেবেলার বইটি লিখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথ কিন্তু তার ছেলেবেলার বইটি লিখেছিলেন সত্তর বছর বয়সে</span>, <span lang=\"BN\">বুদ্ধদেব বসু সম্ভবত ষাট বছর বয়সে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি চিন্তা করে দেখলাম</span>-<span lang=\"BN\">ছেলেবেলার গল্প লেখার জন্য এত দীর্ঘ দিন অপেক্ষা করার কিছু নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথের আগে বোধহয় কেউ ছেলেবেলা লিখেননি</span>, <span lang=\"BN\">এই ধারণাটাই হয়তো ছিল না তখন</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথ আমাদের অনেক নতুন ধারণার সাথে পরিচয় করিয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার দাদা মহর্ষি দেবেন্দ্রনাথ ঠাকুরও কিন্তু তার আত্মজীবনী রচনা করে গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং আত্মজীবনী রচনার ধারণা রবীন্দ্রনাথ পেয়েছিলেন দাদার কাছ থেকে</span>, <span lang=\"BN\">কিন্তু ছেলেবেলাকে পৃথক করে লেখার ধারণাটা বাংলা সাহিত্যে আগে ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে রবীন্দ্রনাথের আগে কারো ছেলেবেলার বই আমরা পাই না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এগুলো পড়ার পর তাই আমি চিন্তা করে দেখলাম</span>, <span lang=\"BN\">ষাট</span>-<span lang=\"BN\">সত্তর বছর পর্যন্ত অপেক্ষা করার কোনো প্রয়োজন নেই</span>, <span lang=\"BN\">স্মৃতি সতেজ থাকতে থাকতেই ছেলেবেলার ঘটনাগুলো আমার লেখা দরকার</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু আমি মনে করেছি যে লিখব</span>; <span lang=\"BN\">এবং আমার লেখা সম্পর্কে পাঠকদের কৌতূহল সৃষ্টি হবে</span>, <span lang=\"BN\">সুতরাং তা আগেই লিখে ফেলা ভালো</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই বই লেখার সময় শুরুটা কোন জায়গা থেকে করব এটা ভাবতে গিয়ে ম্যাক্সিম গোর্কির ছেলেবেলার লেখাটা মনে পড়ল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার বইটি একটি করুণ দৃশ্য দিয়ে শুরু হয়েছিল</span>- <span lang=\"BN\">যা তার জীবনকে নানাভাবে প্রভাবিত করেছে</span>, <span lang=\"BN\">এ ঘটনা তার জীবনের অনেক কষ্টের কারণও হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি সেই কষ্ট জয় করে কীভাবে বড় লেখকে পরিণত হলেন</span>, <span lang=\"BN\">সেই অভিজ্ঞতাটা যেহেতু তার বই থেকে আমি পেয়েছি</span>, <span lang=\"BN\">ফলে আমি </span>‘<span lang=\"BN\">আমার ছেলেবেলা</span>’ <span lang=\"BN\">বইটি মায়ের মৃত্যুর দৃশ্য দিয়েই শুরু করেছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">মায়ের মৃত্যুর পর আপনার অনুভূতি কী রকম ছিল</span>? <span lang=\"BN\">বুঝতে নিশ্চয় সময় লেগেছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমি তো বিশ্বাসই করতে পারিনি আমার মা মারা গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">মাকে দেখি না দেখি না</span>... <span lang=\"BN\">এত ছোটবেলার কথা মানুষের মনে থাকারও কথা না</span>, <span lang=\"BN\">আমার মনে হয় পাঁচ</span>-<span lang=\"BN\">ছয় বছর বয়সের আগের কথা মানুষের মনে থাকে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">সম্ভবত এটা একটি বিশেষ ঘটনা বলেই আমার চোখে দৃশ্যটা রয়ে গেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমানের একটা কবিতা আছে</span>- ‘<span lang=\"BN\">কখনো আমার মাকে</span>’, <span lang=\"BN\">যদিও শামসুর রাহমানের মা খুব দীর্ঘজীবী হয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমান যখন প্রায় পঞ্চাশ বছরের</span>, <span lang=\"BN\">তখন মায়ের সাথে তার একটা ছবি আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">মা তাকে আদর করছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবুও তিনি তার মাকে নিয়ে একটি কবিতা লিখেছিলেন</span>, <span lang=\"BN\">সে কবিতার শুরুতেই আছে</span>- ‘<span lang=\"BN\">কখনো আমার মাকে গান গাইতে শুনিনি</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">আমার কাছে মনে হয়</span>, <span lang=\"BN\">তার এই কবিতাটি আমার মায়ের বেলায় সত্য</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের কিছু পারিবারিক ছবি আছে</span>; <span lang=\"BN\">সেই ছবিগুলোতে দেখা যায় আমি মায়ের সামনে বসে আছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আবার আমার ছোট বোনকে নিয়ে মা আদর করছেন</span>, <span lang=\"BN\">আমি দাঁড়িয়ে দাঁড়িয়ে তা দেখছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">মা</span>’<span lang=\"BN\">র একার ছবিও অনেক দেখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমাদের বাড়িতে ছবি তোলার একটা রেওয়াজ ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আগের দিনের মানুষের তো একটা সমস্যা ছিল যে তারা জন্মবিরতিকরণ পদ্ধতির সাথে পরিচিত ছিল না</span>, <span lang=\"BN\">ফলে আমরা দেখি রবীন্দ্রনাথের মা ১৩</span>-<span lang=\"BN\">১৪টা সন্তানের জন্ম দিয়েছেন</span>, <span lang=\"BN\">আমার মাও খুব অল্প দিনে অল্প বয়সের ব্যবধানে অনেকগুলো সন্তানের জন্ম দিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার একটা বড় ভাই ছিলেন কালীদাস নামে</span>, <span lang=\"BN\">খুব অল্প বয়সেই তিনি মারা যান</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">কালীদাস</span>’ <span lang=\"BN\">নামটি আমার বাবার রাখা</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি মনে করি কালীদাসের নামে নাম রাখার কারণ</span>- <span lang=\"BN\">আমার বাবা হয়তো চেয়েছিলেন তার কোনো এক সন্তান কবি হোক</span>! <span lang=\"BN\">নাহলে কালীদাসে নামে নাম রাখবেন কেন</span>?\r\n\r\n<strong> <span lang=\"BN\">আপনার বাবা তো আর্ট</span>-<span lang=\"BN\">কালচারের সাথে জড়িত ছিলেন বলে শুনেছি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">তিনি কলকাতা আর্ট স্কুলের ছাত্র ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরিবারের ইচ্ছার বিরুদ্ধেই তিনি সেখানে ভর্তি হয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাকে নিয়মিত টাকা পাঠাতে হতো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার জ্যাঠা</span>, <span lang=\"BN\">কাকা বা আমার ঠাকুরমা এটার পক্ষপাতী ছিলেন না</span>, <span lang=\"BN\">ফলে ঠাকুরদাদা মারা যাওয়ার পর বাবাকে টাকা পাঠানো বন্ধ করে দেওয়া হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">বাবাকেও আর্ট স্কুলে পড়াশোনা শেষ না করেই ফেরত আসতে হয়েছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনি তো ছবিও আঁকেন</span>, <span lang=\"BN\">ছবি আঁকার ইচ্ছাটা কীভাবে হলো</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমাদের একটা স্বচ্ছল পরিবার ছিল</span>, <span lang=\"BN\">যেখানে ছবি আঁকা বা তোলার একটা রেওয়াজ চালু ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ঠাকুরদাদা যখন বেঁচে ছিলেন তখন টেকনোলজি এত উন্নত ছিল না</span>, <span lang=\"BN\">যার ফলে ১৯১৩ সালে তিনি তার একটা পোট্রেট আঁকিয়েছিলেন শারদা রায় নামের একজন শিল্পীকে দিয়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি আমার গ্রামে ঠাকুরদাদার</span> (<span lang=\"BN\">রামসুন্দর পাঠাগার</span>) <span lang=\"BN\">নামে যেই পাঠাগার করেছি</span>, <span lang=\"BN\">সেখানে ঐ তৈলচিত্রটি আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯১৩ সালে করা তার ওই পোট্রেট আমি সারা বাংলায় আর একটাও দেখিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এত সুন্দর পোট্রেট ছিল ঐটা</span><span lang=\"HI\">।</span> <span lang=\"BN\">গ্রামে আমি একটি আর্ট স্কুল করেছি শারদা রায়ের নামে</span> (<span lang=\"BN\">শারদা বসু দেব চিত্রশালা</span>)<span lang=\"HI\">।</span> <span lang=\"BN\">তিনি পরবর্তীতে তার কন্যাকে আমার জ্যাঠা মশাইয়ের সঙ্গে বিবাহ দেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা বাঙালিরা চিত্রকলার চর্চার সময়টাকে রবীন্দ্রনাথের সময় থেকেই ধরতে চাই</span>; <span lang=\"BN\">কিন্তু রবীন্দ্রনাথ ১৯২৬</span>-<span lang=\"BN\">২৭ সাল থেকে ছবি আঁকা শুরু করেছিলেন</span>, <span lang=\"BN\">তার আগে থেকেই কিন্তু আমাদের পটচিত্রে এগুলো পাওয়া যেত</span><span lang=\"HI\">।</span> <span lang=\"BN\">গ্রামের কুমার ছিলেন যারা</span>, <span lang=\"BN\">দেব</span>-<span lang=\"BN\">দেবীর মূর্তি তৈরি করতেন</span>, <span lang=\"BN\">মূর্তি তৈরির আগে তারা কাগজে নকশা তৈরি করে নিতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিক্রমপুরে শম্ভু আচার্য্য ছিলেন তিনি যে পটচিত্র তৈরি করেছেন সেগুলোও আগে থেকেই ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আধুনিক চিত্রকলার চর্চা যেমন তৈলচিত্র</span>- <span lang=\"BN\">১৯১৩ সালে শারদা রায়ের করা আমার ঠাকুরদাদার পোট্রেটটির আগে সারা বাংলাতেই কখনো কোনো শিল্পী করেছেন বলে আমার মনে হয় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি আমার একটা লেখায় উল্লেখ করেছিলাম যে যদি কোনো শিল্পীর আঁকা ১৯১৩ সালের কোনো তৈলচিত্র দেখাতে পারেন তাহলে আমি খুশি হবো</span>, <span lang=\"BN\">আমার এই ধারণাটার পরিবর্তন হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আজ পর্যন্ত কেউ আমাকে দেখাতে পারেননি</span>, <span lang=\"BN\">দাবিও করতেও পারেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঐ তৈলচিত্রটি আমাকে ভীষণভাবে প্রভাবিত করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আর আমার বাবা নিজেও তো ছবি আঁকতেন</span>, <span lang=\"BN\">ফলে তার উত্তরাধিকার সূত্রেও এটা পাওয়া বলতে পারো</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">মায়ের মৃত্যুর পর তো আপনার বাবা দ্বিতীয় বিয়ে করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কেমন ছিল নতুন মায়ের সাথে আপনার অভিজ্ঞতা</span>?</strong>\r\n\r\n<span lang=\"BN\">নির্মলেন্দু গুণ</span>: <span lang=\"BN\">মা যে মারা গেছেন আমি তো বুঝতে পারিনি</span>, <span lang=\"BN\">অন্যরা আমাকে বলত</span>, <span lang=\"BN\">আমাকে নিয়ে দুঃখ প্রকাশ করত</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি কিছুতেই বিশ্বাস করতাম না মা মারা গেছেন</span>, <span lang=\"BN\">আমার ধারণা ছিল মা কোথাও বেড়াতে গেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমি কোনোদিন মায়ের জন্য কান্নাকাটি করেছি এ রকম স্মৃতি আমার মনে নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">অন্যরাও পরবর্তীতে কেউ বলেননি যে মায়ের মৃত্যুতে আমি কষ্ট পেয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">জিনিসটা আমি বুঝতেই তো পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার একটা কবিতা আছে </span>‘<span lang=\"BN\">নিজের ঘরে আগুন</span>’, <span lang=\"BN\">সেই কবিতার মধ্যে এর বর্ণনা আছে</span>, <span lang=\"BN\">আমি যে মাকে মুখাগ্নি করেছি</span>, <span lang=\"BN\">তার মৃতদেহে আগুন দিয়েছি</span>, <span lang=\"BN\">আমি যে এত বড় একটা সর্বনাশ করেছিলাম</span>- <span lang=\"BN\">সেটা আমার স্মৃতিতেই নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমার দ্বিতীয় মা যখন আমাদের সংসারে আসলেন</span>, <span lang=\"BN\">তাকে দেখে আমার মনে হলো</span>- <span lang=\"BN\">আগের মায়ের থেকে এ মা একটু মেদবহুল</span>, <span lang=\"BN\">স্বাস্থ্য ভালো</span>, <span lang=\"BN\">বয়স একটু কম মনে হয়েছে</span>, <span lang=\"BN\">রুগ্ন মনে হয় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মনে হয়েছে আমার মা</span>-<span lang=\"BN\">ই বোধহয় চিকিৎসার জন্য বাইরে কোথাও গিয়েছিলেন</span>, <span lang=\"BN\">সেখান থেকে ফিরে এসেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">উনাকে কখনোই আমার মনে হয়নি যে</span>- <span lang=\"BN\">তিনি আমার মা নন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমার একটা কবিতা আছে</span>, <span lang=\"BN\">এই মা মারা যাওয়ার পরে লিখেছিলাম</span>- <span lang=\"BN\">ভুলেই হয়তো কিন্তু আমি তো তোমার নিজের সন্তানরা জন্মের আগেই তোমাকে মা ডেকেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তো তোমার গর্ভজাত সন্তান না</span>, <span lang=\"BN\">কিন্তু তোমার গর্ভজাত সন্তান এবং আমার ভাই</span>-<span lang=\"BN\">বোনদেরও আগে আমি তোমাকে মা ডেকেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটাই আরকি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার সাথে আমার সম্পর্ক সব সময়ই খুব ভালো ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি কখনোই তাকে বিমাতা হিসেবে দেখিনি</span>, <span lang=\"BN\">তিনিও আমার সাথে কখনোই বিমাতাসুলভ আচরণ করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আমার কখনোই মনে হয়নি যে নিজের মা না থাকার কারণে তিনি আমার সাথে খারাপ ব্যবহার করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">বরং সব সময় বাজার</span>-<span lang=\"BN\">সদাই করা</span>, <span lang=\"BN\">দোকান থেকে এটা</span>-<span lang=\"BN\">সেটা আনতে তাকে আমি সাহায্য করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আমার যে বড় ভাই</span>; <span lang=\"BN\">তিনি তো তখন আমার থেকে অনেক বড়</span>, <span lang=\"BN\">নতুন মায়ের সাথে এডজাস্ট তিনি করতে পারতেন না</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে রাগ করে ভারতে চলে গিয়েছিলেন</span><span lang=\"HI\">।</span>\r\n\r\n<strong><span lang=\"BN\">ছাত্র হিসেবে তো আপনি দুর্দান্ত ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ইন্টারমিডিয়েটে নেত্রকোণা কলেজ থেকে আপনি একাই প্রথম বিভাগ পেয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু ঢাকা বিশ্ববিদ্যালয়ের ফার্মেসী বিভাগে সুযোগ পেয়েও ভর্তি হতে পারেননি</span>… </strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">সুযোগ পেয়েছিলাম</span>, <span lang=\"BN\">কিন্তু ৬৪</span>’ <span lang=\"BN\">সালে কিন্তু একটা বিরাট দাঙ্গা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেই দাঙ্গাটা ব্যাপক আকার ধারণ করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">নারায়ণগঞ্জ</span>, <span lang=\"BN\">সোনারগাঁ</span>, <span lang=\"BN\">মানিকগঞ্জসহ ঢাকার আশেপাশের এলাকায় এ দাঙ্গা ছড়িয়ে পড়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঢাকার ভেতরেও পুরনো ঢাকা</span>, <span lang=\"BN\">লক্ষ্মীবাজার</span>, <span lang=\"BN\">শাঁখারীপট্টিতে ছড়িয়ে পড়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন হিন্দু ছাত্ররা বসবাস করতো জগন্নাথ হলে</span>, <span lang=\"BN\">হল আক্রমণ হতে পারে এ রকম আশঙ্কায় হল কর্তৃপক্ষ একদিন রাতে হল বন্ধ করে দেওয়ার ঘোষণা দিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার এক পিসতুতো ভাই ঢাকা বিশ্ববিদ্যালয়ে ক্যামিস্ট্রিতে পড়ত</span>, <span lang=\"BN\">তিনিও জগন্নাথ হলে থাকতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি ঢাকায় এসে তার রুমেই উঠেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">হল বন্ধ করে দেওয়ার পর আমরা রাতে পালিয়ে তার বাড়ি গফরগাঁও</span>’<span lang=\"BN\">য়ে চলে যাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">রাতে সেখানেই থাকি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু দাঙ্গার ভয়াবহতা ক্রমশ বাড়তে থাকে</span>, <span lang=\"BN\">তখন আমরা স্থির করি যে ঢাকাতে আর ফিরে যাওয়া সম্ভব না</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে আমি নেত্রকোণাতেই ফিরে যাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে যাওয়ার পর আমার বাবা আর কিছুতেই ঢাকাতে ফেরার ব্যাপারে রাজি ছিলেন না</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভারতে চলে যাওয়ার জন্য পারিবারিকভাবে একটা চাপও ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার যে বড় ভাই ভারতে চলে গিয়েছিলেন</span>, <span lang=\"BN\">তিনিও সেখান থেকে চিঠি দিচ্ছিলেন যেন তার কাছে চলে যাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি একটা দোদুল্যমান পরিস্থিতির মধ্যে ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবি হব এটা তো তখনো ভাবিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ছাত্র হিসেবে মোটামুটি ভালোই ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">অন্যদের মধ্যে ক্যারিয়ার নিয়ে যে ভাবনা থাকে</span>, <span lang=\"BN\">আমার মধ্যেও তাই ছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার বাবার তো ইচ্ছা ছিল আপনি ডাক্তার হবেন</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">বাবার ইচ্ছা ছিল কিন্তু আমার সেদিকে আগ্রহ ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেবারই প্রথম ঢাকা বিশ্ববিদ্যালয়ে ফার্মেসী বিভাগ চালু করা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">মাত্র ৩৫ টা আসন সংখ্যা ছিল সেখানে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই পঁয়ত্রিশ জনের মধ্যে লিখিত এবং মৌখিক পরীক্ষা মিলে আমার স্থান ছিল একাদশ</span><span lang=\"HI\">।</span> <span lang=\"BN\">সো আই ডিড ওয়েল</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু ঐটা একটা নতুন সাবজেক্ট</span>, <span lang=\"BN\">ফলে ঐ সাবজেক্টে পড়ার জন্য ইন্টারমিডিয়েটে যে প্রথম স্থান অধিকার করেছিলেন তিনি থেকে শুরু করে সকলেই বেশ উদগ্রীব ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">দাঙ্গার ভয়াবহতা কমলে আমি যখন ঢাকায় ফিরলাম</span>, <span lang=\"BN\">তখন এসে দেখি বিশ্ববিদ্যালয় থেকে আমার নাম কেটে দেওয়া হয়েছে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">নাম কেটে দেওয়ার কারণ কী ছিল</span>? <span lang=\"BN\">সাম্প্রদায়িকতার কারণে</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">অবশ্যই</span><span lang=\"HI\">।</span> <span lang=\"BN\">এছাড়া আর কী কারণ থাকতে পারে</span>? <span lang=\"BN\">এরপর ১৯৬৫ সালে আমি আর কী করব</span>? <span lang=\"BN\">ঢাকা বিশ্ববিদ্যালয়ে তো ভর্তি হতে পারলাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু ঢাকা বিশ্ববিদ্যালয়ে চ্যান্স পেয়েছিলাম</span>, <span lang=\"BN\">ফলে ইঞ্জিনিয়ারিং এ চেষ্টাও করিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন মেডিকেলে পড়ার জন্য সেকেন্ড ডিভিশন ওয়াজ গুড</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের সময় সমগ্র ঢাকা বোর্ড থেকে ১১৯ জন প্রথম বিভাগ পেয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই ১১৯ জনের মধ্যে আমি একজন</span><span lang=\"HI\">।</span> <span lang=\"BN\">মেডিকেলে</span>, <span lang=\"BN\">ঢাকা বিশ্ববিদ্যালয়ের ফিজিক্স</span>-<span lang=\"BN\">কেমিস্ট্রিতে প্রথম বিভাগ পাওয়া কোনো শিক্ষার্থীরাই বাদ যায়নি</span>, <span lang=\"BN\">সবাই চ্যান্স পেয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমিই শুধু ঐ বছর বাদ পড়েছিলাম</span>, <span lang=\"BN\">ভর্তি হতে পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ভবিষ্যত স্বপ্নকে এ ঘটনা খুবই আহত করে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে পড়াশোনার প্রতি আমার আগ্রহ কমতে থাকে</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরের বছর ইঞ্জিনিয়ারিং ইউনিভার্সিটিতে আর্কিটেকচারে ভর্তি হওয়ার জন্য পরীক্ষা দিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এক বছর পড়াশোনা কেন করিনি এটা আমাকে সেখানে ব্যাখ্যা করতে হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">লিখিত পরীক্ষায় পাশ করেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফেল করার কথা আমার মাথায় কখনো আসেইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফেল যে করা যায়</span>, <span lang=\"BN\">এ ধারণা আমার ভেতরে ছিলই না কখনো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি কিন্তু পরে বি</span>.<span lang=\"BN\">এস</span>.<span lang=\"BN\">সিতে দুইবার ফেল করেছিলাম</span>! <span lang=\"BN\">একবার হলো কি</span>, <span lang=\"BN\">মাননীয় রাষ্ট্রপতি আব্দুল হামিদের সঙ্গে দেখা করতে গিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি কলেজ জীবন থেকে আমার ভীষণ ঘনিষ্ঠ ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তো মাননীয় রাষ্ট্রপতি আমাকে জিজ্ঞেস করছিলেন আমি কীভাবে প্রধানমন্ত্রী শেখ হাসিনার সহপাঠী হলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তাকে উত্তর দিলাম</span>- <span lang=\"BN\">ইট ওয়াজ নট বাই মাই চয়েজ</span>, <span lang=\"BN\">ইট ওয়াজ অ্যা নেচারাল ফ্রেন্ডশিপ</span>! <span lang=\"BN\">ঢাকা বিশ্ববিদ্যালয়ের বাংলা বিভাগে কে কে ভর্তি হচ্ছে তা তো আমি জানতাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি ক্লাসে গিয়ে একদিন আবিষ্কার করলাম শেখ মুজিবের কন্যা শেখ হাসিনা আমাদের সহপাঠী</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তখন রাষ্ট্রপতি মহোদয়কে বললাম যে</span>- <span lang=\"BN\">স্যার</span>, <span lang=\"BN\">আমি তো দুইবার বি</span>.<span lang=\"BN\">এস</span>.<span lang=\"BN\">সি ফেল করার পর ১৯৬৭ সালে ঢাকা বিশ্ববিদ্যালয়ে ভর্তি হয়েছিলাম</span>, <span lang=\"BN\">তখন উনি আমাকে বললেন</span>- ‘<span lang=\"BN\">ও</span>! <span lang=\"BN\">আপনি দুইবার ফেল করছিলে</span>...<span lang=\"BN\">ন</span>? <span lang=\"BN\">আমি তিনবার</span>! <span lang=\"BN\">আমরা ফেল করছিলাম বইলাই তো বাংলাদেশ পাশ করল</span>’! <span lang=\"BN\">জীবনের উপর আসলেই সে ঝুঁকি নিতে হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা সে ঝুঁকি নিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধু যখন ছয় দফা দাবি পেশ করলেন</span>, <span lang=\"BN\">তারপরে আর আমাদের মনের ভেতর পড়াশোনা করে বড় হওয়ার আগ্রহটা আমরা হারিয়ে ফেলেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মনে হয়েছিল</span>, <span lang=\"BN\">বঙ্গবন্ধুর ছয় দফা দাবি বাস্তবায়িত হলে আমাকে যে সাম্প্রদায়িকতার কারণে বিশ্ববিদ্যালয়ে ভর্তি হতে দেওয়া হয়নি</span>, <span lang=\"BN\">এ পরিস্থিতি থেকে অন্তত পরবর্তী প্রজন্ম মুক্তি পাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">দেশ স্বাধীন হওয়ার পর তো অন্তত এটুকু নিশ্চিত হয়েছে</span>, <span lang=\"BN\">তাই না</span>? <span lang=\"BN\">এখন বহু হিন্দু ছেলে মেডিকেলে পড়ছে</span>, <span lang=\"BN\">ইঞ্জিনিয়ারিং পড়ছে</span>, <span lang=\"BN\">ভারত থেকে পড়াশোনা করে আবার দেশে ফিরছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আগে একটা কথা ছিল যে হিন্দুরা ডাক্তার</span>-<span lang=\"BN\">ইঞ্জিনিয়ার হয়ে ভারতে চলে যায়</span>, <span lang=\"BN\">সুতরাং স্টপ দেম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাদেরকে ডাক্তার</span>-<span lang=\"BN\">ইঞ্জিনিয়ারিং পড়িয়ে আমাদের রাষ্ট্রের অর্থের অপচয় হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে কিন্তু এ কারণেই বুয়েটে পড়তে দেওয়া হলো না</span><span lang=\"HI\">।</span> <span lang=\"BN\">লিখিত পরীক্ষায় সুযোগ পাওয়ার পর মৌখিকে জিজ্ঞেস করা হলো</span>- <span lang=\"BN\">ভারতে আমার কে আছে</span>? <span lang=\"BN\">আমি বললাম যে আমার বড় ভাই আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে তারা আমাকে আর কোনো প্রশ্নই করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেহেতু আমার বড় ভাই সেখানে আছেন</span>, <span lang=\"BN\">তারা ধরেই নিয়েছিলেন যে আমিও চলে যাব</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রশ্ন যেহেতু করেননি</span>, <span lang=\"BN\">আমি ধরে নিয়েছিলাম লিখিত পরীক্ষায় টিকেছি</span>, <span lang=\"BN\">ফলে ভর্তি হতে পারব</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু ফলাফল বের হওয়ার পর দেখলাম যে আমার নাম নেই</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">ছয় দফা আন্দোলন তো খুব কাছ থেকে দেখেছেন</span>, <span lang=\"BN\">সেই আন্দোলনে উজ্জীবিতও হয়েছেন</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এই যে সাম্প্রদায়িক একটা পরিস্থিতি দেশে বিরাজমান ছিল</span>, <span lang=\"BN\">এর থেকে উত্তরণের জন্য বঙ্গবন্ধুর নেতৃত্বে ছয় দফা আন্দোলনের মাধ্যমে স্বায়ত্তশাসিত বাংলাদেশের স্বপ্ন আমরা দেখেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি যেহেতু কবি ছিলাম</span>, <span lang=\"BN\">তাই হয়তো আমার ভাবনা দূরদর্শিতা সম্পন্ন ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি স্বপ্ন দেখতাম</span>- <span lang=\"BN\">স্বায়ত্তশাসিত বাংলাদেশ হলে</span>, <span lang=\"BN\">এর নিয়ন্ত্রণ বাঙালিদের হাতেই চলে আসবে শেষ পর্যন্ত</span>, <span lang=\"BN\">পাকিস্তানিরা বাংলাদেশের উপর নিয়ন্ত্রণ হারাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে ত্রিশ লক্ষ প্রাণের বিনিময়ে ১৯৭১ সালে আমরা যে স্বাধীনতা পেয়েছি</span>; <span lang=\"BN\">সেই স্বাধীনতা হয়তো এত কষ্টের বিনিময়ে অর্জন করতে হতো না</span>- <span lang=\"BN\">যদি ছয় দফার ভিত্তিতে বঙ্গবন্ধু শাসনতন্ত্র প্রণয়নের অধিকার পেতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি সত্তরের নির্বাচনে নিরঙ্কুশ সংখ্যাগরিষ্ঠতা লাভ করার পরও পাকিস্তানিরা তাকে ক্ষমতা হস্তান্তর করতে রাজি হয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধু পাশ করার পর রেসকোর্স ময়দানে এক বিরাট নৌকা বানিয়ে সেখানে ১৬৬ জন সংসদ সদস্য নিয়ে তিনি একটা মিটিং করেছিলেন</span>, <span lang=\"BN\">সেই মিটিং এ তিনি জনগণের উদ্দেশ্যে বলেছিলেন</span>- ‘<span lang=\"BN\">ছয় দফার সঙ্গে যদি কেউ বেঈমানি করে</span>, <span lang=\"BN\">তবে তাকে এই উদ্যানে জ্যান্ত কবর দেবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শেখ মুজিবুর রহমানও যদি করে</span>, <span lang=\"BN\">তাকেও ছাড়বেন না</span>, <span lang=\"BN\">তাকেও এখানেই কবর দেবেন</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">ছয় দফাই হচ্ছে আমাদের মুক্তির একমাত্র পথ</span>, <span lang=\"BN\">এই ছয় দফার পক্ষেই বাংলার মানুষ রায় দিয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং এর সাথে কোনো আপোষ নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরবর্তীতে কিন্তু ইয়াহিয়া এবং ভুট্টোর সাথে যে আলোচনা হলো</span>, <span lang=\"BN\">সেখানে ছয় দফার ভিত্তিতে শাসনতন্ত্র প্রণয়ন না করার জন্য বঙ্গবন্ধুর উপর তারা চাপ সৃষ্টি করেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">বলা হয়েছিল ছয় দফা থেকে সরে এসে তুমি যদি পাকিস্তানের সাথে সংবিধান প্রণয়ন করো</span>, <span lang=\"BN\">তাহলে প্রধানমন্ত্রী করে তোমার কাছে আমরা ক্ষমতা হস্তান্তর করব</span><span lang=\"HI\">।</span> <span lang=\"BN\">ইনফ্যাক্ট প্রেসিডেন্ট ইয়াহিয়া বঙ্গবন্ধুকে প্রাইম মিনিস্টার হিসেবে সম্বোধনও করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু বঙ্গবন্ধু বলেছিলেন</span>, ‘<span lang=\"BN\">আমি প্রধানমন্ত্রীত্ব চাই না</span>, <span lang=\"BN\">আমি বাংলার মানুষের অধিকার চাই</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">এ আন্দোলনের সঙ্গে আত্মিকভাবে আমি যুক্ত হয়ে পড়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধুর এ স্বপ্নের সঙ্গে আমার স্বপ্ন মিশে গিয়েছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">বঙ্গবন্ধুকে নিয়ে প্রথম কবিতাও তো আপনার লেখা</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span>, <span lang=\"BN\">আমিই ১৯৬৭ সালে তাকে নিয়ে প্রথম কবিতাটা লিখেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার আগে কেউ বঙ্গবন্ধুকে কবিতার বিষয় হিসেবে বিবেচনা করেন নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার পরবর্তীকালে উনি যখন নির্বাচনে জয় লাভ করলেন</span>, <span lang=\"BN\">তখন জসীম উদ্দীন সাহেব তাকে নিয়ে কবিতা লিখেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি তো বঙ্গবন্ধুর খুব ঘনিষ্ঠ বন্ধুও ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সিকান্দার আবু জাফর</span>, <span lang=\"BN\">সুফিয়া কামালসহ আরো অনেকেই পরে তাকে নিয়ে কবিতা লিখেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি লিখেছিলাম সবচেয়ে আগে</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৬৭ সালে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যখন ছয় দফা আন্দোলনের সময় তাকে কারাবন্দি করা হয়েছিল</span>, <span lang=\"BN\">তিনি জেলখানায় ছিলেন এবং আওয়ামীলীগের প্রায় হাজার হাজার কর্মীকে গ্রেফতার করা হয়েছিল</span>, <span lang=\"BN\">তাদের মুক্তির জন্য ১৯৬৬ সালের ৭ই জুন সারাদেশে হরতাল ডাকা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে হরতালে এগারজন প্রাণ দিয়েছিলেন পুলিশের গুলিতে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারাই ছিলেন স্বাধীনতা আন্দোলনের প্রথম শহীদ</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি একটা কবিতা লিখেছিলাম</span> (‘<span lang=\"BN\">প্রচ্ছদের জন্য</span>’), <span lang=\"BN\">সে কবিতা </span>‘<span lang=\"BN\">সংবাদ</span>’ <span lang=\"BN\">পত্রিকায় ছাপা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সব লাইন তো মনে নেই</span>, <span lang=\"BN\">কয়েকটা লাইন এ রকম ছিল</span>- ‘<span lang=\"BN\">শীতের রোগীর মতো জবুথবু নয়</span>/ <span lang=\"BN\">গঞ্জের জনতার মতো নির্ভীক হতে হবে</span>/<span lang=\"BN\">রক্তের রঙ দেখে ভয় নেই</span>/<span lang=\"BN\">স্বাধীন দেশের মুক্ত জনতা উল্লাস করো সবে</span>’<span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">ইন্টারমেডিয়েটে পড়ার সময় থেকে নাকি আপনার জুয়া খেলার অভ্যাস হয়েছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার আত্মজীবনীমূলক বইয়ে এ বিষয়টা আমি অকপটে স্বীকার করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি যে হোস্টেল রুমে জুয়া খেলতাম</span>, <span lang=\"BN\">এটা আমার হোস্টেল সুপার গোপন সূত্রে খবর পেয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">একদিন খেলছিলাম কয়জন বন্ধু মিলে</span>, <span lang=\"BN\">তিনি এসে হঠাৎ করে দরজায় কড়া নাড়লেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা ভেবেছি আমাদের সাথে খেলার জন্য এক বন্ধুর মুসলিম হোস্টেল থেকে আসার কথা</span>, <span lang=\"BN\">সে বোধহয় এসেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমরা খুব সানন্দেই দরজা খুলে দিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের বিছানার উপর তাস ছড়ানো</span>, <span lang=\"BN\">টাকা পয়সাও ছড়ানো ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্যারকে দেখে তো সবাই দৌড়</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি খুব কড়া মেজাজের মানুষ ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে তিনি কলকাতায় চলে গিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শুনেছি সম্প্রতি তিনি প্রয়াত হয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">খুব ভালো শিক্ষক ছিলেন</span>, <span lang=\"BN\">কিন্তু আমাকে খুবই অপছন্দ করতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে বলতেন</span>- ‘<span lang=\"BN\">মোস্ট ডিস্টার্বিং বয় ইন দ্য হোস্টেল</span>’! <span lang=\"BN\">অবশ্য যুক্তিসংগত কারণেই আমাকে অপছন্দ করতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফাস্ট ডিভিশন পাওয়া ছাত্র আমি</span>, <span lang=\"BN\">অথচ ছাত্রদের মধ্যে জুয়া খেলার প্রচলন আমিই শুরু করি</span>- <span lang=\"BN\">সেটা তো তার পক্ষে মেনে নেওয়া কঠিন ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি আমাকে হোস্টেল থেকে বহিষ্কার করে দিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শুধু বহিষ্কার করেই ক্লান্ত হননি</span>, <span lang=\"BN\">আমাকে শাসিয়েও ছিলেন</span>- <span lang=\"BN\">কীভাবে প্র্যাকটিক্যাল পরীক্ষায় পাশ করি</span>, <span lang=\"BN\">তিনি দেখে নেবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে দেখলাম এই কলেজে থাকলে আমার পাশ করা কঠিন হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি ওই কলেজ থেকে টি</span>.<span lang=\"BN\">সি নিয়ে নেত্রকোণায় চলে গেলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরীক্ষার মাস ছয়েক আগে নেত্রকোণা কলেজে গিয়ে ভর্তি হলাম</span>, <span lang=\"BN\">সেখান থেকেই আইএসসি পরীক্ষা দিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই কথা আমার আত্মজীবনীতে লেখার পরে তিনি কীভাবে জানি জানতে পেরেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রায় ত্রিশ বছর পর একবার আমার বাসায় আসলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই ত্রিশ বছরে কিন্তু তার সঙ্গে আমার দেখা</span>-<span lang=\"BN\">ই হয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি এসে আমাকে বললেন</span>- ‘<span lang=\"BN\">তুমি এসব কথা লিখছো কেন</span>’? <span lang=\"BN\">আমি বললাম যে স্যার সত্য কথা লিখছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">উনি পরে বললেন</span>- ‘<span lang=\"BN\">তুমি মনের থেইকা আমার উপর কোনো রাগ পোষণ করো না</span>, <span lang=\"BN\">আমি কষ্ট পেয়েছি যে তুমি আমার অত্যাচারে কবি হইছো এই কথা বলছো</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">আমি বলেছি</span>, <span lang=\"BN\">এটা ভালো তো</span><span lang=\"HI\">।</span> <span lang=\"BN\">কারো না কারো অত্যাচারে যদি অন্য কারো পুনর্জন্ম হয়</span>, <span lang=\"BN\">সেটাতে মন্দ কি</span>? <span lang=\"BN\">আমি তো খারাপ কিছু লিখি নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্যার</span>, <span lang=\"BN\">এ ব্যাপারটা নিয়ে কিছুটা বিব্রত ছিলেন</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">জুয়ার এই অভ্যাসটা কি দীর্ঘ দিন ছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা অনেক বছর ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি যখন পরে ঢাকাতে এলাম</span>, <span lang=\"BN\">এখানে এসে জুয়া খেলা আমাদের নিত্য দিনের ঘটনাই ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিদেশে গেলেও আমি খেলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে শহরে ক্যাসিনো আছে</span>, <span lang=\"BN\">সেখানেই চলে যেতাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছুদিন আগে লাস ভেগাসে যে ম্যাসাকার হলো</span>; <span lang=\"BN\">ঐ যে একজন মানসিক ভারসম্যহীন একটি লোক সাতান্ন না আটান্নজন লোক মেরে ফেলল</span>, <span lang=\"BN\">ওইখানেও কিন্তু আমি জুয়া খেলার জন্য গিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">লাস ভেগাস হচ্ছে জুয়ার স্বর্গ</span><span lang=\"HI\">।</span> <span lang=\"BN\">সারা পৃথিবীর জুয়াড়িরা সেখানে গিয়ে জুয়া খেলে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আটলান্টিক সিটি আছে</span>, <span lang=\"BN\">সেখানেও খেলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমেরিকার যে এখন প্রেসিডেন্ট ডোনাল্ড ট্রাম্প</span>, <span lang=\"BN\">তার প্লাজাতে আমি বেশি খেলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ট্রাম্প যখন প্রেসিডেন্ট হলেন</span>, <span lang=\"BN\">তাকে আমি একটা চিঠি লিখেছি</span>- <span lang=\"BN\">অ্যা লেটার টু দ্য নিউলি ইলেকটেড প্রেসিডেন্ট</span>! <span lang=\"BN\">তার ওয়েবসাইটে আমি সেটা পোস্টও করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি সেখানে লিখেছি</span>- ‘<span lang=\"BN\">আপনার ট্রাম্প প্লাজাতে আমি অনেকদিন জুয়া খেলেছি এবং অনেক টাকা হেরেছি</span>; (<span lang=\"BN\">হি ডিক্লিয়ার হিমসেলফ ব্যাংক</span>-<span lang=\"BN\">রাপ্ট থ্রাইস অর ফোর্থ টাইম</span><span lang=\"HI\">।</span> <span lang=\"BN\">হি নিউ হাউ টু চিট দ্য আমেরিকান সিস্টেম</span>) <span lang=\"BN\">আপনি নিজেকে ব্যাংক</span>-<span lang=\"BN\">রাপ্ট ঘোষণা করেছিলেন</span>; <span lang=\"BN\">আপনার কাছে তখন অর্থ দাবি করা সমুচিত হবে না ভেবেছি</span>, <span lang=\"BN\">এখন তো আপনি আমেরিকার প্রেসিডেন্ট হয়েছেন</span>, <span lang=\"BN\">এখন যদি আপনি টাকাটা ফেরত না দেন</span>, <span lang=\"BN\">অন্তত আপনার কনভেকশনে আমাকে ডাকবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার ক্যাসিনোতে আমি আবার খেলতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আর আপনার কাছে আমার একটাই আর্জি</span>- <span lang=\"BN\">আপনি আমার কথা বিবেচনা করে</span>, <span lang=\"BN\">বাংলাদেশের অভিবাসীদের সাথে কোনো দুর্ব্যবহার করবেন না</span>’! <span lang=\"BN\">আমি হ্যাপি ছিলাম ট্রাম্প জয়ী হওয়ার কারণে</span>, <span lang=\"BN\">কারণ হিলারী সম্পর্কে আমার ডিজার্ভেশন ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার বন্ধু শেখ হাসিনাকে নানাভাবে নাজেহাল করার প্রস্তুতি তার মধ্যে ছিল এবং তিনি ইতোপূর্বেও সেটা করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমি চাইনি হিলারী ক্লিনটন জিতুক</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">লেখালেখির শুরুটা কখন থেকে</span>? <span lang=\"BN\">কীভাবে শুরু করলেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">লেখালেখির শুরুটা তো আসলে</span>…<span lang=\"BN\">আমি মুখে মুখে ছড়া</span>-<span lang=\"BN\">টড়া বানাতে পারতাম আগে থেকেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের এলাকার পুরনো কবিদের বেশ সম্মান করতাম</span>; <span lang=\"BN\">কেন করতাম জানি না</span>, <span lang=\"BN\">অন্যদের তো করতাম না</span><span lang=\"HI\">।</span> <span lang=\"BN\">হয়তো আমার মধ্যে একটা ব্যাপার ছিল</span>, <span lang=\"BN\">মানুষ হয়তো তার অজ্ঞাতসারেই নিয়তি দ্বারা পরিচালিত হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বিজ্ঞানে বিশ্বাসী ছিলাম</span>, <span lang=\"BN\">ভাগ্যে ছিলাম অনাস্থাশীল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন যেমন দেখতে পাচ্ছি</span>, <span lang=\"BN\">ভবিতব্যে জীবনের মিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভবিতব্য বলে একটা জিনিস থাকতে পারে</span>, <span lang=\"BN\">নাহলে রবীন্দ্রনাথরা ভাই</span>-<span lang=\"BN\">বোন মিলে তের</span>-<span lang=\"BN\">চৌদ্দজন ছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার অন্য ভাই</span>-<span lang=\"BN\">বোনরা তো কেউ কবি হননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">একই পিতা</span>-<span lang=\"BN\">মাতার ঘরে জন্ম নিয়েছেন</span>, <span lang=\"BN\">একই পরিবেশে ঠাকুর বাড়ির আঙিনার মধ্যে বড় হয়েছেন</span>- <span lang=\"BN\">সেখান থেকেও তো রবীন্দ্রনাথ একজনই হয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমান বলেছেন যে তিনি কবিতা লিখেছেন তার বোন মারা যাওয়ার পর</span><span lang=\"HI\">।</span> <span lang=\"BN\">বোনের অকাল মৃত্যু তাকে খুব কষ্ট দিয়েছিল বলে তিনি একটা কবিতা লিখেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপর থেকে তার মধ্যে ওই উপলব্ধি আসে যে তিনি কবিতা লিখতে পারেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন আমি কবিতা কেন লিখেছি</span>, <span lang=\"BN\">এটা স্পষ্ট করে বলতে পারব না</span><span lang=\"HI\">।</span> <span lang=\"BN\">কেউ যুক্তিসঙ্গত</span>-<span lang=\"BN\">ভাবে এটা ব্যাখ্যা করতে পারবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার পক্ষেও বলা কঠিন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে আমার নিজ এলাকার আঞ্চলিক কবি এবং লোকসাহিত্যের ব্যাপারে ভীষণ আগ্রহ অনেক আগে থেকেই ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঐ এলাকায় কোনো কবি আছে কি</span>-<span lang=\"BN\">না</span>, <span lang=\"BN\">সেটা জানার</span>, <span lang=\"BN\">সে বিষয়ে খোঁজ নেওয়ার এবং তথ্য সংগ্রহ করার প্রতি আমি আগ্রহী ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তাদের জানার পরে অনেকটা প্রভাবিত হয়েই এ জগতের দিকে কিছুটা ধাবিত হয়েছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার প্রথম বই </span>‘<span lang=\"BN\">প্রেমাংশুর রক্ত চাই</span>’ <span lang=\"BN\">ছাপানোর অভিজ্ঞতা কেমন ছিল</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আমার প্রথম কবিতা ১৯৬১ সালে </span>‘<span lang=\"BN\">উত্তর আকাশ</span>’ <span lang=\"BN\">নামের একটি পত্রিকায় ছাপা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এ পত্রিকার সম্পাদনা করতেন খালেক দাদ চৌধুরী</span><span lang=\"HI\">।</span> <span lang=\"BN\">এ পত্রিকায় একটি সাহিত্য বিভাগ ছিল</span>, <span lang=\"BN\">নেত্রকোণার প্রবীণ এবং তরুণ কবি</span>-<span lang=\"BN\">সাহিত্যকরাই সেখানে বেশি লিখতেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপর মাঝখানে অনেকদিন গ্যাপ দেওয়ার পর ১৯৬৫ সালের একুশে ফেব্রুয়ারির উপর লেখা একটা কবিতা ঢাকার </span>‘<span lang=\"BN\">সাপ্তাহিক জনতা</span>’ <span lang=\"BN\">বলে একটা পত্রিকায় ছাপা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে আবার ঢাকা বিশ্ববিদ্যালয়ের ফার্মেসী বিভাগ</span>, <span lang=\"BN\">ইঞ্জিনিয়ারিং</span>, <span lang=\"BN\">বি</span>.<span lang=\"BN\">এস</span>.<span lang=\"BN\">সিতে ভর্তি হওয়ার চক্করে লেখালেখিতে ভাটা পড়ে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এক দিক থেকে অবশ্য ভালোই হলো</span>; <span lang=\"BN\">প্রাতিষ্ঠানিক শিক্ষার ক্ষেত্রে আমি যে হোঁচট খেলাম</span>, <span lang=\"BN\">সেটা আমাকে বাউন্স ব্যাক করতে সুবিধা করল</span>- <span lang=\"BN\">আই গো অ্যান্ড ব্যাক টু মাই ওউন ওয়ার্ল্ড</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার নিজস্ব জগৎ যে কাব্য</span>, <span lang=\"BN\">সেটার দিকে আমার নিয়তি আমাকে ঠেলে দিলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমি আবার কবিতা লেখা শুরু করলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথমে বঙ্গবন্ধুর পক্ষে রাজনৈতিক কবিতা</span>; <span lang=\"BN\">তারপর আস্তে আস্তে প্রেম বোধ জাগ্রত হলো</span>, <span lang=\"BN\">তখন প্রেমের কবিতা</span>…<span lang=\"BN\">এভাবে আস্তে আস্তে কবিতার প্রতি ঝুঁকে পড়লাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবিতার বইটা প্রকাশিত হয়েছে অনেক পরে</span>, <span lang=\"BN\">১৯৭০ সালে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে তার আগেই ৬৭</span>’ <span lang=\"BN\">সালে ঢাকা বিশ্ববিদ্যালয়ে ভর্তি হওয়ার পর আমি </span>‘<span lang=\"BN\">সংবাদ</span>’, ‘<span lang=\"BN\">আজাদ</span>’, ‘<span lang=\"BN\">পয়গাম</span>’, ‘<span lang=\"BN\">পূর্বদেশ</span>’- <span lang=\"BN\">সব কাগজেই লিখতে শুরু করেছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তরুণ কবি হিসেবে মোটামুটি খ্যাতি বই প্রকাশের আগেই পেয়ে গিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু বই প্রকাশের ব্যাপারে প্রথমে কোনো প্রকাশকই আগ্রহ দেখাননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিভিন্ন প্রকাশনীর কাছে গিয়েছি</span>, <span lang=\"BN\">তারা একজন নতুন কবির ব্যাপারে তেমন আগ্রহ প্রকাশ করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">শামসুর রাহমানের বই তার এক বন্ধু নিজের টাকায় প্রকাশ করে দিয়েছিলেন</span>; <span lang=\"BN\">আল মাহমুদের বই তার বন্ধুরা মিলে একটা প্রকাশনা সংস্থা দিয়েছিলেন</span>, <span lang=\"BN\">সেখান থেকে বের করেছিলেন</span>, <span lang=\"BN\">আব্দুল মান্নান সৈয়দও নিজের টাকাতেই বই বের করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি</span>-<span lang=\"BN\">ই কিন্তু প্রথম যে প্রফেশনাল প্রকাশক দিয়ে বই প্রকাশ করিয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">খান ব্রাদার্স থেকে ১৯৭০ সালের নভেম্বর মাসে আমার প্রথম বই </span>‘<span lang=\"BN\">প্রেমাংশুর রক্ত চাই</span>’ <span lang=\"BN\">প্রকাশিত হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৭১ সালের ২৫ মার্চ থেকে তো স্বাধীনতার জন্য যুদ্ধই শুরু হয়</span>, <span lang=\"BN\">ফলে এ বইটি খুব বেশি দিন বাজারে ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">মুক্তিযুদ্ধের সময় ১৯৭১ সালে কলকাতা থেকে চিত্তরঞ্জন সাহা বইটি আবার প্রকাশ করেছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি মুক্তধারা সাহিত্য সংগঠন করতেন</span>, <span lang=\"BN\">সেখান থেকেই বইটি বের হয়েছিল</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আমি শুনেছি </span>‘<span lang=\"BN\">প্রেমাংশুর রক্ত চাই</span>’ <span lang=\"BN\">বইটির প্রথম কপিটি আপনি একজন গণিকাকে দিয়েছিলেন</span>... </strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা তুমি কোথায় শুনলে</span>? <span lang=\"BN\">বই এর মধ্যে লিখেছি নাকি</span>?\r\n\r\n<strong> <span lang=\"BN\">আমি কোথায় যেন এক জায়গায় পড়েছিলাম ঘটনাটা</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">বইটার যে প্রচ্ছদটা ছাপা হয়েছিল বাবু বাজারের খুশি প্রিন্টার্স বলে একটা জায়গা থেকে</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথম বই বেরোচ্ছে খুব আবেগে এবং ফূর্তিতে ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">বইটা যেখানে ছাপা হয়েছে তার পাশেই একটা গণিকা পল্লী ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে আমাদের কয়েকজন বন্ধুর নিয়মিত যাতায়াত ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথম বই ছাপানোর উত্তেজনাটা কারো সাথে শেয়ার করার ইচ্ছা হয়েছিল আমার</span>, <span lang=\"BN\">এই আবেগটাকে শেয়ার করার জন্য কাউকে পাইনি তখন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেই সূত্রেই আমার সঙ্গে যার ভালো সম্পর্ক ছিল</span>, <span lang=\"BN\">ওই মহিলাকে বইয়ের কাভারটা দিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে জিজ্ঞেস করলো </span>‘<span lang=\"BN\">এটা কী</span>’? <span lang=\"BN\">সে তো এ বিষয়ে কিছু জানে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বললাম এটা তোমার ঘরের মধ্যে টাঙানো থাক</span>, <span lang=\"BN\">তুমি যে আমার খুব প্রিয়</span>- <span lang=\"BN\">এটা তারই প্রমাণ বহন করবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি নিজেই ওইটা তার ঘরে টাঙিয়ে দিয়েছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">পঁচাত্তর পরবর্তী সময় আপনাকে তো গ্রেফতার করা হয়েছিল বলে শুনেছি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">সেনাবাহিনী ধরে নিয়ে গিয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">৭ই নভেম্বরের পাল্টা অভ্যুত্থানে যখন খালেদ মোশাররফসহ অনেকেই নিহত হয়েছিলেন</span>, <span lang=\"BN\">তখন সেনাবাহিনীর মধ্যে একটা বিশৃঙ্খলার সৃষ্টি হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা সেটাকে সিপাহী বিপ্লব বলে দাবি করেছিলেন</span>, <span lang=\"BN\">কিন্তু তাদের চরিত্র</span>- <span lang=\"BN\">কোনো বিপ্লবী চরিত্র ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">খালেদ মোশাররফকে হত্যা করার পর সেনাবাহিনীর সদস্যরা তার বাড়ি লুট করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">শুধুমাত্র একটি ভাঙা চেয়ার রেখে সবই তারা লুট করে নিয়ে যায়</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং যারা ৭ই নভেম্বরের বিপ্লবের কথা বলে</span>, <span lang=\"BN\">তাদের চরিত্রে যদি বিন্দুমাত্র বিপ্লবের উপাদান থাকতো তাহলে খালেদ মোশাররফের বাড়ি এভাবে লুণ্ঠিত হতো না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার সঙ্গে আরো যারা নিহত হয়েছিলেন বজলুল হুদা এবং এটিএম হায়দার</span>- <span lang=\"BN\">তাদের তিনজনকে একসঙ্গে হত্যা করা হয়েছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে সময় যখন সেনা কর্মকর্তাদের হত্যা করা হচ্ছিল</span>, <span lang=\"BN\">বিটিভি তখন রামপুরাতে স্থানান্তরিত হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে গিয়ে আকমল</span>, <span lang=\"BN\">মুনীরুল আলম</span>- <span lang=\"BN\">এ রকম কয়েকজনকে ওরা ভারতীয় চর অভিযোগে হত্যা করে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেনাবাহিনীর উপর কোনো নিয়ন্ত্রণ নেই</span>, <span lang=\"BN\">কোনো চেইন অব কমান্ড নেই</span>-  <span lang=\"BN\">এ রকম একটা অবস্থায় আমার বন্ধু আবুল হাসান তখন পিজি হাসপাতালে চিকিৎসাধীন ছিলেন</span>, <span lang=\"BN\">তাকে দেখতে গিয়ে আমি অপেক্ষমান সেনাবাহিনীর হাতে ধরা পড়ি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা সেখানে কার জন্য</span>, <span lang=\"BN\">কেন অপেক্ষা করছিল</span>- <span lang=\"BN\">এ প্রশ্নের জবাব মেলেনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু আমার মনে হয়েছে</span>- <span lang=\"BN\">আমাকে ধরেই যখন তাদের অপেক্ষার শেষ হলো</span>, <span lang=\"BN\">তখন আমার জন্যই বোধহয় অপেক্ষা করছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">একজন কর্নেল আমাকে অ্যারেস্ট করে একটা ট্রাকে উঠতে বললেন</span>, <span lang=\"BN\">যেহেতু সেনাবাহিনীর মধ্যে তখন কোনো কমান্ড নেই</span>, <span lang=\"BN\">ফলে আমি ঐ ট্রাকে উঠতে ভয় পাচ্ছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">ট্রাকের মধ্যে অধিকাংশই অশিক্ষিত জোয়ানরা আছে</span>, <span lang=\"BN\">আমি কবি হিসেবে কতটা মূল্য বহন করি</span>- <span lang=\"BN\">তারা তো সেটা বুঝবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার সঙ্গে হয়তো মিসবিহেভ করবে</span>, <span lang=\"BN\">কিংবা মেরেও ফেলতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে কর্নেল আমাকে গ্রেফতার করল</span>, <span lang=\"BN\">তাকে আমি গিয়ে বললাম</span>- <span lang=\"BN\">স্যার</span>, <span lang=\"BN\">আমি আপনার সঙ্গে যেতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু তিনি তাতে রাজি হলেন না</span>, <span lang=\"BN\">ফলে জোয়ানদের রোষের মধ্যে আমাকে পড়তে হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা ভাবলো আমি তাদের প্রতি আস্থা রাখিনি</span>, <span lang=\"BN\">তাদের সঙ্গে যেতে রাজি হইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন আমার দিকে অস্ত্র তাক করে তারা ভয় দেখানো শুরু করল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপর আমাকে সোহরাওয়ার্দীর ভেতরে পুলিশ ক্যাম্পে নিয়ে যাওয়া হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন যেটা থানা হয়েছে</span>, <span lang=\"BN\">আগে সেটা পুলিশের অস্থায়ী ক্যাম্প ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">যখন আমি ঐ ক্যাম্পের ভেতরে ঢুকছি</span>, <span lang=\"BN\">কর্নেল সাহেব তখন বের হয়ে যাচ্ছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি সেল্যুট দিয়ে তার দৃষ্টি আকর্ষণ করার চেষ্টা করলাম</span>, <span lang=\"BN\">তিনি না তাকিয়েই চলে গেলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভেতর থেকে একজন পুলিশ অফিসার এসে আমাকে রিসিভ করে নিয়ে গেলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভেতরে নিয়ে যাওয়ার পর পুলিশ অফিসারটি আমার কানে কানে বলল</span>- <span lang=\"BN\">আপনি ভয় পাবেন না</span>, <span lang=\"BN\">যে অফিসার আপনাকে অ্যারেস্ট করেছেন</span>, <span lang=\"BN\">তিনি আমাদের লিখিত দিয়ে গেছেন</span>- ‘<span lang=\"BN\">ডোন্ট মিসবিহেভ উইথ হিম</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">ঐ কর্নেল ভদ্রলোকের নাম ছিল কর্নেল নওয়াজেশ আহমেদ</span><span lang=\"HI\">।</span> <span lang=\"BN\">এরপরে ঐ ক্যাম্প থেকে রমনা থানার পুলিশকে ডাকা হলো</span>, <span lang=\"BN\">যাতে তাদের কাস্টডিতে আমাকে নিয়ে যাওয়া হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরে রমনা থানায় আমাকে নিয়ে যাওয়া হলো</span>, <span lang=\"BN\">ওইখানে আমি সাত দিন ছিলাম</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">রমনা থানায় আপনি যখন ছিলেন</span>, <span lang=\"BN\">আপনাকে মুক্তি দেওয়ার জন্য বোধহয় বিবৃতি প্রকাশ করার চেষ্টা করেছিলেন কবি মহাদেব সাহা</span><span lang=\"HI\">।</span> <span lang=\"BN\">তখন শামসুর রাহমান</span>, <span lang=\"BN\">হাসান হাফিজুর রহমান এবং আহসান হাবীব নাকি আপনার পক্ষে কাজ করার জন্য তাকে ডিসকারেজ করেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হ্যাঁ</span><span lang=\"HI\">।</span> <span lang=\"BN\">মহাদেব অ্যাটেম্প নিয়েছিল আমাকে মুক্তির জন্য একটা বিবৃতি প্রকাশ করতে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু তুমি যাদের নাম বললে</span>, <span lang=\"BN\">তারা নিজেরা তো বিবৃতি দিতে রাজি ছিলেনই না</span>, <span lang=\"BN\">মহাদেবকেও ডিসকারেজ করেছিলেন এটা বলে যে</span>- ‘<span lang=\"BN\">তোমার এটা নিয়ে মুভ করা উচিত না</span>, <span lang=\"BN\">কারণ বঙ্গবন্ধু নিহত হওয়ার পর নির্মল কোথায় ছিল সে সম্পর্কে আমাদের স্পষ্ট ধারণা নেই</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধুকে হত্যার পর আমি গ্রামে ফিরে গিয়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার ধারণা ছিলো</span>- <span lang=\"BN\">যে কোনো সময় আমাকে মেরে ফেলা হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিছুটা মানসিক ভারসাম্যও হারিয়ে ফেলেছিলাম আমি</span><span lang=\"HI\">।</span> <span lang=\"BN\">দীর্ঘ সময় আমাকে অনুপস্থিত দেখে</span>, <span lang=\"BN\">তারা অনুমান করতে পারেননি হোয়াট আই ডিড দ্যাট টাইম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি তো অ্যারেস্ট হয়েছি ১১ নভেম্বর</span>, <span lang=\"BN\">বঙ্গবন্ধুকে হত্যা করা হয়েছে ১৫ আগস্ট</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৮ আগস্ট আমি ঢাকা ত্যাগ করেছি অ্যান্ড কেম ব্যাক টু ঢাকা অন ফাস্ট নভেম্বর</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে এই আড়াই মাস সময় যে আমি বাড়িতে ছিলাম</span>, <span lang=\"BN\">এ সময় কিন্তু কাদের সিদ্দিকীর নেতৃত্বে ভারতে একটা বাহিনী গড়ে উঠেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা বঙ্গবন্ধু হত্যার প্রতিশোধ নেওয়ার চেষ্টা চালাচ্ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেখানে অনেকেই যোগদান করেছিল এবং মজার বিষয় হচ্ছে ওই বাহিনীতে ময়মনসিংহের পূর্বাঞ্চলের মানুষই বেশি ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">তো তারা মাঝে মাঝে আক্রমণ করতো</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার যেহেতু বাড়ি ঐদিকেই</span>, <span lang=\"BN\">ফলে অনেকের ধারণা হয়েছিল সম্ভবত আমি কাদেরিয়া বাহিনীর সঙ্গে যুক্ত হয়েছি</span>, <span lang=\"BN\">আর ভারতের সঙ্গেও বোধহয় আমার একটা যোগাযোগ আছে</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">ব্যক্তি লেখক এবং তার লেখক সত্ত্বা কী অভিন্ন নাকি আলাদা</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">আলাদা কি করে হয়</span>- <span lang=\"BN\">এটা আমি বুঝি না</span><span lang=\"HI\">।</span> <span lang=\"BN\">যিনি লেখক তিনি তো তার দেহের বাইরের সত্ত্বা নন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি দেহের ভেতরেই বাস করেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং এই দেহের সঙ্গে তার তো কোনো বিরোধ নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">তবে আমি যেরকম অকপটে জীবনের সকল সত্য প্রকাশ করি</span>, <span lang=\"BN\">তাতে আমাকে অন্যের কাছ থেকে ভিন্ন মনে হতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা হয়তো বলতে পারো</span>- <span lang=\"BN\">আপনার জীবনের সঙ্গে অন্যের জীবনের মিল নেই</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেমন একজন বলেছিল</span>- <span lang=\"BN\">আপনি যেভাবে আপনার বোহেমিয়ান জীবনের কথা বর্ণনা করেন</span>, <span lang=\"BN\">তাতে আমাদের পক্ষে আপনার রচনা পাঠ করার কথা ছেলে</span>-<span lang=\"BN\">মেয়েদের বলা সম্ভব হয় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বললাম</span>- <span lang=\"BN\">আমি আমার জীবনের অভিজ্ঞতাকে বর্ণনা করছি</span>; <span lang=\"BN\">সে অভিজ্ঞতা মানুষকে তার ভুল থেকে শিক্ষা গ্রহণ করতে আগ্রহী করবে</span>, <span lang=\"BN\">এ অভিজ্ঞতা মানুষকে আপোষহীন সংগ্রামের মধ্যে দিয়ে জীবন যাপন করা শেখাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ঢাকা শহরে থাকার মতো কোনো ব্যবস্থা আমার ছিল না</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৬৬ সালে একটা পত্রিকায় আড়াইশ টাকা বেতনের চাকরি পাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রথম বেতন পেয়ে আমি একটা বালিশ কিনেছিলাম যে নিজের বালিশে ঘুমাতে পারব</span>, <span lang=\"BN\">একটা থালা কিনেছিলাম এটা ভেবে যে নিজের থালায় খাব</span><span lang=\"HI\">।</span> <span lang=\"BN\">নিজের থালা</span>-<span lang=\"BN\">বালিশ কেনা আমার সঙ্গতির মধ্যে ছিল না</span>; <span lang=\"BN\">চিন্তার মধ্যেও ছিল না</span>, <span lang=\"BN\">এগুলো যে আমার নেই</span>- <span lang=\"BN\">সেই উপলব্ধিও আমার ছিল না</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">আমার কণ্ঠস্বর</span>’ <span lang=\"BN\">বইটি পড়লে আমার জীবন সংগ্রামের সঙ্গে পাঠকের যে পরিচয় হবে</span>- <span lang=\"BN\">সেটা তাদেরকে জীবনে সংগ্রাম করতে সহায়তা করবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">বিশ্ববিদ্যালয়ের ফার্মেসীতে ভর্তি হতে পারলাম না</span>; <span lang=\"BN\">মেডিকেলে পড়তে পারলাম না</span>, <span lang=\"BN\">কিন্তু আমার জীবনে যে বড় হওয়ার স্বপ্ন</span>- <span lang=\"BN\">সেটা মরে যায়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রতিকূলতার সঙ্গে সংগ্রাম করে</span>; <span lang=\"BN\">বর্ষা</span>-<span lang=\"BN\">প্লাবনের মধ্যে পাল্লা দিয়ে যে ধানগুলো বাড়ে</span>- <span lang=\"BN\">আমি সেভাবে প্রতিকূলতার সঙ্গে লড়ে মাথা উঁচু করে বাঁচার জন্য সংগ্রাম করেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে কারণে আমার অকপট স্বীকারোক্তিগুলো মনে হয় সবার জানা জরুরি</span>, <span lang=\"BN\">জরুরি না হলেও আমি কী রকম সেটা তো আমাকে বলতেই হবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি মিথ্যে কথা বলে মানুষের চিত্ত জয় করতে চাই না</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার অসংখ্য প্রেমের কবিতা আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার লেখালেখি জীবনে নারী এবং প্রেমকে কীভাবে মূল্যায়ন করেন</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা তো একটা প্রাকৃতিক সত্য</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে আমাদের সৃজন করেছেন অর্থাৎ স্রষ্টা যিনি</span>, <span lang=\"BN\">তিনি নারী এবং পুরুষদের পরস্পরের প্রতি আকৃষ্ট হওয়ার জন্য কিছু মন্ত্র তাদের ভেতরে দিয়ে দিয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার </span>‘<span lang=\"BN\">নিশিকাব্য</span>’<span lang=\"BN\">র ভূমিকায় আছে</span>, <span lang=\"BN\">এটা হাদিস থেকে উদ্ধৃত করছেন একজন তিউনিশিয়ান কবি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি বলেছেন</span>- ‘<span lang=\"BN\">সকল প্রশংসা আল্লাহর</span>, <span lang=\"BN\">যিনি এই পুরুষের প্রিয় অঙ্গসমূহ নারীর দেহে এবং নারীর প্রিয় অঙ্গসমূহ পুরুষের দেহে সংস্থাপন করেছেন</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">এখন তিনি একটা নারী মূর্তি তৈরি করলেন আর একটা পুরুষ মূর্তি তৈরি করলেন</span>- <span lang=\"BN\">তারা যদি পরস্পর আকৃষ্ট বোধ না করে</span>, <span lang=\"BN\">ইফ দে ডু নট এপ্রিশিয়েট ইচ আদার</span>- <span lang=\"BN\">তাহলে তো তারা ইউনাইটেড হওয়ার প্রয়োজন বোধ করবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আর যদি ইউনাইটেড হওয়ার প্রয়োজন বোধ না করে</span>, <span lang=\"BN\">তাহলে তো সৃষ্টির প্রক্রিয়ার মধ্যে সে অংশগ্রহণ করবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">রবীন্দ্রনাথের একটা গান আছে না</span>-  <span lang=\"BN\">তুমি তাই এসেছ নীচে</span>/<span lang=\"BN\">আমায় নইলে ত্রিভুবনেশ্বর</span>/<span lang=\"BN\">তোমার প্রেম হত যে মিছে</span>/ <span lang=\"BN\">আমায় নিয়ে মেলেছ এই মেলা</span>/<span lang=\"BN\">আমার হিয়ায় চলছে রসের খেলা</span>/<span lang=\"BN\">মোর জীবনে বিচিত্ররূপ ধরে</span>/<span lang=\"BN\">তোমার ইচ্ছা তরঙ্গিছে</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">অর্থাৎ এখানে স্রষ্টার ইচ্ছাটা তরঙ্গিত হচ্ছে নারী এবং পুরুষের মাধ্যমে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যার ফলে নারী</span>-<span lang=\"BN\">পুরুষের পরস্পরের প্রতি একটা বিশেষ আকর্ষণ থাকে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা শুধু মানুষের ক্ষেত্রে না</span>, <span lang=\"BN\">রিপ্রোডাকশন বা নিজেকে পুনরায় সৃষ্টি করার একটা আগ্রহের দিক থেকে পৃথিবীর সকল জীব</span>-<span lang=\"BN\">জন্তুর মধ্যেই ঐক্য আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং নারীর প্রতি যে পুরুষের আকর্ষণ বা পুরুষের প্রতি যে নারীর আগ্রহ</span>- <span lang=\"BN\">এটা প্রাকৃতিকভাবেই আমাদের মধ্যে স্রষ্টা দিয়ে দিয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা শুধু তার ডাকে সাড়া দিচ্ছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের ভেতরের যে প্রবণতাগুলো শৈশবে সুপ্ত আকারে থাকে</span>, <span lang=\"BN\">সে সময় তো আমরা নারী</span>-<span lang=\"BN\">পুরুষের ব্যবধানটা বুঝতে পারি না</span>, <span lang=\"BN\">মানে পার্থক্যগুলো তো ছোটবেলায় ধারণাতে আসে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আচার</span>-<span lang=\"BN\">আচরণের মধ্যেও কোনো পার্থক্য বোঝা যায় না</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু বয়োঃবৃদ্ধির সঙ্গে সঙ্গে বোঝা যাচ্ছে যে নারীর প্রতি পুরুষের বা পুরুষের প্রতি নারীর এক ধরণের কাম তৃষ্ণা জাগ্রত হচ্ছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে এটারই একটা সভ্য</span>-<span lang=\"BN\">সংস্কৃত প্রকাশ হচ্ছে </span>‘<span lang=\"BN\">প্রেম</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">এই প্রেমটা কোনো মৌলিক রিপু নয়</span>, <span lang=\"BN\">মৌলিক রিপু হচ্ছে </span>‘<span lang=\"BN\">কাম</span>’<span lang=\"HI\">।</span> <span lang=\"BN\">মানুষ আর অন্য প্রাণীর মধ্যে পার্থক্য হলো</span>, <span lang=\"BN\">নিজেদের পরিবর্তন করার শক্তি আমাদের আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের ভেতরে যে প্রকৃতি প্রদত্ত হয়েছে</span>, <span lang=\"BN\">সে প্রকৃতি পরিবর্তন করার ক্ষমতাও আমাদের রয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে আমরা ধর্ষণকে উৎসাহিত করি না</span>, <span lang=\"BN\">প্রেমকে উৎসাহিত করি</span><span lang=\"HI\">।</span> <span lang=\"BN\">ধর্ষণ সামাজিক বিশৃঙ্খলার জন্য করা হয়</span>, <span lang=\"BN\">এটা অন্যায় এবং পাপ</span><span lang=\"HI\">।</span> <span lang=\"BN\">মানব জাতির গর্ভধারিণী হচ্ছে নারী</span>, <span lang=\"BN\">সেই নারীকে পূজনীয় জ্ঞান করা</span>, <span lang=\"BN\">তার প্রতি কৃতজ্ঞতা বোধে নত হওয়া যেখানে পুরুষের কর্তব্য</span>, <span lang=\"BN\">সেখানে শিকড় অস্তিত্বের এই নিগুঢ় সত্যটিকে উপলব্ধি করতে না পেরে ধর্ষক তার কামকে চরিতার্থ করার জন্য নারীর উপর অত্যাচার করছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সঙ্গম আর ধর্ষণের মধ্যে যে পার্থক্য আছে</span>, <span lang=\"BN\">এটা ঐ ধর্ষকরা ভুলে যাচ্ছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখানে সে অন্য বন্য প্রাণীর থেকেও ভয়ঙ্কর আচরণ করছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">ফলে তাকে পাশবিক যখন বলা হয়</span>, <span lang=\"BN\">তখন মূলত পশুকে অপমান করা হয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">পশুর মধ্যে এই পাশবিকতা নেই</span>, <span lang=\"BN\">যে অন্যায়</span>, <span lang=\"BN\">নিষ্ঠুর আচরণ আমরা মানুষের মধ্যে প্রত্যক্ষ করি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কদিন আগে আমি লিখেছি</span>- <span lang=\"BN\">মায়ানমারের বিরুদ্ধে যুদ্ধ করতে চাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেটা আমি লিখেছি</span>- <span lang=\"BN\">রোহিঙ্গা নারীদের উপর অত্যাচারের ভিডিওগুলো দেখে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে নারীকে ধর্ষণ করা হচ্ছে</span>, <span lang=\"BN\">তাকেই আবার মেরে ফেলা হচ্ছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">মানুষের চরিত্রের এই অধঃপতন</span>, <span lang=\"BN\">অমানবিকতাকে পরাভূত না করা পর্যন্ত মানুষ যে সভ্য</span>- <span lang=\"BN\">এটা দাবি করা যাবে না</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">সব সৃষ্টিই তো স্রষ্টার কাছে খুব প্রিয় বা সন্তানতুল্য</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা আপনার কণ্ঠে আপনার সৃষ্ট একটি প্রিয় কবিতা শুনতে চাই</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এই কবিতাটা</span> (‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’) <span lang=\"BN\">আমারও খুব প্রিয়</span>, <span lang=\"BN\">পাঠকদেরও প্রিয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা আবার গ্রামীণফোনেরও খুব প্রিয়</span><span lang=\"HI\">।</span> <span lang=\"BN\">গ্রামীণফোন এটাকে ওয়েলকাম টিউন হিসেবে ব্যবহার করে ব্যবহারকারীদের কাছ থেকে মাসে ত্রিশ টাকা করে কেটে নিচ্ছে</span>, <span lang=\"BN\">কিন্তু আমাকে তারা আজ পর্যন্ত একটি পয়সাও রয়্যালিটি দেয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে জন্য আমি এই কবিতাটা পাঠ করছি এবং গ্রামীণফোনের প্রতি এই অভিযোগ উত্থাপন করছি</span>- <span lang=\"BN\">তারা আমার এই কবিতাটি বহু বছর ধরে ব্যবহার করে আসছে</span>, <span lang=\"BN\">কিন্তু আমি তাদের কাছে আজ পর্যন্ত কোনো টাকা পাইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">অনেকবার আমি ফেসবুকের স্ট্যাটাসেও এটা লিখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদের বর্তমান যে টেলিযোগাযোগ প্রতিমন্ত্রী আছেন</span>; <span lang=\"BN\">তাকেও বলেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারাও আমার এই ন্যায্য</span>-<span lang=\"BN\">ন্যায়সঙ্গত দাবি আদায় করে দিতে পারেননি</span>, <span lang=\"BN\">কিংবা কোনো উদ্যোগই হয়তো গ্রহণ করেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি এর প্রতিবাদ জানিয়ে কবিতাটি পাঠ করছি</span>…\r\n\r\n“<span lang=\"BN\">আমি বলছি না ভালোবাসতেই হবে</span>, <span lang=\"BN\">আমি চাই</span>\r\n\r\n<span lang=\"BN\">কেউ একজন আমার জন্য অপেক্ষা করুক</span>,\r\n\r\n<span lang=\"BN\">শুধু ঘরের ভিতর থেকে দরোজা খুলে দেবার জন্য</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">বাইরে থেকে দরোজা খুলতে খুলতে আমি এখন ক্লান্ত</span><span lang=\"HI\">।</span>\r\n\r\n&nbsp;\r\n\r\n<span lang=\"BN\">আমি বলছি না ভালোবাসতেই হবে</span>, <span lang=\"BN\">আমি চাই</span>\r\n\r\n<span lang=\"BN\">কেউ আমাকে খেতে দিক</span><span lang=\"HI\">।</span><span lang=\"BN\">আমি হাতপাখা নিয়ে</span>\r\n\r\n<span lang=\"BN\">কাউকে আমার পাশে বসে থাকতে বলছি না</span>,\r\n\r\n<span lang=\"BN\">আমি জানি</span>, <span lang=\"BN\">এই ইলেক্ট্রিকের যুগ</span>\r\n\r\n<span lang=\"BN\">নারীকে মুক্তি দিয়েছে স্বামী</span>-<span lang=\"BN\">সেবার দায় থেকে</span><span lang=\"HI\">।</span>\r\n\r\n&nbsp;\r\n\r\n<span lang=\"BN\">আমি চাই কেউ একজন আমাকে জিজ্ঞেস করুক</span>:\r\n\r\n<span lang=\"BN\">আমার জল লাগবে কিনা</span>, <span lang=\"BN\">নুন লাগবে কিনা</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">পাটশাক ভাজার সঙ্গে আরও একটা</span>\r\n\r\n<span lang=\"BN\">তেলে ভাজা শুকনো মরিচ লাগবে কি না</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">এঁটো বাসন গেঞ্জি</span>-<span lang=\"BN\">রুমাল আমি নিজেই ধুতে পারি</span><span lang=\"HI\">।</span>\r\n\r\n&nbsp;\r\n\r\n<span lang=\"BN\">আমি বলছি না ভালোবাসতেই হবে</span>, <span lang=\"BN\">আমি চাই</span>\r\n\r\n<span lang=\"BN\">কেউ একজন ভিতর থেকে আমার ঘরের দরোজা খুলে দিক</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">কেউ আমাকে কিছু খেতে বলুক</span><span lang=\"HI\">।</span>\r\n\r\n<span lang=\"BN\">কাম</span>-<span lang=\"BN\">বাসনার সঙ্গী না হোক</span>, <span lang=\"BN\">কেউ অন্তত আমাকে</span>\r\n\r\n<span lang=\"BN\">জিজ্ঞেস করুক</span>, ‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’\"?\r\n\r\n<strong> <span lang=\"BN\">একজন কবি বা লেখককে কতটা রাজনৈতিক সচেতন হতে হয়</span>?</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">এটা ম্যান টু ম্যান ভ্যারি করে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা যে ভূখণ্ডে বাস করছি</span>, <span lang=\"BN\">সেটি সাম্প্রদায়িক দাঙ্গায় ক্ষত</span>-<span lang=\"BN\">বিক্ষত হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">১৯৪৭ সালে দেশ বিভক্ত হয়ে ভারত</span>-<span lang=\"BN\">পাকিস্তান হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">বঙ্গবন্ধু খুব অল্প কথায় ইতিহাসটা বর্ণনা করেছিলেন</span>- ‘<span lang=\"BN\">৫২</span>’ <span lang=\"BN\">সালে ভাষার জন্য রক্ত দিয়েছি</span>, <span lang=\"BN\">৫৪</span>’ <span lang=\"BN\">সালে নির্বাচনে জয় লাভ করেও ক্ষমতার গদিতে বসতে পারি নাই</span>, <span lang=\"BN\">৫৮</span>’ <span lang=\"BN\">সালে আইয়ূব খান সামরিক শাসন জারি করে আমাদের দশ বছরের জন্য গোলাম করে রেখেছেন</span>, <span lang=\"BN\">কী অন্যায় করেছিলাম</span>?’- <span lang=\"BN\">এই যে তিন</span>-<span lang=\"BN\">চারটা ঘটনা উল্লেখ করে তিনি দীর্ঘ দিনের বঞ্চনার ইতিহাস তুলে ধরেছিলেন</span>...<span lang=\"BN\">অল্প কথায় বলতে গেলে আমরা যদি বৃটিশ আমল থেকে ধরি</span>, <span lang=\"BN\">আমরা দুশ বছর বৃটিশদের গোলামী করেছি</span>, <span lang=\"BN\">তারপরে পাকিস্তান হলো</span>, <span lang=\"BN\">ভাষা আন্দোলন শুরু হয়ে গেল</span><span lang=\"HI\">।</span> <span lang=\"BN\">ভাষার জন্য গান লেখা</span>, <span lang=\"BN\">রক্ত দেওয়া</span><span lang=\"HI\">।</span> <span lang=\"BN\">৫৪</span>’ <span lang=\"BN\">সালে যুক্তফ্রন্ট জয় লাভ করল</span>, <span lang=\"BN\">তাদেরকে ক্ষমতায় যেতে দেওয়া হলো না</span><span lang=\"HI\">।</span> <span lang=\"BN\">৫৮</span>’ <span lang=\"BN\">সালে সামরিক শাসন চলে আসলো</span>, <span lang=\"BN\">যেটা আমরা ১৯৬৯</span>-<span lang=\"BN\">৭০ পর্যন্ত পেলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রতিকূল শক্তির বিরুদ্ধে বাংলাদেশের মানুষকে যত সংগ্রাম করতে হয়েছে</span>, <span lang=\"BN\">এ রকম সংগ্রাম খুব কম জাতি করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">বাংলাদেশের সঙ্গে তুলনা করার মতো একটি জাতিই আছে</span>, <span lang=\"BN\">তারা হলো ভিয়েতনামের জনগণ</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারাও অনেক অপশক্তির বিরুদ্ধে লড়াই করে গেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাদেরও অনেক সংগ্রাম করতে হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">দেশ স্বাধীন হলো</span>, <span lang=\"BN\">কয়েক বছরের মধ্যেই বঙ্গবন্ধুকে হত্যা করা হলো</span><span lang=\"HI\">।</span> <span lang=\"BN\">অপশক্তি ক্ষমতায় এসে বাংলাদেশের মুক্তিযুদ্ধের চেতনার অঙ্গিকার থেকে দেশকে সরিয়ে আবার পাকিস্তানি ভাব ধারায় নিয়ে যাওয়ার চেষ্টা করল</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই সর্ব সময়টা আমাকে তো বিভিন্ন অপশক্তির বিরুদ্ধেই সংগ্রাম করতে হয়েছে</span>, <span lang=\"BN\">আমি তো সব সময় প্রেমের কবিতা লিখতে পারিনি</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">তোমার চোখ এত লাল কেন</span>’?- <span lang=\"BN\">এ প্রশ্ন যেই প্রেমিকার কাছে আমি প্রত্যাশা করছি</span>, <span lang=\"BN\">তাকে তো আমি সময় দিতে পারিনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে তো বারবার যুদ্ধ ক্ষেত্রে যেতে হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সুতরাং বাংলাদেশের কবিতার এই বৈশিষ্ট্য অন্যসব কবিতা থেকে বাংলাদেশের কবিতাকে আলাদা করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এ দেশের কবিতা</span>- <span lang=\"BN\">সংগ্রামী কবিতা</span>, <span lang=\"BN\">সংগ্রামের কবিতা</span><span lang=\"HI\">।</span>\r\n\r\n<strong> <span lang=\"BN\">আপনার একটা কবিতার কিছু লাইন আছে</span>, ‘<span lang=\"BN\">আর কিছু তো আরাধ্য নয়</span>, <span lang=\"BN\">কাব্য এবং নারী ছাড়া</span>/ <span lang=\"BN\">ওটা পেলে জাহান্নামেও যেতে আমি এক পা খাড়া</span>’<span lang=\"HI\">।</span></strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">হুম</span><span lang=\"HI\">।</span> ‘<span lang=\"BN\">গুণ তো আমার নামেই আছে জন্মসূত্রে ছায়ার মতন</span>/<span lang=\"BN\">দোষটা আমার কর্মসূত্রে খেটে</span>-<span lang=\"BN\">পাওয়া</span>, <span lang=\"BN\">অর্জিত ধন</span>...’\r\n\r\n<strong> <span lang=\"BN\">আপনার এটা খুব জনপ্রিয় কবিতা</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার অসংখ্য ভক্ত আছে এ কবিতার</span><span lang=\"HI\">।</span> <span lang=\"BN\">কিন্তু এ কবিতার যারা ভক্ত</span>- <span lang=\"BN\">স্বাধীনতা পদক পাওয়ার জন্য যে স্ট্যাটাসটি আপনি দিয়েছিলেন</span>, <span lang=\"BN\">সেটা বোধহয় তাদের জন্য বেশ দুঃখজনক ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আপনার কাছে তো নিশ্চয়ই এর ব্যাখ্যা আছে</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">অনেক মানুষ একসঙ্গে ভুল করতে পারে</span>, <span lang=\"BN\">কিংবা অনেক মানুষ একসঙ্গে একটা ভুল সিদ্ধান্ত নিতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">স্কটল্যান্ড একবার ইংল্যান্ড থেকে বের হয়ে যাওয়ার সিদ্ধান্ত নিয়েছিল</span>, <span lang=\"BN\">পরবর্তীকালে দেশের মানুষ আবার সিদ্ধান্ত পরিবর্তনও করেছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি বলছি যে অনেকগুলো মানুষও কিন্তু ভুল সিদ্ধান্ত নিতে পারে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেমন আমার ক্ষেত্রে যেটা হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি দেখেছি যে ঐ ঘটনার বিপুল প্রতিক্রিয়া হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">অধিকাংশ মানুষ আমার এভাবে পুরস্কার চাওয়াটা গ্রহণ করতে পারেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">কবির সম্পর্কে যে ধারণা তাদের মনের মধ্যে আছে</span>, <span lang=\"BN\">সেটা তো আমি না</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা তো আমার মতো কবি দেখেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মতো কবি কীভাবে রিয়্যাক্ট করে</span>, <span lang=\"BN\">কীভাবে চায়</span>- <span lang=\"BN\">আমার চাওয়ার পদ্ধতির সঙ্গে তারা পরিচিত না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার অনেক কিছুই তারা নিতে পারবে না</span><span lang=\"HI\">।</span> <span lang=\"BN\">এই যে তুমি একটু আগে বললে যে</span>- <span lang=\"BN\">আপনার প্রথম বইটা আপনি একজন গণিকাকে দিয়েছিলেন</span>, <span lang=\"BN\">এটা পৃথিবীর খুব কম কবি</span>-<span lang=\"BN\">ই স্বীকার করবেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার কন্যা একবার আমাকে প্রশ্ন করেছে</span>- <span lang=\"BN\">বাবা তুমি কী গণিকালয়ে যেতে</span>? <span lang=\"BN\">আমি বলেছি হ্যাঁ</span>, <span lang=\"BN\">যেতাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মেয়ে আমাকে বলেছে</span>- <span lang=\"BN\">থ্যাংক ইউ</span>, <span lang=\"BN\">তুমি সত্য কথা বলেছো তাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মতো কবি তারা দেখেনি বলে আমার আচরণে কষ্ট পেয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমার মতো আর কেউ চাইতে পারবে</span>? <span lang=\"BN\">স্বাধীনতা পুরস্কার চাওয়ার মতো শক্তি কি তাদের আছে</span>, <span lang=\"BN\">সাহস আছে তাদের</span>? <span lang=\"BN\">যাদের নেই</span>, <span lang=\"BN\">তারা ভাবছে</span>- <span lang=\"BN\">এই কি</span>! <span lang=\"BN\">এই কবি পুরস্কার চায় কেন</span>! <span lang=\"BN\">আবার প্রধানমন্ত্রী সে দাবি মেনেও নেন</span>! <span lang=\"BN\">প্রধানমন্ত্রীর ক্যাবিনেটের মিনিস্টাররা পর্যন্ত বিব্রত হয়েছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা আমাকে মনোনীত করেননি বলে শেখ হাসিনা তাদেরকে ভৎসনা করেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কেন এত দিন আমার নাম তারা মনোনয়ন দেননি</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি বলেছেন</span>- <span lang=\"BN\">নির্মলেন্দু গুণ যদি স্বাধীনতা পুরস্কার না পায়</span>, <span lang=\"BN\">কারা পাচ্ছে তবে এ পুরস্কার</span>? <span lang=\"BN\">আই ডিজার্ভ ইট সো প্রাউডলি</span>, <span lang=\"BN\">সো স্ট্রংলি</span>- <span lang=\"BN\">ফলে আমার কাছে এটা না পাওয়াটাই অসম্মানজনক ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">পাওয়াটা যে সম্মানজনক হয়েছে তা নয়</span>, <span lang=\"BN\">কিন্তু না পাওয়াটা খুব অসম্মানজনক ছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">সে জন্য আমি এই বিরক্তিকর জিনিসটির অবসান হোক চেয়েছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি ভেবেছি</span>, <span lang=\"BN\">আমি যদি চাই</span>- <span lang=\"BN\">তবে তাদের পুরস্কারের মূল্যটা বৃদ্ধি পাবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি চাইলে এ পুরস্কার যে খুব কাঙ্ক্ষিত এবং প্রত্যাশিত</span>- <span lang=\"BN\">এটা লোকে জানবে</span><span lang=\"HI\">।</span> <span lang=\"BN\">তো পুরস্কারের মূল্য বৃদ্ধি হলে আমার তো কোনো ক্ষতি নাই</span><span lang=\"HI\">।</span> <span lang=\"BN\">পরবর্তীতে যারা পাবে</span>, <span lang=\"BN\">তারা একটা উর্ধ্ব মূল্যের পুরস্কার পাবে</span>, <span lang=\"BN\">যে পুরস্কার নির্মলেন্দু গুণ প্রকাশ্যে চেয়ে নিয়েছিলেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">প্রকাশ্যে না চেয়ে আমি যদি গোপনে শেখ হাসিনার কাছে পুরস্কার চাইতাম</span>, <span lang=\"BN\">সেটা কি ভালো হতো</span>? <span lang=\"BN\">অনেকে তো গোপনে চাইছে</span>! <span lang=\"BN\">আমি যে গোপনীয়তায় বিশ্বাস করি না</span>- <span lang=\"BN\">তারা সেটা বিবেচনা করেনি</span>, <span lang=\"BN\">তারা ভেবেছে</span>- <span lang=\"BN\">আমার এভাবে চাওয়াটা ঠিক হয়নি</span><span lang=\"HI\">।</span> <span lang=\"BN\">এটা না চাইলে এখনো আমার ভেতরে পুঞ্জিভূত ক্ষোভ কাজ করতো হয়তো</span><span lang=\"HI\">।</span> <span lang=\"BN\">যে আমি স্বাধীনতা শব্দটি কীভাবে আমাদের হলো সেই কবিতা লিখিছি</span>, <span lang=\"BN\">যে আমি বঙ্গবন্ধুকে নিয়ে প্রথম কবিতা লিখেছি</span>, <span lang=\"BN\">যে আমি বঙ্গবন্ধুর মৃত্যুর পর কবিতা লিখলাম</span>- <span lang=\"BN\">আমি ছাড়া এ পুরস্কার দাবি করার দ্বিতীয় ব্যক্তি আমি দেখি না</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমাকে যারা ছোট দেখতে চায়</span>, <span lang=\"BN\">বঞ্চিত দেখতে চায়</span>, <span lang=\"BN\">তারাই আমাকে পুরস্কারবিহীনও দেখতে চায়</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমি চেয়েছি তাই আমাকে দিয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">এখন আরেকজন চেয়ে নিক</span>, <span lang=\"BN\">আমি তো পথ সহজ করে দিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">তারা এখন প্রমাণ করুক</span>- <span lang=\"BN\">চাইলেই দেয় কি</span>-<span lang=\"BN\">না</span>!\r\n\r\n<strong><span lang=\"BN\">আপনার অনেক ঘনিষ্ঠ একজন বন্ধু ছিলেন আমাদের আরেকজন জনপ্রিয় কবি আবুল হাসান</span><span lang=\"HI\">।</span> <span lang=\"BN\">তার সঙ্গে আপনার স্মৃতিগুলো যদি শোনাতেন</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> ‘<span lang=\"BN\">আমার কণ্ঠস্বর</span>’ <span lang=\"BN\">বইটিতে আমাদের যৌথ জীবন নিয়ে আমি লিখেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা দীর্ঘ দিন একসঙ্গে থেকেছি</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেও কিন্তু কবিতার জন্য আমার সঙ্গে বিশ্ববিদ্যালয় ত্যাগ করেছিল</span><span lang=\"HI\">।</span> <span lang=\"BN\">আমরা একসঙ্গে ঢাকা বিশ্ববিদ্যালয় ছেড়েছিলাম</span><span lang=\"HI\">।</span> <span lang=\"BN\">লেখাপড়া করে ডিগ্রী অর্জন করে</span>, <span lang=\"BN\">চাকরি করে</span>, <span lang=\"BN\">সরকারি আমলা হয়ে নিজেদের প্রতিষ্ঠিত করে আমরা আমাদের প্রতিভার অপচয় করতে চাইনি</span><span lang=\"HI\">।</span> <span lang=\"BN\">আবুল হাসান সম্পর্কে আমার আপত্তির একটা জায়গা আছে</span>- <span lang=\"BN\">যখন ২৫ মার্চের জেনোসাইড ঢাকাতে হলো</span>, <span lang=\"BN\">তারপরে এ শহরকে যারা বাসযোগ্য বলে মনে করেছিল</span>- <span lang=\"BN\">হি ওয়াজ এমং দেম</span><span lang=\"HI\">।</span> <span lang=\"BN\">অথচ তার বয়স ছিল মুক্তিযুদ্ধে অংশগ্রহণ করার মতো</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি মুক্তিযুদ্ধে অংশগ্রহণ করেননি</span><span lang=\"HI\">।</span>\r\n\r\n<strong><span lang=\"BN\">আমাদের অনেক কবি</span>-<span lang=\"BN\">ই কিন্তু মুক্তিযুদ্ধে অংশগ্রহণ করেননি বা করতে পারেননি</span>...</strong>\r\n\r\n<strong><span lang=\"BN\">নির্মলেন্দু গুণ</span>:</strong> <span lang=\"BN\">যারা করেননি</span>, <span lang=\"BN\">যাদের বয়স ছিল</span>, <span lang=\"BN\">যারা বলে</span>- ‘<span lang=\"BN\">এখন যৌবন যার</span>/<span lang=\"BN\">যুদ্ধে যাবার তার শ্রেষ্ঠ সময়</span>’ <span lang=\"BN\">তারা মুক্তিযুদ্ধের সময় কোথায় ছিল</span>? <span lang=\"BN\">শ্রেষ্ঠ সময়টা তারা কোথায় কাটিয়েছিল</span>? <span lang=\"BN\">জাতি তো জানতে চায় সেটা</span>, <span lang=\"BN\">তাই না</span>? <span lang=\"BN\">সুতরাং তাদের সাথে বন্ধুত্ব ছিল</span>, <span lang=\"BN\">বন্ধুত্ব এখনো আছে কিন্তু এই ক্ষোভটাও আছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">যেমন শামসুর রাহমান যুদ্ধে যাননি কিন্তু তিনি </span>‘<span lang=\"BN\">তোমাকে আসতেই হবে স্বাধীনতা</span>’ <span lang=\"BN\">বা </span>‘<span lang=\"BN\">তোমাকে পাওয়ার জন্য হে স্বাধীনতা</span>’ <span lang=\"BN\">বা </span>‘<span lang=\"BN\">স্বাধীনতা তুমি</span>’- <span lang=\"BN\">এ কবিতাগুলো তো লিখেছেন</span><span lang=\"HI\">।</span> <span lang=\"BN\">তিনি ঢাকাতে বন্দী শিবিরে থেকেও ছদ্মনামে কবিতা লিখে সেগুলো পাঠিয়েছেন ভারতে</span><span lang=\"HI\">।</span> <span lang=\"BN\">সেগুলো কলকাতা থেকে প্রকাশিত হয়েছে</span><span lang=\"HI\">।</span> <span lang=\"BN\">জাতির দুঃসময়ে যখন দেশকে বাঁচাতে মানুষ যুদ্ধ করছে</span>, <span lang=\"BN\">আমাদের তরুণ কবিদের মধ্যে অনেকেই এখানে থেকে গিয়েছিলেন</span>, <span lang=\"BN\">যুদ্ধের পক্ষ তো তারা নেয়</span>-<span lang=\"BN\">ইনি</span>, <span lang=\"BN\">শামসুর রাহমানের মতো কবিতা লিখে পাঠানোর এই প্রয়াসটাও তো তাদের মধ্যে দেখিনি তখন</span><span lang=\"HI\">।</span> <span lang=\"BN\">কালের ইতিহাসে এ প্রশ্নের মুখোমুখি হবেন তারা</span><span lang=\"HI\">।</span> <span lang=\"BN\">তুমি কোথায় ছিলে</span>, <span lang=\"BN\">কী করছিলে</span>? <span lang=\"BN\">কাদের সমর্থন করেছিলে</span>- <span lang=\"BN\">এ প্রশ্নগুলো উত্থাপন করলেই অনেকের সঙ্গে বন্ধুত্ব আর থাকে না</span><span lang=\"HI\">।</span>', '‘প্রকাশ্যে না চেয়ে যদি গোপনে শেখ হাসিনার কাছে পুরস্কার চাইতাম, সেটা কী ভালো হতো?', '', 'inherit', 'closed', 'closed', '', '384-revision-v1', '', '', '2017-10-24 19:14:56', '2017-10-24 19:14:56', '', 384, 'https://shakti24.com/2017/10/24/384-revision-v1/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(386, 2, '2017-10-24 19:12:31', '2017-10-24 19:12:31', 'ধানীচিটা\r\n‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘\r\n\r\nখেয়ালবিহীন অযত্মে বড় হলো ধানগাছ;\r\nশিকড়-বাঁকড় আবৃত পরগাছার প্রভাবে\r\nগুঁড়ে জলসিঞ্চন নেই; কাস্তের ছোঁয়া নেই\r\nনিজের অস্তিত্বের স্বরূপ অন্যের দ্বারা আচ্ছন্ন!\r\nজন্মের বিকলাঙ্গতায় প্রসার ঘটাতে সে ব্যর্থ\r\nএমনকি তার ভেতরে গোপনে যে শষ্যদানা ছিলো\r\nতারও হলো না প্রতিফলন”!\r\nধানচিটা হয়ে শুকিয়ে সে কেবল জড় হয়েই রইল\r\nবিকাশ হলো না তার পূর্নাঙ্গতার\r\nক্রমাগত;\r\nএ-কথাটি বড়বেশি জ্বালা দেয়।।\r\n\r\n&nbsp;\r\n\r\nকবিতা তুমি\r\n‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘\r\n\r\nকবিতা আমি তোমাকে ধুলোপথ থেকে কুড়িয়ে পেয়েছি\r\nশুকনো পাতার মর্মরধ্বনি থেকে পেয়েছি সুর\r\nকবিতা তুমি জানো না আমি যখন ছিলাম না\r\nতখনও আমি কোনভাবেই তোমারি ছিলাম\r\n“‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘\r\n\r\nকবিতা তুমি কিন্তু  শুঁধুই শব্দমালা নও\r\nআর নও জড়পার্থিব\r\nকবিতা তুমি আমার অনুধ্যান-মহাসত্য\r\n\r\nকবিতা কেনো জানি তোমাকে মনে হয়\r\nকল্পতরু\r\nআবার কখনও পারিভাষিকের বহু বাইরে\r\nঅখন্ডময়!\r\nযাই ভাবি না কেন তোমাকে\r\nতুমি আমার থেকে দূরে নও-এও আমি জানি\r\nতাই আমি মহাকাল্পিক শব্দচয়ন দিয়ে\r\nতোমাকে বুনন করি স্বপ্নেরভিতর ।\r\n\r\n&nbsp;\r\n\r\nবুদ্ধির অতীত!\r\n‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘‘\r\n\r\nআকাশটা মেঘাচ্ছন্ন; অনেকটা আরবের মরুভূমির মতো।\r\nদেখে মনে হয় কে যেনো আকাশের বুকে বালু ছড়িয়ে দিয়েছে;\r\nআর সেই বালুঝড়ে আকাশের স্বরূপ ঢাকা পড়েছে----!\r\n\r\nরাত দ্বিপ্রহর। পায়ের কাছে স্বপ্ন, দৃশ্যগন্ধ নাকের নালপথ ধরে\r\nস্মৃতিরভিতর আলপনা আঁকছে।\r\n\r\nজীবন বিচিত্র! ক্ষণকালের জন্যও তার স্রোত থাকে না থেমে,,,,,\r\nআর মানুষ কেবল ঘটনা,,,তা ঘটে চলে অবিরত দিক-দিশাহীন\r\n-----এমনকি মৃত্যুও ঘটনাকে পারে’না ঠেকাতে।\r\n\r\nযেমুন মানুষ এই যে স্বপ্ন দেখে এর নেই কোনো রূপ-আঁকড়\r\nবাস্তবের সাথে নেই কোনো সাদৃশ্যতা,,,আবার স্বপ্নের ইতিবৃত্ত\r\nমানুষের ফেলেআসা সুদীর্ঘ অতীতের কালছায়া!\r\nতা আমরা ক্রমশ ভুলছি,,,আজ কিংবা আগামী-\r\nসিনেমার পর্দার মতো তা আবার প্রতিরূপ\r\nহয়ে মাঝেমধ্যে জেগে উঠছে স্বপ্নেরভিতর\r\nযা বুদ্ধির অতীত,,,,আত্মার অংশ।', 'ভাবজগত: অসীমকুমারের তিনটি কবিতা:', '', 'inherit', 'closed', 'closed', '', '380-revision-v1', '', '', '2017-10-24 19:12:31', '2017-10-24 19:12:31', '', 380, 'https://shakti24.com/2017/10/24/380-revision-v1/', 0, 'revision', '', 0),
(389, 2, '2017-10-24 19:38:27', '2017-10-24 19:38:27', 'উপমহাদেশের শাস্ত্রীয় সংগীতের খ্যাতিমান শিল্পী বিদুষী গিরিজা দেবী মারা গেছেন।\r\n\r\nমঙ্গলবার রাত ৮টার দিকে কলকাতার বিএম বিড়লা হাসপাতালে তার মৃত্যু হয় বলে আনন্দবাজারসহ পশ্চিমবঙ্গের বেশ কয়েকটি সংবাদমাধ্যমের খবরে বলা হয়েছে।\r\n\r\n৮৮ বছর বয়সী গিরিজা দেবী বার্ধক্যজনিত রোগে ভুগছিলেন।\r\n\r\nবেনারস ও সেনিয়া ঘরানার শিল্পী গিরিজা দেবী ১৯২৯ সালে বারাণসীতে জন্মগ্রহণ করেন। হিন্দুস্তানি ধ্রূপদী সংগীতকে তিনি ভিন্ন এক মাত্রা এনে দিয়েছিলেন।\r\nকণ্ঠশিল্পী ও সারেঙ্গিবাদক সারজুপ্রসাদ মিশ্রর কাছে তিনি প্রথমে খেয়াল ও টপ্পায় প্রশিক্ষণ নেন। পরবর্তীতে চাঁদ মিশ্রর কাছে বিভিন্ন সংগীতরীতি রপ্ত করেন।\r\n\r\nছয় দশকের বেশি বর্ণাঢ্য সংগীত জীবনে গিরিজা দেবী অর্জন করেছেন বহু পুরস্কার ও সম্মাননা, যার মধ্যে রয়েছে পদ্মবিভূষণ, পদ্মভূষণ, পদ্মশ্রী, সংগীত নাটক আকাদেমি পুরস্কার এবং সংগীত নাটক আকাদেমি ফেলোশিপ।\r\n\r\nপ্রতিষ্ঠাকাল থেকে কলকাতা আইসিটি সংগীত রিসার্চ একাডেমিতে সংগীত গুরু ছিলেন তিনি। ঢাকায় বেঙ্গল ক্ল্যাসিক্যাল উৎসবে তিনবার এসেছিলেন দেবী গিরিজা প্রসাদ।', 'শাস্ত্রীয় সংগীত শিল্পী গিরিজা দেবী আর নেই', '', 'publish', 'open', 'open', '', '%e0%a6%b6%e0%a6%be%e0%a6%b8%e0%a7%8d%e0%a6%a4%e0%a7%8d%e0%a6%b0%e0%a7%80%e0%a7%9f-%e0%a6%b8%e0%a6%82%e0%a6%97%e0%a7%80%e0%a6%a4-%e0%a6%b6%e0%a6%bf%e0%a6%b2%e0%a7%8d%e0%a6%aa%e0%a7%80-%e0%a6%97', '', '', '2017-10-24 19:38:27', '2017-10-24 19:38:27', '', 0, 'https://shakti24.com/?p=389', 0, 'post', '', 0),
